DECLARE @PassageId INT;

SELECT @LanguageId = Id FROM Languages WHERE UPPER(ISO6393Code) = 'HIN'
SELECT @PassageId = NULL;
SELECT @PassageId = Id FROM Passages WHERE [StartVerseId] = 1043011014 AND [EndVerseId] = 1043011032;
IF @PassageId IS NULL
BEGIN
INSERT INTO Passages([StartVerseId], [EndVerseId]) VALUES (1043011014, 1043011032);
SELECT @PassageId = @@IDENTITY;
END

SELECT @ResourceId = NULL;
SELECT @ResourceId = Id FROM Resources WHERE [ParentResourceId] = 1 AND [EnglishLabel] = N'CBBT-ER Luke 11:14-32';
IF @ResourceId IS NULL
BEGIN
INSERT INTO Resources([ParentResourceId], [EnglishLabel]) VALUES (1, N'CBBT-ER Luke 11:14-32');
SELECT @ResourceId = @@IDENTITY;
END

IF NOT EXISTS (SELECT 1 FROM PassageResources WHERE [PassageId] = @PassageId AND [ResourceId] = @ResourceId)
BEGIN
INSERT INTO PassageResources([PassageId], [ResourceId]) VALUES (@PassageId, @ResourceId);
END

SELECT @AudioResourceContentId = NULL;
SELECT @AudioResourceContentId = Id FROM ResourceContents WHERE [ResourceId] = @ResourceId AND [LanguageId] = @LanguageId AND [MediaType] = 2;
IF @AudioResourceContentId IS NULL
BEGIN
INSERT INTO ResourceContents([ResourceId], [LanguageId], [DisplayName], [Version], [Completed], [Trusted], [Content], [ContentSize], [MediaType]) VALUES (@ResourceId, @LanguageId, N'लूका 11:14-32', 1, 1, 1, N'{"webm":{"url":"https://cdn.aquifer.bible/aquifer-content/resources/CBBTER/HIN/audio/webm/HIN_CBBTER_043_LUK_011_014_WEBM.zip","size":5271103,"steps":[{"stepNumber":1,"file":"1.webm"},{"stepNumber":2,"file":"2.webm"},{"stepNumber":3,"file":"3.webm"},{"stepNumber":4,"file":"4.webm"},{"stepNumber":5,"file":"5.webm"}]},"mp3":{"url":"https://cdn.aquifer.bible/aquifer-content/resources/CBBTER/HIN/audio/mp3/HIN_CBBTER_043_LUK_011_014_MP3.zip","size":8740799,"steps":[{"stepNumber":1,"file":"1.mp3"},{"stepNumber":2,"file":"2.mp3"},{"stepNumber":3,"file":"3.mp3"},{"stepNumber":4,"file":"4.mp3"},{"stepNumber":5,"file":"5.mp3"}]}}', 5271103, 2);
SELECT @AudioResourceContentId = @@IDENTITY;
END
ELSE
UPDATE ResourceContents SET [DisplayName] = N'लूका 11:14-32', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'{"webm":{"url":"https://cdn.aquifer.bible/aquifer-content/resources/CBBTER/HIN/audio/webm/HIN_CBBTER_043_LUK_011_014_WEBM.zip","size":5271103,"steps":[{"stepNumber":1,"file":"1.webm"},{"stepNumber":2,"file":"2.webm"},{"stepNumber":3,"file":"3.webm"},{"stepNumber":4,"file":"4.webm"},{"stepNumber":5,"file":"5.webm"}]},"mp3":{"url":"https://cdn.aquifer.bible/aquifer-content/resources/CBBTER/HIN/audio/mp3/HIN_CBBTER_043_LUK_011_014_MP3.zip","size":8740799,"steps":[{"stepNumber":1,"file":"1.mp3"},{"stepNumber":2,"file":"2.mp3"},{"stepNumber":3,"file":"3.mp3"},{"stepNumber":4,"file":"4.mp3"},{"stepNumber":5,"file":"5.mp3"}]}}', [ContentSize] = 5271103, Updated = GETUTCDATE() WHERE Id = @AudioResourceContentId;

SELECT @TextResourceContentId = NULL;
SELECT @TextResourceContentId = Id FROM ResourceContents WHERE [ResourceId] = @ResourceId AND [LanguageId] = @LanguageId AND [MediaType] = 1;
IF @TextResourceContentId IS NULL
BEGIN
INSERT INTO ResourceContents([ResourceId], [LanguageId], [DisplayName], [Version], [Completed], [Trusted], [Content], [ContentSize], [MediaType]) VALUES (@ResourceId, @LanguageId, N'लूका 11:14-32', 1, 1, 1, N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"सुनो और दिल"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका 11:14-32"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका 11:14-32 को सुने और इसे अपने दिल में बसा ले। इस भाग को तीन बार सुने (यदि संभव हो तो तीन अलग अनुवादों में इसे पढ़ें)। फिर एक समूह के रूप में दिए गए प्रश्नों पर चर्चा करें:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद आया है?"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"<!— —>"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं आया या समझ में नहीं आया है?"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"<!— —>"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यशु के बारे में क्या बताती है?"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"<!— —>"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बताती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे रोज के जीवन को कैसे प्रभावित करती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप ऐसे किस व्यक्ति को जानते हैं जिसे इस कहानी को सुनने की जरूरत है?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मंच तैयार करना"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका 11:14-32"}]},{"type":"paragraph","content":[{"type":"text","text":"समझने में आसान ऐसे संस्करण में एक बार इस भाग को सुने।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु और उनके शिष्य या अनुयाई यरूशलेम की ओर यात्रा कर रहे हैं। पिछली कहानी में, यीशु ने अपने शिष्यों को प्रार्थना के बारे में सिखाया। अब, यीशु भीड़ में के एक आदमी में से दुष्टात्मा को निकालते हैं और लोग सोचते हैं कि वह शैतान की शक्ति का उपयोग कर रहे हैं। अन्य लोग संकेत मांगते हैं। तब यीशु एक कहानी सुनाते हैं और उनकी सोच को सुधारते है जिससे यह पता चलता है कि वह शैतान से अधिक शक्तिशाली है। यीशु योना का चिन्ह देते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका हमें यह नहीं बताता की यह कहानी कब और कहां घटित हुई है। एक दुरात्मा या दुष्टात्मा थी जिसने एक मनुष्य को अपने काबू में कर रखा था और उसे गूंगा या बोलने में असमर्थ बना रखा था। यीशु ने दुष्टात्मा को बाहर निकाला और वह आदमी फिर से बोलने लगा। भीड़ में शामिल लोग तरह-तरह की प्रतिक्रियाएं दे रहे थे। पहले की तरह, कुछ लोग आश्चर्यचकित हो गए लेकिन दूसरे लोगों ने कहा कि यीशु दुष्टात्मा को बाहर निकालने के लिए बालजबुल या शैतान की शक्ति का उपयोग कर रहे हैं। इसका अर्थ यह होगा की यीशु एक झूठे भविष्यवक्ता थे और परमेश्वर की शक्ति का उपयोग नहीं करते थे। भीड़ में अन्य लोग यीशु को परखना चाहते थे या उसे गलत साबित करने की कोशिश करना चाहते थे। उन लोगों ने वैसा करने के लिए स्वर्ग से या उस स्थान से जहां परमेश्वर निवास करते हैं कोई संकेत मांगा। इस मामले में, स्वर्ग स्वयं परमेश्वर का प्रतिनिधित्व करता है। वह लोग चाहते थे कि उनकी शक्ति परमेश्वर की ओर से है यह साबित करने के लिए यीशु चमत्कार करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुके और अपने समूह के साथ चर्चा करें। आपकी संस्कृति में आध्यात्मिक दुनिया में सबसे शक्तिशाली कौन है? उस समय की कहानी बताओ जब किसी को किसी चीज के बारे में ईश्वर से संकेत मिला हो। लोग किस प्रकार की चीजों को ईश्वर का \"संकेत\" मानते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु जानते थे कि लोग क्या कह रहे थे और उनके अनकहे इरादे क्या थे । यीशु ने यह कह कर अपनी बाजू रखी कि यदि वह शैतान की शक्ति का उपयोग कर रहे होते तो शैतान अपने ही दुष्टात्माओं से लड़ रहा होता क्योंकि शैतान सभी दुष्टात्माओं का नेता है। यह विभाजन शैतान के घर या राज्य को नष्ट करने वाला होता। जब यशु पूछते हैं कि शैतान का राज्य कैसे बना रह सकता है?\" तब यीशु यह पूछ रहे हैं कि शैतान का शासन कैसे बना रह सकता है। यीशु की यह उम्मीद नहीं थी कि उनके सवाल का जवाब दिया जाए।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु कहते हैं कि यदि वह शैतान की शक्ति का उपयोग कर रहे थे तो उन पर आरोप लगाने वालों को यह बताना जरूरी होगा कि वह अपने अनुयायियों पर आरोप क्यों नहीं लगा रहे थे जो शैतान की शक्ति का उपयोग करके दुष्टात्माओं को निकालते थे। मत्ती के अनुसार, यीशु पर आरोप लगाने वाले फरीसी या धार्मिक नेता थे। यीशु शायद धार्मिक नेताओं के अनुयायियों की बात कर रहे थे। वह अनुयायी दुष्टात्माओं को उस तरह से निकाल रहे है जिस तरह से धार्मिक नेताओं ने उन्हें दिखाया है और यीशु भी ऐसा ही कर रहे हैं। फिर यदि नेता कहते हैं कि यह शैतान की शक्ति से है तो उनके अनुयाई धार्मिक नेताओं का भी उसी तरह से न्याय करेंगे जैसे नेता यीशु का न्याय कर रहे हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुके और अपने समूह के साथ चर्चा करें। आपके संस्कृति में किस प्रकार के लोगों के पास दुष्टात्माओं पर अधिकार है?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु कहते हैं कि अगर वह परमेश्वर की उंगली से दुष्टात्माओं को बाहर निकाल रहे हैं तो परमेश्वर का राज्य लोगों के करीब आ गया है। परमेश्वर की उंगली यह सामान्य रूप से इस्तेमाल किया जाने वाला एक मुहावरा था जिसका अर्थ है \"परमेश्वर की कार्यशील शक्ति।\" यीशु कह रहे थे कि जिन लोगों से वह बात कर रहे थे उनके दिलों में परमेश्वर ने पहले से ही राज्य करना शुरू कर दिया है। परमेश्वर यीशु के द्वारा अपनी शक्ति और अधिकार का उपयोग कर रहे थे और यही कारण था कि यीशु दुष्टात्मा को निकाल सके।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु अपने खुद के और शैतान के बारे में सिखाने के लिए दो शक्तिशाली पुरुषों के रूप में बताते हैं। यीशु शैतान की तुलना हथियारों का कवच पहने हुए एक शक्तिशाली व्यक्ति से करते हैं। वह उन लोगों की रक्षा करता है जिन पर उसका अधिकार है।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुके और शस्त्रधारी कवच की एक तस्वीर दिखाएं अगर यह आपके संस्कृति में एक सामान्य संकल्पना नहीं है तो।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु अपनी तुलना एक बलवान व्यक्ति से करते हैं जो कि दूसरे बलवान व्यक्ति से लड़कर जीत जाता हैं। वह उस बलवान व्यक्ति के कवच और हथियार छीन लेता है और उस पराजित शत्रु की लूट या संपत्ति को बांट देता है। उसी तरह, यीशु लोगों पर से शैतान की शक्ति को छीन लेते हैं। दुष्टात्माओं को निकालने के द्वारा येशु ने दिखा दिया कि वह दुष्टात्माओं और उनके नेता शैतान से अधिक शक्तिशाली थे। दुष्टात्मा के जाने के बाद यीशु उस व्यक्ति के लिए आशीष लेकर आते हैं। इस तुलना का उपयोग करने में यीशु का तात्पर्य यह है कि जो कोई यीशु का मित्र नहीं है वह शैतान का मित्र है। यीशु बटोरने और बिखेरने के उदाहरण का भी उपयोग करते हैं उस बात पर से वह यह कहते हैं कि जो लोग दूसरे लोगों को परमेश्वर के पास इकट्ठा लाने में उनकी मदद नहीं करते हैं वह वास्तव में उन्हें परमेश्वर से दूर धकेल रहे हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"फिर यीशु ने दुष्टात्माओं से छुड़ाए गए लोगों के बारे में बात की। जब एक दुष्टात्मा एक व्यक्ति को छोड़कर चली जाती है, तो वह सुनसान जगहों पर भटकती रहती है और वह चैन से रह नहीं पाती। लेकिन उसके बजाय, वह दुष्टात्मा उस व्यक्ति के पास लौटना चाहती है जिसे उसने पहले काबू में रखा था। यीशु एक साफ-सुथरे घर की तुलना एक ऐसे व्यक्ति से करते हैं जो आध्यात्मिक रूप से खाली है और आत्मा के लिए उपलब्ध है, चाहे वह अच्छी या बुरी आत्मा हो। जब वह दुष्टात्मा उस व्यक्ति के पास लौटती है जिसमें वह पहले रहती थी और उस व्यक्ति को आध्यात्मिक रूप से खाली पाती है, तब वह जाती है और सात और आत्माएं ले आती है जो उससे भी अधिक दुष्ट हैं। वह सभी आठ आत्माएं उस व्यक्ति को अपने काबू में ले लेती है या घर में रहती है और वह उस ग्रस्त व्यक्ति के साथ और भी बुरी चीजें होने देती है।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुके और अपने समूह के साथ चर्चा करें। आपकी संस्कृति में लोग आत्माओं के बारे में क्या विश्वास करते हैं? आत्माएं कहां रहती है? आत्माएं किस प्रकार की होती है और वह लोगों से क्या करवाती है?"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यशु सिखा रहे थे तब एक स्त्री ने जोर से पुकार कर कहा कि यीशु की माता धन्य है, या उन पर परमेश्वर का अनुग्रह है। वह यीशु की स्तुति कर रही थी। यीशु ने उस बात से इनकार नहीं किया लेकिन क्या अधिक महत्वपूर्ण था यह सम्मान पूर्वक बताकर उसे सही किया — \"वह लोग जो परमेश्वर के संदेश को सुनते हैं और उसका पालन करते हैं वह धन्य है।\""}]},{"type":"paragraph","content":[{"type":"text","text":"अधिक से अधिक लोग यीशु को सुनने आते थे। जिन्होंने उससे वह परमेश्वर की ओर से है यह प्रमाणित करने के लिए संकेत मांगा, यीशु ने उन लोगों को दुष्ट कह कर जवाब दिया। भले ही यीशु उनके लिए कई चमत्कार कर चुके थे, फिर भी लोगों ने विश्वास नहीं किया कि वह परमेश्वर की ओर से आए हैं। यीशु कहते हैं कि परमेश्वर उन्हें उनके अविश्वास के लिए दंड देंगे। यीशु योना का चिन्ह देते हैं। योना परमेश्वर का भविष्यवक्ता था जो यीशु से बहुत पहले आया था। एक बड़ी मछली ने उसे निगल लिया था, परंतु तीन दिन के बाद परमेश्वर ने ऐसा होने दिया कि वह मछली योना को जमीन पर उगल दे। मत्ती का सुसमाचार यह कहता है कि योना का अनुभव, भविष्य में यीशु के साथ जो होगा उसकी तुलना है। यीशु मरेंगे और फिर से जीवित होने से पहले तीन दिन तक गाडे जाएंगे। योना के अनुभव ने नीनवे के लोगों के लिए यह साबित किया कि उस का संदेश परमेश्वर की ओर से था। यीशु योना की तुलना अपने आप से याने मनुष्य के पुत्र से करते हैं। परमेश्वर ने योना को मरने से बचाया और परमेश्वर यीशु को फिर से जीवित करके बचाएंगे। यह चमत्कार वह संकेत थे कि योना और यीशु ने परमेश्वर के अधिकार से बातें की थी।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने उन्हें याद दिलाया कि शीबा की रानी सुलेमान के शब्दों को केवल सुनने और उसका जवाब देने के लिए परदेश से आई थी। सुलेमान एक यहूदी राजा था जो अपनी बुद्धिमानी के लिए जाना जाता था। उस रानी को अपने देश से बहुत दूर जाना पड़ा था।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुके और अपने समूह को इज़राइल, नीनवे और शिबा का नक्शा दिखाएं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु कहते हैं की न्याय के दिन, दुनिया के अंत के समय में, जब परमेश्वर हर किसी का न्याय करेंगे तब रानी और नीनवे के लोग उन लोगों पर दोष लगाने के लिए खड़े होंगे जिन्होंने यीशु पर विश्वास नहीं किया था। यीशु के समय के लोगों को परमेश्वर को और भी बड़े तरीके से स्वयं को प्रकट होते देखने का सौभाग्य मिला था, परंतु फिर भी उन्होंने विश्वास नहीं किया।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का वर्णन करना"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका 11:14-32"}]},{"type":"paragraph","content":[{"type":"text","text":"समझने में आसान ऐसे संस्करण में एक बार इस भाग को सुने।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, आप समूह को इस कहानी को दृश्य रूप देने में मदद करेंगे। आप समूह को कहानी के दृश्य, बनावट और पात्रों का वर्णन करने में मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में चार दृश्य है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" यीशु और उनके शिष्य यरूशलेम की और यात्रा कर रहे है। तब एक भीड़ जमा हो जाती है। एक दुष्टात्मा मनुष्य को अपने काबू में कर लेती है और उसे बोलने में असमर्थ कर देती है। यीशु ने उस दुष्टात्मा को बाहर निकाला और वह मनुष्य फिर से बोलने लगता है। भीड़ में तीन अलग-अलग प्रतिक्रियाएं होती है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" यीशु उन लोगों को जवाब देते हैं जो सोचते हैं कि यीशु शैतान की शक्ति का उपयोग कर रहे हैं। यीशु दो शक्तिशाली व्यक्तियों का उदाहरण देते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"तीसरा दृश्य:"},{"type":"text","text":" यीशु उन लोगों के बारे में बात करते हैं जो दुष्टात्माओं के काबू में हुआ करते थे। एक दुष्टात्मा एक व्यक्ति में से जंगल में चली जाती है। वह उस व्यक्ति के पास लौटती है जिसे उसने काबू में रखा था, वह उसे साफ सुथरा पाती है, फिर सात और दुष्टात्माओं को वह उस व्यक्ति के पास ले आती है। एक महिला चिल्लाती है और यीशु की शिक्षाओं की प्रशंसा करती है। यीशु उसे बताते हैं कि क्या अधिक महत्वपूर्ण है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"चौथा दृश्य:"},{"type":"text","text":" यीशु योना के चिन्ह के बारे में बात करते हैं। योना नीनवे के लोगो के पास जाता है और वह उसका संदेश सुनते हैं। शीबा की रानी सुलेमान के पास जाती है और वह उसका संदेश सुनती है। यीशु लोगों से बात करते हैं परंतु वह उसके संदेश को नहीं सुनते।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्रों में यह लोग शामिल है:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"भीड़"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"दुष्टात्मा ग्रसित आदमी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"दुष्टात्मा"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"अनेक धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेताओं के अनुयायी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जो संकेत मांग रहे हैं वह लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"शैतान"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आठ दुष्टात्माएं"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वह मनुष्य जिसके पास दुष्ट आत्मा लौटती है"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पुकारने वाली स्त्री"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"नीनवे के लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"योना"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"सुलेमान"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"शीबा की रानी"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में कहानी और उसमें होने वाली गतिविधियों को दृश्य में लाने के लिए संभव चित्र बनाने के लिए बोर्ड बनाएं, उस पर चित्र बनाएं या कुछ प्रकार की वस्तुएं (ऐसी वस्तुएं जिन्हें आप इधर-उधर हिला सकते हैं जैसे छोटे पत्थर, छडिया, बच्चों के खिलौने) का उपयोग करें।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है कि दुष्टात्मा के कारण मनुष्य बोल नहीं सकता है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है कि दूसरा दृश्य यीशु के साथ शुरू होता है, यीशु जानते है कि लोग क्या सोच रहे हैं। यीशु उन्हें समझाते हैं कि एक घर या राज्य को अपने आपके विरुद्ध विभाजित नहीं किया जा सकता हैं। फिर यीशु \"अब\" जैसे शब्द का उपयोग यह दिखाने के लिए करते हैं कि वह विषय बदल रहे हैं। यीशु बोलते हैं कि यदि वह सोचते हैं कि वह दुष्टात्माओं को निकालने के लिए शैतान की शक्ति का उपयोग कर रहे है तो उन्हें भी दुष्टात्माओं को निकालने के लिए किसी प्रकार की शक्ति का उपयोग करना होगा। वह शक्ति कौन प्रदान कर रहा है जिसका उपयोग वे दुष्टात्माओं को निकालने के लिए कर रहे हैं? दुष्टात्माओं को निकालने वाले धार्मिक नेताओं के अनुयायी यह प्रमाणित करेंगे की धार्मिक नेता यीशु के बारे में जो कहते हैं वह गलत है। फिर यीशु बात को बदलने वाले शब्द के साथ एक बात पर आते हैं और कहते हैं: लेकिन यदि मैंने परमेश्वर की शक्ति से दुष्टात्माओं को निकाला है तो परमेश्वर का राज्य आ गया है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यहां रुके और यीशु के तर्क को सुनें। आप अपनी भाषा में कैसे दिखाते हैं कि तर्क में कोई नई बात कह रहा है?"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है कि जब यीशु दो शक्तिशाली व्यक्तियों के बारे में बात करते हैं तो वह शैतान की तुलना स्वयं से कर रहे हैं। यीशु का उदाहरण दो प्रतिस्पर्धी राज्यों, शैतान के राज्य और परमेश्वर के राज्य की बात करता है। शैतान को शैतानी ताकतों के सेनापति के रूप में चित्रित किया गया है और यीशु को युद्ध के मैदान में पराजित करने वाले के रूप में चित्रित किया गया है, जो उसकी संपत्ति को लूट रहा है। यीशु दिखाते हैं की अंत में परमेश्वर के राज्य या घर की जीत होगी।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है कि जो लोग संकेत माँगते हैं वे यीशु की परीक्षा लेना चाहते हैं। वे वास्तव में विश्वास नहीं करते कि यीशु परमेश्वर की शक्ति का उपयोग कर रहे हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है कि यीशु एक व्यक्ति के अंदर की तुलना एक घर से करते हैं। यदि कोई व्यक्ति परमेश्वर के आत्मा का घर नहीं है तो वह व्यक्ति दुष्टात्माओं का घर बन सकता है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है की इससे पहले कि जब परमेश्वर ने मछली को तट पर योना को उगलने के लिए प्रेरित किया योना ने समुद्र के तल पर एक बड़ी मछली के अंदर तीन दिन बिताए। भविष्य में, परमेश्वर द्वारा यीशु को फिर से जीवन देने से पहले यीशु तीन दिनों तक कब्र में रहेंगे। यह योना की निशानी है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"यह याद रखना महत्वपूर्ण है कि नीनवे के लोग और शीबा की रानी अन्यजाति या गैर-यहूदी थे। नीनवे के लोगों और शीबा की रानी के बारे में बात करने के लिए यीशु उसी विवरण का उपयोग करते हैं। दोनों ही मामलों में वे न्याय के लिए उठेंगे और यीशु कहते हैं यहां कोई और उनसे महान है।\""}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"पाठ को मूर्त रूप देना"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका 11:14-32"}]},{"type":"paragraph","content":[{"type":"text","text":"समझने में आसान ऐसे संस्करण में एक बार उस भाग को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में समूह कहानी का नाट्य रूपांतरण करेगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में चार दृश्य है।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्रों में यह लोग शामिल है:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"भीड़"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"दुष्टात्मा ग्रसित आदमी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"दुष्टात्मा"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"अनेक धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेताओं के अनुयायी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"संकेत मांगने वाले लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"शैतान"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आठ दुष्टात्माएं"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वह मनुष्य जिसके पास दुष्टात्मा लौट आई"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पुकारने वाली स्त्री"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"नीनवे के लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"योना"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"सुलेमान"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"शीबा की रानी"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"समूह को कहानी पर दो बार अभिनय करने को कहें। पहली बार समूह को बिना रुके कहानी पर अभिनय करने को कहें। दूसरी बार जब समूह कहानी पर अभिनय करेगा, तो उन्हें कहानी के अलग-अलग बिंदुओं पर रोके।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब समूह पहली बार कहानी पर अभिनय करेगा तब कहानी के संवाद, प्रभाव, कथानक और कालक्रम पर ध्यान दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह निश्चित करें कि जब यीशु भीड़ के सामने उनका वर्णन करते हैं तो समूह उदाहरणों पर अभिनय करता है। उदाहरण के लिए, जब यीशु भीड़ से बात कर रहे होते हैं तो समूह दो शक्तिशाली व्यक्तियों के लड़ने का अभिनय कर सकता है। वे उस दुष्टात्मा का अभिनय कर सकते हैं जिसे अभी ही एक व्यक्ति से निकाला गया था। दुष्टात्मा जंगल में जाती है और उस व्यक्ति के पास वापस आती है। फिर वह उस व्यक्ति को काबू में करने के लिए और सात दुष्टात्माएं लाती है। अंत में, समूह योना को नीनवे के लोगों को उपदेश देने और वह लोग परमेश्वर की ओर मुड़ने का और राजा सुलेमान को सुनने के लिए बहुत दूर यात्रा करने वाली शीबा की रानी का अभिनय कर सकता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"निश्चित करें कि जब यीशु यहां सुलेमान से महान किसी के होने की बात करते हैं तो वह भीड़ की ओर मुड़ते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब समूह अभिनय करता है तब वह कहानी का कालक्रम फिर से बदलना चाह सकता है क्योंकि वास्तव में जो हुआ उसके बारे में वे अधिक विचारात्मक होने के लिए कार्य करते हैं। समूह उस आदमी को दिखा सकता है जो दुष्टात्मा ग्रस्त है और बोलने में असमर्थ है। यीशु ने दुष्टात्मा को बाहर निकाला और वह मनुष्य अब फिर से बोल सकता है। भीड़ की मिश्रित प्रतिक्रियाएं होती है और यीशु उन्हें सिखाना शुरू करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब समूह इस कहानी पर अभिनय करता है तो उन्हें कुछ बिंदुओं पर रोके।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक दुष्टात्मा ग्रसित व्यक्ति का अभिनय करें जो बोलने में असमर्थ है। यीशु मनुष्य में से दुष्टात्मा को निकालते हैं और वह मनुष्य फिर से बोल सकता है। भीड़ में से कुछ लोग हैरान हो जाते हैं। दूसरे सोचते हैं कि यीशु शैतान की शक्ति का उपयोग कर रहे हैं। और वह चाहते हैं कि यीशु यह साबित करने के लिए एक संकेत दे कि वह परमेश्वर की शक्ति का उपयोग कर रहे हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय करना बंद करे।"},{"type":"text","text":" बोल सकने वाले किसी अभिनेता से पूछें, \"आप कैसा महसूस कर रहे हैं?\" आप इस तरह की बातें सुन सकते हैं, \"मैं आखिर दुष्टात्मा की शक्ति से मुक्त हो गया हूं\", \"वे कैसे सोच सकते हैं कि यीशु शैतान की शक्ति का उपयोग करते हैं? मैं शैतान की शक्ति को जानता हूं और यीशु की शक्ति की तुलना में वह कुछ भी नहीं है।\" और \"मै आभारी हू। यीशु ने जो किया है उसके लिए मैं परमेश्वर की स्तुति करूंगा।\" अभिनय करना फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह अभिनय करे जब यीशु ने अपनी बाजू रखने के लिये यह कहा कि यदि घर में रहने वाले लोग आपस में लड़ रहे हों तो एक घर खड़ा नहीं हो सकता। यीशु कहते हैं कि शैतान के लिए अपने दुष्टात्माओं के खिलाफ लड़कर अपने राज्य को बांटने में कोई मतलब नहीं है। यीशु सवाल करते हैं कि धार्मिक नेता अपने अनुयायियों पर जो शैतान की शक्ति का उपयोग करके दुष्टात्माओं को निकालते है उन पर आरोप क्यों नहीं लगाते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय करना बंद करे।"},{"type":"text","text":" आरोप लगाने वाले की भूमिका निभाने वाले अभिनेता से पूछें, \"आप कैसा महसूस करते हैं?\" आप इस तरह की बातें सुन सकते हैं, \"निराशा। यीशु के पास हमेशा हर बात का जवाब होता है!\" \"मैंने सोचा था कि निश्चित रूप से लोग विश्वास करेंगे कि वह शैतान की शक्ति का उपयोग कर रहे थे।\" और \"मैं जानता हूँ कि मेरे अनुयायी परमेश्वर की शक्ति का उपयोग करते हैं। मैं नहीं चाहता कि वह मुझसे सवाल करें।\" अभिनय करना फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"लड़ने वाले बलवान पुरुष का उदाहरण देने वाले यीशु का अभिनय करें। बलवान पुरुष कवच और अस्त्र-शस्त्र वाले मनुष्य के विरुद्ध जीत जाता है। वह जिन लोगों की रखवाली कर रहा था वे मुक्त हो गए। यीशु कहते हैं कि जो उसके साथ नहीं है वे उसके खिलाफ हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय करना बंद करे।"},{"type":"text","text":" मुक्त हुए लोगों की भूमिका निभाने वाले अभिनेता से पूछें, \"आप कैसा महसूस करते हैं?\" तो आप इस तरह की बातें सुन सकते हैं, \"आनंदपूर्ण! आखिर हम मुक्त हो गये हैं!\" \"मैंने कभी नहीं सोचा था कि शैतान से ज्यादा ताकतवर कोई हो सकता है,\" और \"मैं बलवान आदमी का अनुसरण करना चाहता हूं। मैं उसके साथ सुरक्षित महसूस करता हूं।\" अभिनय करना फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक व्यक्ति से निकाले गए दुष्टात्मा का उदाहरण देते हुए यीशु का अभिनय करें। दुष्टात्मा जंगल में जाती है, लौटती है, फिर सात और ज्यादा दुष्टात्माओं को उसी व्यक्ति के पास वापस लाती है जिसमें वह रहती थी। अब वह व्यक्ति पहले से भी बदतर है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय करना बंद करे।"},{"type":"text","text":" दुष्टात्मा ग्रस्त व्यक्ति की भूमिका निभाने वाले अभिनेता से पूछें, \"आप कैसा महसूस करते हैं?\" आप इस तरह की बातें सुन सकते हैं, \"मैं यह कल्पना नहीं कर सकता, वह उस दुष्टात्मा से भी बदतर है जो मुझमें रहा करती थी\" \"मै डरा हुआ था। मैं नहीं चाहता कि दुष्टात्मा मेरे पास लौटें।\" और \" मै बहुत निराश हू और मैं परमेश्वर की आत्मा पाना चाहता हूँ। वह शैतान से बहुत अधिक शक्तिशाली है।\" अभिनय करना फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक महिला को चिल्लाते हुए अभिनय करें कि यीशु की माँ धन्य है। यीशु उससे कहते हैं कि परमेश्वर के वचन को सुनना और उसका पालन करना अधिक महत्वपूर्ण है। यीशु उन दुष्ट लोगों को बुलाते है जो संकेत माँगते हैं और वह उन्हें योना का संकेत देते है। यीशु कहते हैं कि योना और सुलैमान से यहां कोई और महान है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय करना बंद करे।"},{"type":"text","text":" भीड़ की भूमिका निभाने वाले अभिनेता से पूछें, \"आप कैसा महसूस कर रहे हैं?\" आप इस तरह की बातें सुन सकते हैं, \"मै परेशान हुआ। यीशु ने हमें दुष्ट क्यों कहा?\" \"गुस्सा आया। परमेश्वर में विश्वास करने के लिए यीशु गैर-यहूदियों कि प्रशंसा कैसे कर सकते हैं?\" और \"गडबड लगी। इस्राएल के सबसे बुद्धिमान राजा से बड़ा कौन हो सकता है? यीशु किस संकेत की बात कर रहे है?"}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"खाली जगह भरना"}]},{"type":"paragraph","content":[{"type":"text","text":"लूका 11:14-32"}]},{"type":"paragraph","content":[{"type":"text","text":"समझने में आसान ऐसे संस्करण में एक बार उस भाग को सुने।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु एक व्यक्ति से जो गूंगा है, उसमे से दुरात्मा या "},{"type":"text","marks":[{"type":"bold"}],"text":"दुष्टात्मा"},{"type":"text","text":" को निकालते है। दुष्टात्मा का उसी तरह से अनुवाद करना निश्चित करें जैसे आपने पिछले भागो में किया है। दुष्टात्मा यह शब्द मुख्य शब्दावली में है। भीड़ ने यीशु के चमत्कार पर मिश्रित प्रतिक्रियाएं दी। कुछ लोगों ने सोचा कि यीशु "},{"type":"text","marks":[{"type":"bold"}],"text":"दुष्टात्माओं के राजकुमार, बालजबुल"},{"type":"text","text":" की शक्ति का उपयोग कर रहे थे। बालजबुल एक मूर्तिपूजक देवता को संदर्भित करता है, लेकिन लोग उसे "},{"type":"text","marks":[{"type":"bold"}],"text":"शैतान"},{"type":"text","text":" से संदर्भित करते थे, जो सभी बुरी आत्माओं का मुखिया और परमेश्वर का दुश्मन है। शैतान का उसी तरह से अनुवाद करना निश्चित करें जैसे आपने पहले किया था। शैतान यह शब्द मुख्य शब्दावली में है।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपने अनुवादकों के साथ चर्चा करें कि आपकी भाषा में शैतान के लिए बालजबुल यह शब्द बदलना आसान होगा क्या?"}]},{"type":"paragraph","content":[{"type":"text","text":"भीड़ में से अन्य लोगों ने यीशु की परीक्षा ली और "},{"type":"text","marks":[{"type":"bold"}],"text":"स्वर्ग"},{"type":"text","text":" से या उस स्थान से जहाँ परमेश्वर रहते है, वह एक "},{"type":"text","marks":[{"type":"bold"}],"text":"संकेत"},{"type":"text","text":" माँग रहे थे। इस संदर्भ में, स्वर्ग स्वयं परमेश्वर को संदर्भित करता है। एक संकेत, एक चिन्ह या निशान है कि कुछ सच है या होगा। वह लोग चाहते थे कि यीशु यह साबित करने के लिए चमत्कार करे कि वह परमेश्वर की शक्ति का उपयोग कर रहे थे। संकेत और स्वर्ग का उसी तरह से अनुवाद करना निश्चित करें जैसे आपने पिछले भागो में किया है। यह दोनों शब्द मुख्य शब्दावली में हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु एक ऐसे "},{"type":"text","marks":[{"type":"bold"}],"text":"राज्य"},{"type":"text","text":" के बारे में बात करते हैं जो अपने ही विरुद्ध बटा हुआ है। यह एक ऐसे देश को संदर्भित कर सकता है जहां देश के विभिन्न समूह एक-दूसरे के खिलाफ लड़ते हैं। लेकिन यीशु किसी राजा द्वारा शासित देश की बात नहीं कर रहे हैं। इसके बजाय वह सामान्य शब्दों में बोल रहे हैं। कोई भी देश या जाति जो खुद के खिलाफ लड़ती है बर्बाद हो जाएगी, या उस देश में कुछ ही लोग जीवित रहेंगे। शैतान का राज्य, उसकी शक्ति और सभी दुष्टात्माओं पर उसके शासन को दर्शाता है। यदि शैतान अपनी दुष्टात्माओं से लड़ता है, तो वह अपने ही राज्य का बँटवारा कर रहा है। राज्य का उसी तरह से अनुवाद करना निश्चित करें जैसे आपने पिछले भागो में किया है। राज्य यह शब्द मुख्य शब्दावली में है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु कहते हैं कि यदि वह लोग परमेश्वर की शक्ति का उपयोग कर रहे हैं, तो "},{"type":"text","marks":[{"type":"bold"}],"text":"परमेश्वर का राज्य"},{"type":"text","text":" उनके निकट आ गया है। यहाँ, परमेश्वर का राज्य परमेश्वर के लोगों के दिलों में उनके विश्वास के माध्यम से वादा किए गए उद्धारकर्ता के रूप में शासन करने के लिए संदर्भित करता है। परमेश्वर के राज्य का उसी तरह से अनुवाद करना निश्चित करें जैसा आपने पहले किया था। परमेश्वर का राज्य यह शब्द मुख्य शब्दावली में है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु कहते हैं, "},{"type":"text","marks":[{"type":"bold"}],"text":"जो मेरे साथ नहीं बटोरता वह बिखेरता"},{"type":"text","text":" है। यीशु का शाब्दिक अर्थ यह नहीं था बल्कि इसके बजाय यह तात्पर्य था कि जो कोई भी लोगों को परमेश्वर के पास नहीं बुला रहा था, वह वास्तव में उन्हें परमेश्वर से दूर कर रहा था।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपने अनुवादकों के साथ इस कल्पना का अनुवाद करने के सबसे अच्छे तरीके के बारे में चर्चा करें। आप अधिक विशिष्ट उदाहरण का उपयोग कर सकते हैं जैसे भेड़ या फसल इकट्ठा करना। या अधिक सामान्य रूप से इकट्ठा करने का अनुवाद का सबसे अच्छा तरीका यह हो सकता है, जैसे, \"जो मेरे साथ काम नहीं कर रहा है वह मेरे खिलाफ काम कर रहा है।\""}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु "},{"type":"text","marks":[{"type":"bold"}],"text":"अशुद्ध आत्मा"},{"type":"text","text":" कहते हैं, यह फिर से एक दुरात्मा या दुष्टात्मा को संदर्भित करता है, जिसे यहूदी संस्कृति में अशुद्ध या परमेश्वर की सेवा के लिए अनुपयुक्त माना जाता था। यीशु कहते हैं कि जब दुष्टात्मा उस व्यक्ति के पास लौटती है जिसे उसने पहले काबू में कर रखा था तो वह घर को "},{"type":"text","marks":[{"type":"bold"}],"text":"साफ-सुथरा"},{"type":"text","text":" पाती है। घर में झाडू लगाने का अर्थ है झाडू से झाडकर फर्श को साफ करना।"}]},{"type":"paragraph","content":[{"type":"text","text":"भीड़ में से एक महिला पुकारती है और कहती है कि यीशु की माता "},{"type":"text","marks":[{"type":"bold"}],"text":"धन्य है"},{"type":"text","text":", या उस पर परमेश्वर का अनुग्रह है। धन्य का अनुवाद उसी तरह से करना निश्चित करें जैसे आपने पहले किया था। धन्य यह शब्द मुख्य शब्दावली में है। यीशु ने यह कहकर उसे सही किया कि जो लोग "},{"type":"text","marks":[{"type":"bold"}],"text":"परमेश्वर के वचन"},{"type":"text","text":" को सुनते हैं और उसका पालन करते हैं वे धन्य हैं। परमेश्वर का वचन उस संदेश को संदर्भित करता है जिसे परमेश्वर ने यीशु और लिखित शास्त्रों के माध्यम से पाहुचाया है। परमेश्वर का वचन का उसी तरह से अनुवाद करना निश्चित करें जैसे आपने पहले किया था। परमेश्वर का वचन यह शब्द मुख्य शब्दावली में है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने कहा, \"यह एक दुष्ट "},{"type":"text","marks":[{"type":"bold"}],"text":"पीढ़ी"},{"type":"text","text":" है।\" यहाँ, पीढ़ी उन लोगों को संदर्भित करती है जो एक ही समय में एक दूसरे के रूप में रहते हैं। पीढ़ी का उसी तरह अनुवाद करना निश्चित करें जैसे आपने पिछले भागो में किया है। पीढी यह शब्द मुख्य शब्दावली में है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु योना के बारे में बात करते है, जिसे "},{"type":"text","marks":[{"type":"bold"}],"text":"नीनवे के लोगों"},{"type":"text","text":", या नीनवे शहर में रहने वाले लोगों को प्रचार करने के लिए भेजा गया था। नीनवे, असीरिया की राजधानी थी और इज़राइल के उत्तर-पूर्व में स्थित थी। यीशु कहते हैं कि जैसे योना नीनवे के लोगों के लिए एक संकेत के रूप में था वैसे हि "},{"type":"text","marks":[{"type":"bold"}],"text":"मनुष्य का पुत्र"},{"type":"text","text":" उनकी पीढ़ी के लिए एक संकेत होंगे। यीशु ने मनुष्य का पुत्र की उपाधि का प्रयोग खुद के लिए किया है जिनके पास सारी शक्ति और अधिकार है। मनुष्य का पुत्र का उसी तरह से अनुवाद करना निश्चित करें जैसा आपने पिछले भागो में किया है। मनुष्य का पुत्र यह शब्द मुख्य शब्दावली में है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने "},{"type":"text","marks":[{"type":"bold"}],"text":"दक्षिण की रानी"},{"type":"text","text":" या शिबा की रानी का भी उल्लेख किया है। वह लगभग 1000 साल पहले रहती थी और इज़राइल के दक्षिण में स्थित एक देश पर शासन करती थी। वह शायद उस देश में शासन करती थी जो अब वर्तमान में इथियोपिया है। जब यीशु कहते हैं कि वह "},{"type":"text","marks":[{"type":"bold"}],"text":"पृथ्वी के छोर से"},{"type":"text","text":" आई है, तो उसका सीधा सा मतलब यह है कि वह बहुत दूर से आई है।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुकें और अपने समूह को इज़राइल, नीनवे और शिबा का नक्शा दिखाएं, अगर आपने पहले नहीं दिखाया है तो।"}]},{"type":"paragraph","content":[{"type":"text","text":"वह, राजा सुलैमान की "},{"type":"text","marks":[{"type":"bold"}],"text":"बुद्धिमानी"},{"type":"text","text":" या उसके सच्चे विचारों को सुनने आई थी। केवल परमेश्वर ही इंसान को बुद्धिमानी दे सकते है। बुद्धिमानी यह शब्द मुख्य शब्दावली में है।"}]}]}}]', 68384, 1);
