UPDATE ResourceContents SET [DisplayName] = N'मरकुस 2:23-3:6', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:23 से 3:6 तक सुनें और इसे अपने दिल में बसा लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिए गए सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप किसी ऐसे व्यक्ति को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:23-3:6"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए सबसे आसान लहजे में टेक्स्ट को एक बार सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक बार फिर से यीशु और उनके शिष्य, धार्मिक नेताओं को तब हैरान कर देते हैं, जब वे मूसा के बनाए गए धार्मिक कानून और यहूदी धार्मिक नेताओं की पीढ़ियों से चले आ रही धार्मिक परंपराओं का हमेशा अनुसरण नहीं करते हैं। यीशु के शिष्य, अनाज के खेतों में चल रहे थे और अपने हाथों से अनाज के सिरों को इकट्ठा कर रहे थे। मूसा के कानून ने उन्हें भूखे होने पर किसी अजनबी के खेत से अपने हाथों से अनाज के सिरों को उखाड़ने की अनुमति दी, लेकिन समस्या यह थी कि उन्होंने विश्राम के दिन यानी सबा को ऐसा किया था।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपनी अनुवाद टीम को गेहूँ के दानों की तस्वीर दिखाएँ।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहूदी संस्कृति में विश्राम के दिन सबा के लिए सख्त नियम थे। लोगों को किसी भी तरह से काम करने की अनुमति नहीं थी और उन्हें बीमार लोगों की मदद करने की भी अनुमति नहीं दी गई थी जब तक कि बीमार व्यक्ति बिना मदद के मरने वाला न हो। सबा (उदाहरण 34:21) को कटाई वाले भोजन की भी अनुमति नहीं थी। फरीसियों ने सुना कि यीशु के शिष्य क्या कर रहे थे और वे सीधे यीशु के पास आ गए, क्योंकि शिष्यों के कार्यों के लिए, उनकी संस्कृति के मामले में, यीशु ही ज़िम्मेदार थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम के साथ चर्चा करें\\— क्या आपको कोई धार्मिक विश्राम दिन या छुट्टी मिलती है? क्या इन दिनों के लिए आपका कोई विशेष नियम है? क्या आपको इन विशेष दिनों पर कुछ ख़ास तरह के काम करने की अनुमति है या नहीं है?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु, धार्मिक नेताओं को एक कहानी की मदद से जवाब देते हैं। पुराने टेस्टामेंट में, जब डेविड, राजा सोल से दूर भाग रहे थे, तो उन्होंने कुछ रोटी ली जिन्हें केवल पादरियों को ही खाने की अनुमति थी। यही वह रोटी थी जो पादरी लोग, हर सप्ताह परमेश्वर के सामने रखते थे। डेविड ने इस रोटी का कुछ हिस्सा अपने साथियों को दे दिया जो उसके साथ थे। हालाँकि, उसने यह काम सबा के दिन नहीं किया होगा, लेकिन डेविड ने जो किया वह वैसा ही लगता है जैसा यीशु के शिष्यों ने किया है। परमेश्वर ने डेविड के कार्य की निंदा कभी नहीं की और यीशु यहाँ कह रहे हैं कि परमेश्वर धार्मिक कानूनों का सख्ती से पालन करने से ज़्यादा इस बात की परवाह करते हैं कि लोगों की ज़रूरतें पूरी हों।"}]},{"type":"paragraph","content":[{"type":"text","text":"बातचीत, पद्य 27 में अजीब तरीके से टूट जाती है, जब इसमें कहा जाता है, \"तब यीशु ने उनसे कहा,...\" जब यीशु कहते हैं कि सबा को, लोगों की ज़रूरतों को पूरा करने के लिए बनाया गया था, तो उनकी बात से सब हैरान हो जाते हैं। जब यीशु कहते हैं, \"इसलिए...आदम का पुत्र, प्रभु है, यहाँ तक कि यह सबा से भी ऊपर है,\" वह कहते हैं कि उनका उन चीज़ों पर नियंत्रण है जिनके बारे में धार्मिक नेता सोचते थे कि वे उनके नियंत्रण में हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी का अगला खंड हमें "},{"type":"text","marks":[{"type":"italic"}],"text":"दिखाता"},{"type":"text","text":" है कि यीशु वास्तव में प्रभु या गुरु हैं, जिनका स्थान सबा से भी ऊपर है। यीशु आराधनालय (संभवतः अभी भी कपरनियम में) में हैं और एक विकृत हाथ वाले व्यक्ति को देखते हैं\\—जो शायद एक लकवाग्रस्त हाथ है, लेकिन यह कहानी, व्यक्ति के ठीक होने के बारे में इतनी नहीं है, बल्कि इस बारे में है कि सबा के दिन ठीक होने से वह धारणा कैसे टूट जाती है जिसे कुछ लोग धार्मिक कानून के रूप में देखते थे। आराधनालय में लोग यीशु को देख रहे हैं\\—देखने के लिए इस्तेमाल किए गए शब्द का मतलब है, किसी को गलत काम करते हुए पकड़ने के लिए देखना। यीशु के द्वारा व्यक्ति को ठीक किए जाने से पहले, वह लोगों से पूछता है, \"क्या सबा के दिन किसी की भलाई करना या किसी को नुकसान पहुँचाना, किसी की जान बचाना या जान लेने की अनुमति है?\" इस तरह के सवाल के लिए एक ऐसे जवाब की उम्मीद की जाती है जो धार्मिक कानून के हिसाब से एक आधिकारिक बयान हो। कानून के अनुसार सही जवाब यह होगा कि कानून ने लोगों को सबा के दिन भलाई का काम करने और जान बचाने की अनुमति दी है। यीशु का मानना था कि इस व्यक्ति को ठीक करना एक भलाई का काम होगा। लोगों को शर्म आएगी अगर उन्होंने कहा कि इस काम को करना एक भलाई का काम नहीं है, लेकिन वे यीशु के सामने हारना नहीं चाहते थे, इसलिए वे चुप थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब कोई शिक्षक या महत्वपूर्ण व्यक्ति आपकी संस्कृति में एक सवाल पूछता है, तो लोग किस तरह जवाब देते हैं? क्या वे चुप रहते हैं? क्या वे एक विशेष, सम्मानजनक तरीके से जवाब देते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु नाराज़ और दुखी थे कि लोग इतने ज़िद्दी थे और अब भी यह नहीं मान रहे थे कि वह सबा के दिन को नियंत्रित कर रहे थे। लेकिन फरीसियों ने महसूस किया कि अगर यीशु ने सबा की उनकी परंपराओं को तोड़ दिया, तो वे जल्द ही उन लोगों पर नियंत्रण खो देंगे, जिन पर कभी उनका नियंत्रण हुआ करता था। बेशक, इसी वजह से वे यीशु को मारना चाहते हैं क्योंकि वे उनके जीवन जीने के तरीके के लिए खतरा बन गए थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"आपकी संस्कृति में किस तरह के काम से धार्मिक नेता नाराज़ हो जाते हैं। धार्मिक नेता, नाराज़ हो जाने पर क्या करते हैं? पारंपरिक धर्मों में और ईसाई चर्च में क्या होता है, दोनों के बारे में चर्चा करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"फरीसी, गैलिली के शासक, हेरोड एंटीपस के समर्थकों या हेरोडियंस से मिलने गए। हेरोडियंस आमतौर पर फरीसियों के दुश्मन थे, लेकिन चूँकि वे दोनों यीशु को मारना चाहते थे, इसलिए वे फरीसियों से मिलने के लिए सहमत थे। इसी के साथ हमें यीशु का कष्टदायक भविष्य दिखना शुरू हो जाता है।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों को परिभाषित करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:23-3:6"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए टेक्स्ट को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में आप इस कहानी का दृश्य बनाने में समूह की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग्स, व कहानी के चरित्रों को उतारने में समूह की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में छह दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" विश्राम के धार्मिक दिन, यीशु अपने शिष्यों के साथ अनाज के खेतों में चल रहे हैं। उनके शिष्य रुक जाते हैं और अनाज की बालियों का सेवन करते हैं। विश्राम के धार्मिक दिन यानी सबा को अनाज तोड़ना कानून के खिलाफ़ था। वहाँ मौजूद फरीसी, यीशु से कहते हैं, \"आप अनाज काटकर, कानून को क्यों तोड़ रहे हैं?\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" यीशु, फरीसियों से बात करते हैं। वह उन्हें पटकथा से एक कहानी बताते हैं। \"एक दिन, डेविड और उनके साथी बहुत भूखे थे। डेविड परमेश्वर के घर गए और रोटी के उस पवित्र टुकड़े खाकर कानून को तोड़ दिया, जिसे केवल पादरियों को ही खाने की अनुमति थी। डेविड ने उस रोटी को अपने साथियों को भी दिया।\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"तीसरा दृश्य:"},{"type":"text","text":" यीशु, फरीसियों की तरफ़ देखते हैं और कहते हैं, \"सबा को लोगों के लिए बनाया गया था, न कि लोगों को सबा की ज़रूरतों को पूरा करने के लिए। आदम का पुत्र, प्रभु है जो सबा से भी ऊपर है।\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"चौथा दृश्य:"},{"type":"text","text":" यीशु एक आराधनालय में जाते हैं और एक विकृत हाथ वाले व्यक्ति को देखते हैं। सबा का दिन होने के कारण, हर कोई यीशु को देख रहा है कि क्या वह कुछ गलत करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पाँचवाँ दृश्य:"},{"type":"text","text":" यीशु विकृत हाथ वाले व्यक्ति को सबके सामने खड़ा करते हैं। वह आराधनालय में लोगों से पूछते हैं, \"क्या धार्मिक कानून हमें सबा के दिन अच्छे काम करने की अनुमति देता है, या हानिकारक काम करने की? क्या यह हमें जीवन बचाने की अनुमति देता है या नष्ट करने की?\" वह विकृत हाथ वाले व्यक्ति से कहते हैं, \"अपना हाथ फ़ैलाइए।\" व्यक्ति उनका हाथ पकड़ लेता है और वह ठीक हो जाता है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"छठा दृश्य:"},{"type":"text","text":" फरीसी तुरंत हेरोड के समर्थकों से मिलने और यीशु की हत्या करने की योजना बनाने के लिए चले जाते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्रों में ये शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"फरीसी लोग"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु की कहानी के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"डेविड"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"डेविड के साथी"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक विकृत हाथ वाला व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आराधनालय में मौजूद लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"फरीसी लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"हेरोड के समर्थक"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह से स्टोरीबोर्ड बनाने को कहें, रेखाचित्र बनाएँ या इसमें कथा का दृश्य बनाने के लिए किसी तरह के फ़ेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य में, यीशु और उनके साथी, अनाज के खेतों में जा रहे हैं और खाने के लिए अनाज की बालियाँ तोड़ लेते हैं। फरीसी, यीशु को संबोधित करते हैं। वे बताते हैं कि सबा के दिन, लोगों को काम करना नहीं चाहिए।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य में, यीशु, डेविड के बारे में एक कहानी बताते हैं। डेविड और उनके साथियों की कहानी की कल्पना करें, जो परमेश्वर के घर गए और वह रोटी खाई जो केवल पादरियों के लिए थी।"}]},{"type":"paragraph","content":[{"type":"text","text":"तीसरे दृश्य में, कल्पना करें कि यीशु, सबा से अधिक महत्वपूर्ण, प्रभु हैं और लोगों को, लोगों के लिए, न कि सबा के कानून के लिए, सबा के दिन आराम करना चाहिए।"}]},{"type":"paragraph","content":[{"type":"text","text":"चौथा दृश्य भी एक सबा के दिन होता है, लेकिन यह एक अलग सेटिंग में होता है। कल्पना करें कि यीशु, एक यहूदी पूजा घर, एक आराधनालय में प्रवेश कर रहे हैं और एक विकृत हाथ वाले व्यक्ति को सबके सामने खड़े होने के लिए कह रहे हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पाँचवें दृश्य में संवाद की कल्पना करें। यीशु, आराधनालय में एकत्रित लोगों से पूछते हैं, जिनमें फरीसी भी शामिल हैं, \"क्या धार्मिक कानून हमें अच्छा या बुरा काम करने, जान बचाने या जान लेने की अनुमति देता है?\" लेकिन कोई भी उन्हें जवाब नहीं देता है। यीशु गुस्से से और उदास होकर चारों ओर देखते हैं, और उसके बाद व्यक्ति से कहते हैं, \"अपना हाथ फ़ैलाइए।\" व्यक्ति अपना हाथ फ़ैलाता है और वह ठीक हो जाता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"अंतिम दृश्य में, कल्पना करें कि फरीसी तुरंत आराधनालय से जा रहे हैं। वे हेरोड के समर्थकों के साथ मिलकर यह साज़िश करने लगते हैं कि यीशु की हत्या कैसे की जाए।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"पाठ को एक मूर्त आकार देना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:23-3:6"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए टेक्स्ट को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में छह दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्रों में ये शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"फरीसी लोग"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु की कहानी के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"डेविड"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"डेविड के साथी"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक विकृत हाथ वाला व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आराधनालय में मौजूद लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"फरीसी लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"हेरोड के समर्थक"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने को कहें। जिस भाषा में वे अनुवाद कर रहे हैं, उसी भाषा में उन्हें उस पर अभिनय करना चाहिए। सबसे पहले, टीम को बिना रुके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी की अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जैसे जैसे टीम कहानी पर पहली बार अभिनय करती है, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ बिंदुओं पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु और उनके शिष्य चल रहे हैं। जब वे चलते हैं, तो शिष्य रुक जाते हैं और अनाज की बालियों को खाने लगते हैं। फरीसी यह देखते हैं और यीशु से पूछते हैं, \"आपके शिष्य सबा के दिन अनाज की कटाई करके कानून को क्यों तोड़ रहे हैं?\" "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" फरीसी की भूमिका निभाने वालों से पूछें, \"आप अभी कैसा महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"हम यीशु को अपने धार्मिक कानूनों को तोड़ते देखते रहते हैं\" या \"हम इस बात से परेशान हैं कि यीशु अन्य लोगों को अपने धार्मिक कानूनों को तोड़ने के लिए प्रोत्साहित कर रहे हैं।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु, डेविड के बारे में एक कहानी बताते हैं। डेविड और उनके साथी भूखे थे, इसलिए वे परमेश्वर के घर गए और वह रोटी खाकर कानून को तोड़ दिया जो केवल पादरियों के खाने के लिए ही थी। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" डेविड की भूमिका निभाने वाले अभिनेता से पूछें, \"आपने कानून को क्यों तोड़ दिया?\" आप ऐसी बातें सुन सकते हैं कि \"परमेश्वर चाहते थे कि मैं अपने साथियों के लिए भोजन की व्यवस्था करूँ क्योंकि हम भूखे थे\" या \"हमारे लिए परमेश्वर की देखभाल, धार्मिक कानूनों से अधिक महत्वपूर्ण है।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु, फरीसियों से कहते हैं कि सब्त का निर्माण लोगों की जरूरतों को पूरा करने के लिए किया गया था, न कि सब्त की आवश्यकताओं को पूरा करने वाले लोगों के लिए। यीशु कहते हैं, \"आदम का पुत्र, प्रभु है, जो सबा से भी ऊपर है।\" "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" समूह से पूछें, \"आदम के पुत्र के लिए सबा के दिन से ऊपर होने यानी प्रभु बनने का क्या मतलब है?\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु फिर एक आराधनालय में जाते हैं और एक विकृत हाथ वाले व्यक्ति को देखते हैं। वह उस व्यक्ति को सबके सामने खड़े होने के लिए कहते हैं, और पूछते हैं, \"क्या धार्मिक कानून, सबा के दिन, हमें अच्छा काम करने की अनुमति देता है या बुरा काम करने की? क्या यह जान बचाने का दिन है या जान लेने का?\" "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" यीशु की भूमिका निभाने वाले अभिनेता से पूछें, \"आप कैसा महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"मैं सबा के बारे में एक बात कहने वाला हूँ,\" \"मैं चाहता हूँ कि लोग मुझे समझें,\" या \"मैं इस बात से नाराज़ हूँ कि लोग देखभाल और करुणा से अधिक महत्व अपने धार्मिक कानूनों और परंपराओं को देते रहते हैं।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कोई भी यीशु को जवाब नहीं देता है। यीशु चारों ओर देखते हैं और उनके पत्थर दिल से बहुत नाराज़ और उदास दिखाई देते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" आराधनालय में लोगों की भूमिका निभाने वाले अभिनेताओं से पूछें, \"आप यीशु को जवाब क्यों नहीं देते हैं?\" आप ऐसी बातें सुन सकते हैं कि \"हम जानते हैं कि वह सही हैं, इसलिए हम उन्हें जवाब नहीं देना चाहते हैं,\" \"हम यीशु के सामने हारना नहीं चाहते हैं,\" \"हम नहीं जानते कि कैसे जवाब दें,\" या \"हम शर्मिंदा हैं।\" यीशु की भूमिका निभाने वाले अभिनेता से पूछें, \"आप कैसा महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"मैं नाराज़ हूँ,\" \"मैं बहुत उदास हूँ,\" या \"मैं इस बात से निराश हूँ कि ये लोग मेरी बात नहीं सुन रहे हैं।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु, विकृत हाथ वाले व्यक्ति से कहते हैं, \"अपना हाथ फ़ैलाइए।\" व्यक्ति अपना हाथ फ़ैलाता है और वह ठीक हो जाता है। फरीसी तुरंत चले जाते हैं। वे यह योजना बनाने के लिए हेरोड के समर्थकों से मिलते हैं कि वे कैसे यीशु की हत्या कर सकते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय के अंत में,"},{"type":"text","text":" फरीसियों की भूमिका निभाने वाले अभिनेताओं से पूछें, \"आप कैसा महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"गुस्सा,\" \"इस बात से नाराज हैं कि यीशु ने सबा के दिन किसी को ठीक कर दिया,\" \"यीशु की शक्ति से डर रहे हैं,\" \"हम सोचते हैं कि यीशु परमेश्वर की निन्दा कर रहे हैं,\" या \"हम इतने नाराज़ हैं कि हम यीशु की हत्या कर सकते हैं।\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"खाली जगह को भरना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:23-3:6"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए सबसे आसान लहजे में टेक्स्ट को एक बार सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"सबा"}]},{"type":"paragraph","content":[{"type":"text","text":"उसी शब्द का इस्तेमाल करें, जो आपने सबा के लिए पहले इस्तेमाल किया है। यहूदी लोगों ने सबा के दिन को विशेष रूप से विश्राम और पूजा के लिए बनाया था, क्योंकि परमेश्वर ने उन्हें यह आदेश दिया था। इसे सप्ताह का 7वाँ दिन माना जाता था। यीशु के समय पर, इस बारे में सख्त, विशिष्ट नियम थे कि इस दिन किस तरह का काम किया जा सकता है और किस तरह का काम नहीं किया जा सकता है। यह वह दिन भी था जब लोग पूजा करने के लिए आराधनालय में जाते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"शिष्य"}]},{"type":"paragraph","content":[{"type":"text","text":"उसी शब्द का इस्तेमाल करें जिसे आपने शिष्यों के लिए पहले इस्तेमाल किया है। यीशु के "},{"type":"text","marks":[{"type":"italic"}],"text":"सबसे करीबी अनुयायी"},{"type":"text","text":" उनकी पब्लिक मिनिस्ट्री के ज़्यादातर समय उनके साथ थे, हर जगह उनके साथ यात्रा करते थे, उनसे सीखते थे, और कभी-कभी उन्हें राज्य की अच्छी खबर का उपदेश देने और बीमार को ठीक करने के लिए यीशु द्वारा भेजा जाता था। अगर भाषा में एक शिक्षक के समर्पित अनुयायियों के लिए एक आसान शब्द है, तो आप उस शब्द का इस्तेमाल कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"फरीसी लोग"}]},{"type":"paragraph","content":[{"type":"text","text":"उसी शब्द का इस्तेमाल करें जिसे आपने फरीसियों के लिए पहले इस्तेमाल किया है। \"फरीसी\" नाम का अर्थ \"अलग होना\" है। फरीसी बहुत प्रभावशाली और सही माने जाते थे। वे लोगों के लिए अच्छे उदाहरण थे क्योंकि वे परमेश्वर के नियमों के प्रति समर्पित थे। उन्होंने लिखित और मौखिक दोनों तरह की धार्मिक परंपराओं और कानूनों को मान्यता दी। फरीसी शब्द इस प्रकार के धार्मिक नेता का नाम है।"}]},{"type":"paragraph","content":[{"type":"text","text":"शास्त्र"}]},{"type":"paragraph","content":[{"type":"text","text":"किसी भी धार्मिक समूह का पवित्र लेखन।"}]},{"type":"paragraph","content":[{"type":"text","text":"आप यहाँ कौनसा शब्द इस्तेमाल करेंगे? याद रखें कि आप जिस शब्द का इस्तेमाल करते हैं, उसे किसी विशिष्ट धर्म के पवित्र लेखन का उल्लेख नहीं करना चाहिए। यह एक सामान्य शब्द होना चाहिए।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने कहा कि डेविड को अबियाथर के समय उच्च पादरी बनने का मौका मिला था। हम जानते हैं कि अबियाथर उस समय उच्च पादरी नहीं थे, लेकिन उनका शासन उस समय के आसपास था।"}]},{"type":"paragraph","content":[{"type":"text","text":"उच्च पादरी/पादरी लोग"}]},{"type":"paragraph","content":[{"type":"text","text":"यहूदी धर्म में पादरी वे लोग थे जिन्होंने लोगों को परमेश्वर से जोड़ने के लिए, परमेश्वर के लिए उनसे बलिदान करवाए, लोगों को शिक्षित किया था और परमेश्वर के मंदिर की देखभाल की थी। पुजारी अपने लोगों केे लिए परमेश्वर के प्रतिनिधि थे और परमेश्वर के लिए लोगों के प्रतिनिधि थे। उच्च पादरी सबसे महत्वपूर्ण या प्रमुख पादरी होते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर का घर"}]},{"type":"paragraph","content":[{"type":"text","text":"इसे प्रत्यक्ष रूप से प्रस्तुत करें\\—शायद मंदिर को संदर्भित करता है। चूँकि हम पक्का नहीं हैं, इसलिए इसे \"परमेश्वर के घर\" के रूप में रखना सबसे अच्छा है।"}]},{"type":"paragraph","content":[{"type":"text","text":"उपस्थिति की रोटी"}]},{"type":"paragraph","content":[{"type":"text","text":"यह वह रोटी थी जिसे यहूदी मंदिर में पादरियों द्वारा परमेश्वर के सामने रखा गया था। उपस्थिति की रोटी इस रोटी का आधिकारिक नाम है, लेकिन इसका मतलब है एक विशेष रोटी, जिसे खुद परमेश्वर के लिए अलग रखा गया था। आप ज़रूरत पड़ने पर इसका वर्णन इस तरह भी कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"आदम का बेटा"}]},{"type":"paragraph","content":[{"type":"text","text":"पुराने टेस्टामेंट में \"आदम का बेटा\" शब्द का अर्थ बस एक व्यक्ति है। यीशु इस शब्द का इस्तेमाल अपने बारे में बात करने के लिए अपने पसंदीदा तरीके के रूप में करते हैं। \"आदम का बेटा\" वह व्यक्ति है जिसे डैनियल 7 में, परमेश्वर ने सभी अधिकार और शक्ति दी हैं**।** यीशु को शायद यह शब्द इसलिए पसंद है क्योंकि यह शीर्षक एक साधारण व्यक्ति को या एक अलौकिक सत्ता को संदर्भित कर सकता है, जैसा कि डैनियल 7 में किया गया है। आदम का बेटा शब्द लोगों को यह फ़ैसला करने के लिए मजबूर करता है कि यीशु, इंसान हैं या परमेश्वर। इसने मसीहा/मसीह के राजनीतिक शब्द से भी परहेज़ किया\\—वह व्यक्ति जिसे लोग राजा बनाना चाहते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क हर बार उस पर बात करना पसंद करते हैं जब यीशु ने खुद को आदम का बेटा कहा, ताकि वह एक व्यक्ति की तरह दुखी होने और मरने की उनकी किस्मत के बारे में बात कर सकें। लेकिन, आदम का बेटा एक आम इंसान से बढ़कर हैं और वे मरकर भी ज़िंदा हो जाएँगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु तीसरे व्यक्ति की तरह खुद की बात करते हैं, जब वह खुद को यह खिताब देते हैं। पक्का करें कि आपके अनुवाद से यह स्पष्ट हो यीशु खुद के बारे में बोल रहे हैं। कभी-कभी ''आदम का बेटा'' शब्द का मतलब है कि यीशु एक कुंवारी महिला से नहीं पैदा हो सकते हैं। अगर यह मामला है, तो आप कुछ ऐसा कह सकते हैं कि \"वह व्यक्ति जो वास्तव में व्यक्ति था।\""}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय"}]},{"type":"paragraph","content":[{"type":"text","text":"उसी शब्द का इस्तेमाल करें जिसे आपने आराधनालय के लिए पहले इस्तेमाल किया है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और सुनिश्चित करें कि आप उसी शब्द का इस्तेमाल कर रहे हैं जिसे आपने पहले ही इस्तेमाल किया है।"}]},{"type":"paragraph","content":[{"type":"text","text":"सूखा हाथ"}]},{"type":"paragraph","content":[{"type":"text","text":"अकड़न का संदर्भ देता है\\—शायद एक हाथ जो हिल नहीं सकता है या इस्तेमाल नहीं किया जा सकता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"जान बचाना या जान लेना?"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ बचाने के लिए सोज़ो शब्द का इस्तेमाल किया गया है\\—इसका इस्तेमाल ''संरक्षण,'' ''ठीक,'' ''इलाज'', ''उद्धार'' के रूप में किया जा सकता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"सामने खड़े हो जाइए"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय में लोग एक गोलाकार तरीके से बैठे थे, शायद इसलिए उसे \"बीच में खड़ा होने को कहा गया\" जहाँ लोग बैठे थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"उनके दिल की कठोरता/ज़िद्दी दिल पर दुखी"}]},{"type":"paragraph","content":[{"type":"text","text":"यह उन लोगों को संदर्भित करता है जो यीशु की बातों में छिपी सच्चाई को देखना नहीं चाहते थे। यह सच्चाई को न देखने या उस पर अमल न करने के ज़िद्दीपन को संदर्भित करता है। यीशु का दुःख वैसा नहीं था जैसा कि लोग उस बात पर दुखी होते थे जब वे किसी को खो देते थे, लेकिन वह एक गहरी गड़बड़ी थी जिसे वह देख रहे थे।"}]}]}}]', [ContentSize] = 52495, Updated = GETUTCDATE() WHERE Id = @TextResourceContentId;

