UPDATE ResourceContents SET [DisplayName] = N'मरकुस 1:14-20', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:14-20 को सुनें और इसे दिल से याद कर लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप किसी ऐसे को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:14-20"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"हमने अभी देखा कि मार्क ने यीशु की मिनिस्ट्री का परिचय दिया और अब मिनिस्ट्री शुरू हो चुकी है। मार्क जॉन और यीशु के बीच की कहानी को अलग-अलग तरह से बताता है। जॉन अब जेल में हैं और यीशु चल रही कहानी का फ़ोकस हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु सिखाते हैं और दिखाते हैं (चमत्कार के ज़रिए) कि परमेश्वर का राज्य करीब है। यीशु के समय में लोगों का मानना था कि एक दिन ईश्वर इज़राइल की अपनी प्रजा के ज़रिए पूरे विश्व पर शासन करेंगे। लेकिन मार्क में, यीशु आ गए हैं और वह एक अलग तरह के राज्य के बारे में बता रहे हैं। वह एक ऐसे राज्य के बारे में बता रहे हैं, जहां परमेश्वर अब हमारे दिलों पर राज करते हैं (यीशु के ज़रिए) और एक दिन पूरी तरह से वह पृथ्वी पर सब कुछ पुनर्स्थापित कर देते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु का संदेश वर्तमान में हो रही चीज़ों के बारे में बताता है। उनका कहना है कि \"समय आ गया है\"। इसका अर्थ है कि परमेश्वर के राज्य के बारे में पुराने आदेश में जो सभी भविष्यवाणियां की गई थीं और जिस रक्षक के बारे में वादा किया गया था, वह सब अब होने लगा है, क्योंकि यीशु, हमारे रक्षक, अपनी मिनिस्ट्री की शुरुआत कर रहे हैं। \"समय\" का अर्थ है कि परमेश्वर ने इस समय को विशेष रूप से अपने राज्य के लिए चुना है \\— यह लोगों के लिए यीशु से बात करने का एक अच्छा समय है क्योंकि वे सीधे यीशु से जवाब पा सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु का संदेश एकदम सरल है\\—प्रयाश्चित करो\\—आज्ञाकारी बनो और ईश्वर की शरण में आ जाओ। और विश्वास करें\\—मार्क, यीशु पर विश्वास रखने के बारे में बहुत कुछ कहते हैं। साथ ही, वे यीशु की आज्ञा मानकर और उनका अनुसरण करके यह दिखाने के बारे में बताते हैं कि आप यीशु को मानते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"जैसे ही यीशु अपनी मिनिस्ट्री की शुरुआत करते हैं, तो वह शिष्यता जैसे महत्वपूर्ण विषय पर ध्यान केंद्रित करते हैं। पैंगबर जरमाया ने उन लोगों के बारे में बात की जो दूसरे लोगों को ईश्वर के रास्ते पर लाने की कोशिश करते हैं या जो न्याय करने के लिए उन्हें परमेश्वर के पास लाते हैं। हालांकि, यीशु ''लोगों को ईश्वर के रास्ते पर लाने'' या लोगों को यीशु तक लाने के बारे में बात करते हैं, ताकि उन्हें बचाया जा सके और उन्हें समझाया जा सके कि परमेश्वर के राज्य में कैसे रहा जाता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु लोगों को बुलाते हैं, तो वह उनसे अपेक्षा करते हैं कि वे तुरंत जवाब दें और उनसे पूरी तरह से प्रतिबद्ध हों, भले ही समय कितना भी मुश्किल क्यों न हो। जेम्स और जॉन ने यीशु का अनुसरण करने के लिए, अपने परिवार और एक संपन्न व्यवसाय समेत सब कुछ छोड़ दिया। जब लोग यीशु का अनुसरण करते हैं, तो इसका अर्थ है कि वे उनके आह्वान पर जवाब देते हैं, उनके राज को मानते हैं और उनके बताए रास्ते पर चलते हैं। यीशु जिस बात का आदेश देते हैं, लोग उस बात पर अमल करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहां रुकें और एक अनुवाद टीम के रूप में चर्चा करें: आपकी संस्कृति में जब लोग किसी सम्मानित शिक्षक या नेता का अनुसरण करने का फ़ैसला करते हैं, तब वे क्या करते हैं? उनके जीवन में कैसे बदलाव आता है?"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का मतलब बताना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:14-20"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में आप इस कहानी का दृश्य बनाने में समूह की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग्स, व कहानी के चरित्रों को उतारने में समूह की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" जॉन को गिरफ़्तार कर लिया गया है। यीशु गैलिलि में जाते हैं, जहां वह अपनी मिनिस्ट्री की शुरुआत करते हैं और परमेश्वर के राज्य के आने के बारे में बताते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" यीशु गैलिलि के सागर के किनारे पर चल रहे हैं और अपने पहले शिष्यों को बुला रहे हैं, जो अपने जाल छोड़कर उनका अनुसरण करने लगे हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जॉन (जो गिरफ़्तार हो गए हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वह व्यक्ति जिन्होंने जॉन को गिरफ़्तार किया है"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु (जो परमेश्वर के राज्य का उपदेश दे रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वे लोग जिनको यीशु उपदेश दे रहे हैं"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु (जो समुद्र तट पर चल रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"साइमन और उनके भाई एंड्रयू (जो पानी में जाल फेंक रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जेम्स और जॉन (जो अपने जाल को तैयार कर रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जेम्स और जॉन के पिता, ज़ेबेदी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"ज़ेबेदी के साथ नाव में नौकर"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यह ग्रुप इस कहानी को विज़ुअलाइज़ करे, उन्हें गैलिलि सागर और मछली पकड़ने वाले जाल की तस्वीर दिखाएँ। यहीं पर यीशु ने अपनी मिनिस्ट्री का अधिकांश समय बिताया।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"टेक्स्ट को एम्बेड करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:14-20"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जॉन (जो गिरफ़्तार हो गए हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वह व्यक्ति जिन्होंने जॉन को गिरफ़्तार किया है"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु (जो परमेश्वर के राज्य का उपदेश दे रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वे लोग जिनको यीशु उपदेश दे रहे हैं"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु (जो समुद्र तट पर चल रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"साइमन और उनके भाई एंड्रयू (जो पानी में जाल फेंक रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जेम्स और जॉन (जो अपने जाल को तैयार कर रहे हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जेम्स और जॉन के पिता, ज़ेबेदी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"ज़ेबेदी के साथ नाव में नौकर"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने को कहें। जिस भाषा में वे अनुवाद कर रहे हैं, उसी भाषा में उन्हें उस पर अभिनय करना चाहिए। सबसे पहले, टीम को बिना रूके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी के अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जैसे जैसे टीम कहानी पर पहली बार अभिनय करते है, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ जगहों पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कहानी में, यीशु गैलिलि में जाते हैं और जॉन की गिरफ़्तारी के बाद उपदेश देने लगते हैं। जॉन की गिरफ़्तारी के बाद, "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय को बंद कर दें"},{"type":"text","text":"। यीशु की भूमिका निभाने वाले अभिनेता से पूछें, कि वह कैसा महसूस कर रहे हैं और वह क्या सोच रहे हैं। आप ऐसी बातें सुन सकते हैं कि \"वह उदास थे, जॉन उनके चचेरे भाई थे\" या \"यीशु को पता था कि यह उनकी मिनिस्ट्री का एक शुरुआती बिंदु था क्योंकि वे परमेश्वर के राज्य के बारे में उपदेश देना चाहते थे।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु साइमन और एंड्रयू को बुलाते हैं और कहते हैं, \"आओ, मेरा अनुसरण करो और मैं तुम्हें लोगों को ईश्वर के रास्ते पर लाना सिखाऊँगा,\" "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय को बंद कर दें"},{"type":"text","text":"। साइमन और एंड्रयू की भूमिका निभाने वाले अभिनेताओं से पूछें, कि वे क्या सोचते हैं जब यह शिक्षक उन्हें बुलाते हैं। आप ऐसी बातें सुन सकते हैं कि \"उन्हें बस पता था कि उन्हें उनके साथ चलना चाहिए\" या \"उन्हें इस बात का एहसास था कि उन्हें तुरंत अपने जाल को छोड़कर, उनका अनुसरण करना चाहिए।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कहानी के बाकी हिस्सों का अभिनय करना खत्म करें। अंत में, ग्रुप से पूछें, \"अगर आप एक मछुआरे होते और आप अपना काम कर रहे होते और यीशु आपके पास आकर, आपसे उनका अनुसरण करने को कहते, तो आप क्या प्रतिक्रिया देते? आपको कैसा महसूस होता?\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"गैप को भरते हुए"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:14-20"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"गैलिलि आओ/जाओ\\—"}]},{"type":"paragraph","content":[{"type":"text","text":"किसी जगह पर आने या किसी जगह जाने के लिए एक्शन शब्द का इस्तेमाल करें\\—जो भी आपकी भाषा में सबसे स्वाभाविक एक्शन शब्द हो।"}]},{"type":"paragraph","content":[{"type":"text","text":"गैलिलि"}]},{"type":"paragraph","content":[{"type":"text","text":"यह गैलिलि सागर/झील के आसपास का क्षेत्र है. यीशु इस कहानी में गैलिलि सागर के किनारे चल रहे थे। यह झील लगभग 21 किलोमीटर लंबी और 10 किलोमीटर चौड़ी है। इसकी सर्वाधिक गहराई 67 मीटर है। कई लोग इसे मछली पकड़ने और पैसा कमाने के लिए इस्तेमाल करते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"यदि आपने पहले अनुवादकों को झील का नक्शा और तस्वीरें नहीं दिखाई हैं, तो अब दिखाएँ। आप इस जलाशय के लिए क्या शब्द इस्तेमाल करेंगे, इस बात पर चर्चा करें*।*"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर का राज्य"}]},{"type":"paragraph","content":[{"type":"text","text":"एक राज्य एक ऐसा स्थान है जहां एक राजा लोगों के एक समूह पर शासन करता है। पुराने नियम में, परमेश्वर का राज्य उनकी इज़रायल की प्रजा थी। लेकिन इज़रायल की जनता कभी भी परमेश्वर की बात नहीं मानती थी। अब परमेश्वर का राज्य किसी जगह को नहीं, बल्कि उन सभी लोगों को संदर्भित करता है, जो उनका अनुसरण करते हैं। हालाँकि, हम जानते हैं कि अब भी सब परमेश्वर की बात नहीं मानते हैं और उनका राज्य अभी भी पूरी तरह से सही नहीं है। लेकिन यीशु भी परमेश्वर के राज्य का हिस्सा बनने आए थे। परमेश्वर का राज्य, अब वे लोग हैं जो यीशु का राजा के रूप में अनुसरण कर रहे हैं, जो एक दूसरे की मदद कर रहे हैं और जो दूसरों को यीशु का प्यार बाँट रहे हैं। बाद में परमेश्वर उत्तम राज्य लेकर आएंगे। परमेश्वर आएँगे, बुरे लोगों का न्याय करेंगे, और दुनिया सही हो जाएगी। परमेश्वर अपने लोगों पर पूरी तरह से राज करेंगे। इसके बाद, कोई और तकलीफ़ या बुराई नहीं होगी और सभी लोग उनकी बात मानेंगे। जब यीशु ने परमेश्वर के राज्य के बारे में बात की, तो लोगों को यह समझ में नहीं आया, इसलिए यह उन्हें एक रहस्य जैसा लगा। उन्हें सच में यीशु की बात सुनने की ज़रूरत थी, ताकि वे समझ सकें कि वह क्या कहना चाहते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"हम अब परमेश्वर के राज्य का अनुभव करते हैं, क्योंकि हमने उनकी बात सुनी और यीशु का अनुसरण करते हुए उनकी बात मानी।"}]},{"type":"paragraph","content":[{"type":"text","text":"पश्चाताप"}]},{"type":"paragraph","content":[{"type":"text","text":"पापों से मुँह मोड़ें और परमेश्वर की शरण में जाएँ, और परमेश्वर की राह पर चलते हुए एक नया जीवन जीना शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"आपने जिन शब्दों का पहले इस्तेमाल किया है, उन्हीं शब्दों का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"विश्वास करने का मतलब"},{"type":"text","text":" उस निष्ठा से है जो कि भावनात्मक तौर पर आपको मज़बूत बनाती है, आपके दिल को ईश्वर पर भरोसा दिलाती है और जो हर संदेह दूर करती है।"}]},{"type":"paragraph","content":[{"type":"text","text":"कुछ भाषाओं में विश्वास और आज्ञा के लिए सिर्फ़ एक शब्द है। विश्वास, परमेश्वर के वचन के प्रति लोगों की प्रतिक्रिया को बताता है। यह परमेश्वर पर आपकी आस्था या विश्वास के बारे में है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"शिक्षा के टूल:"},{"type":"text","text":" पहले ही किसी स्वयंसेवक से इसको लेकर मदद माँग लें। कमरे के बीच में एक कुर्सी रखें और स्वयंसेवक से पूछें, \"क्या आप मानते हैं कि यहां एक कुर्सी है? क्या आप मानते हैं कि यह एक कुर्सी है? क्या आप मानते हैं कि यह कुर्सी इतनी मज़बूत है कि आप इस पर आराम से बैठ सकें?\" स्वयंसेवक को इन सभी सवालों के लिए \"हां\" कहना चाहिए। फिर पूछें, \"मुझे पता है कि आप थक चुके हैं, इसलिए यह कुर्सी आपके बैठने के लिए है।\" स्वयंसेवक को कुर्सी पर बैठने से इंकार कर देना चाहिए। ऐसा कई बार करें, यह पूछते हुए कि स्वयंसेवक कुर्सी पर क्यों नहीं बैठेंगे, जबकि उन्हें पता है कि कुर्सी वहाँ है और वह मानते हैं कि कुर्सी काफ़ी मज़बूत है। ग्रुप को समझाएँ कि यह विश्वास के बारे में एक उदाहरण है। अगर व्यक्ति कुर्सी पर नहीं बैठेगा तो वास्तव में उन्हें कुर्सी पर विश्वास नहीं है।"}]},{"type":"paragraph","content":[{"type":"text","text":"खुशखबरी/गॉस्पल"}]},{"type":"paragraph","content":[{"type":"text","text":"अच्छी खबर यह है कि यीशु इस धरती पर थे, हमारे पापों की वजह से वह मर गए और अब वह फिर से जी उठे हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"साइमन"},{"type":"text","text":" यहाँ साइमन पीटर के रूप में वही व्यक्ति है, जो बाद में यीशु के शिष्यों में से एक बने।"}]},{"type":"paragraph","content":[{"type":"text","text":"साइमन के भाई"}]},{"type":"paragraph","content":[{"type":"text","text":"साइमन अपने भाई से बड़े हैं, इसलिए दोनों भाइयों के बारे में बात करते समय, आप अपनी संस्कृति में, उनके नाम किसी भी क्रम में डाल सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"मछली पकड़ने का जाल"}]},{"type":"paragraph","content":[{"type":"text","text":"मछली पकड़ने के जाल गोलाकार थे और कलाई में लिपटे हुए थे। उन्हें हाथ से एक गोलाकार तरीके से फेंका जाता था।"}]},{"type":"paragraph","content":[{"type":"text","text":"मछली पकड़ने वाले जाल का एक चित्र दिखाएं"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"मछुआरे"},{"type":"text","text":" ऐसे व्यक्ति थे जो पैसे कमाने के लिए मछली पकड़ते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"नाव"}]},{"type":"paragraph","content":[{"type":"text","text":"मछली पकड़ने वाली नाव लगभग 8 मीटर लंबी और 2 ½ मीटर चौड़ी थीं। यह लगभग 1 1/4 मीटर गहरी थी। इतने बड़े आकार की नाव में तेरह लोग आराम से आ सकते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपने अनुवादकों को नाव की तस्वीर दिखाएं और नाव का आकार दिखाने के लिए एक तरीका चुनें। आप एक छड़ी से जमीन पर आयाम खींचकर और लोगों से खींची हुई लाइनों के अंदर खड़े होने के लिए कहकर, ऐसा कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"जाल को ठीक करना"}]},{"type":"paragraph","content":[{"type":"text","text":"जेम्स और जॉन या तो अपने जाल को इस्तेमाल करने के लिए तैयार कर रहे होते थे या उनके फटने के बाद उन्हें सही कर रहे होते थे। ठीक करना शब्द का मतलब यहाँ कुछ भी हो सकता है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"लोगों के मछुआरे"},{"type":"text","text":" ऐसे मछुआरे थे जो लोगों को ईश्वर के रास्ते पर ले जाते थे। इस बात का ध्यान रखें कि आप ''कैचिंग'' या ''फ़िशिंग'' के लिए बुरे शब्दों का इस्तेमाल न करें। आप ऐसा कह सकते हैं कि आप लोगों को ऐसे तरीके से पकड़कर ईश्वर के रास्ते पर ले जाएँगे जैसे आप मछली को पकड़ते हैं..."}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"यीशु ने उन्हें अनुसरण करने के लिए बुलाया"},{"type":"text","text":" या उन्हें उनके साथ आने के लिए आमंत्रित किया"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अनुसरण करना,"},{"type":"text","text":" आदेश पर प्रतिक्रिया देना, अधिकार के लिए स्वीकृति देना, अनुकरण के उदाहरण पेश करना होता है"}]},{"type":"paragraph","content":[{"type":"text","text":"यह यीशु की आज्ञा थी कि उनका इस तरह से अनुसरण किया जाए।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"काम पर रखे गए व्यक्ति"},{"type":"text","text":" गुलाम नहीं थे, बल्कि उन्हें काम करने के लिए पैसे दिए जाते थे।"}]}]}}]', [ContentSize] = 36637, Updated = GETUTCDATE() WHERE Id = @TextResourceContentId;

