SELECT @TextResourceContentId = NULL;
SELECT @TextResourceContentId = Id FROM ResourceContents WHERE [ResourceId] = @ResourceId AND [LanguageId] = @LanguageId AND [MediaType] = 1;
IF @TextResourceContentId IS NULL
BEGIN
INSERT INTO ResourceContents([ResourceId], [LanguageId], [DisplayName], [Version], [Completed], [Trusted], [Content], [ContentSize], [MediaType]) VALUES (@ResourceId, @LanguageId, N'मरकुस 1:21-28', 1, 1, 1, N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:21-28 सुनें और इसे दिल से याद कर लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"क्या आप किसी ऐसे व्यक्ति को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:21-28"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने अभी चार मछुआरों को अपने शिष्य बनने के लिए बुलाया है। उनमें से 5 पास में ही मौजूद केपरनॉम गए थे। केपरनॉम मिस्र और दमास्कस के बीच मुख्य सड़क पर एक महत्वपूर्ण शहर था। यह गैलिलि इलाके में मिनिस्ट्री के दौरान यीशु का मुख्यालय बन गया।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम को गैलिलि सागर का एक नक्शा दिखाएँ, जो केपरनॉम, मिस्र और दमास्कस को दिखाता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"सबा के दिन या विश्राम और पूजा के यहूदियों के दिन, लोगों का उपदेश सुनने के लिए आराधनालय में इकट्ठा होना सामान्य बात थी। आराधनालय वह जगह थी जहाँ पूरा गाँव पूजा करता था। यह वही जगह है जहाँ लोग इकट्ठा हुए थे और जहाँ उन्होंने सम्मानित शिक्षकों के साथ परमेश्वर के उपदेशों पर चर्चा की थी। ज़ाहिर है, लोग यीशु को जानते थे और उनका सम्मान करते थे क्योंकि उन्होंने ही यीशु को उन्हें शिक्षा देने के लिए कहा था। मार्क हमें यह नहीं बताते कि यीशु ने इस दिन क्या सिखाया, लेकिन हमें पता है कि यीशु का उपदेश असामान्य है। आमतौर पर, कानून के शिक्षक धार्मिक कानून के बारे में बात करते थे और पैगंबर ने किसी विषय के बारे में क्या कहा था, इस बारे में बात करते थे। इस मामले में, यीशु अपने अधिकार में बोलते हुए प्रतीत होते हैं। शायद वह परमेश्वर के राज्य के बारे में बात करते हुए, लोगों को बता रहे हैं कि उन्हें क्या करना चाहिए। यीशु ऐसे बोले जैसे कि उनके पास लोगों को क्या करना है यह बताने की शक्ति और अधिकार है, पर लोग इसकी उम्मीद नहीं कर रहे थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"तब राक्षस के वश में मौजूद व्यक्ति बोलता है। इस बात पर आश्चर्य होता है कि एक राक्षस के वश में मौजूद व्यक्ति आराधनालय में कैसे आ गया। राक्षस वैसे ही बोल रहा था जैसे कि यहूदी लोगों को लगता था। वे आमतौर पर ऐसा कहते हैं \"आपका हमसे कोई लेना-देना नहीं है!\" वह राक्षस यीशु से बहुत हिंसक तरह से बात करता है\\—यह दिखाता है कि वह यीशु से डर रहा है, क्योंकि वह जानता है कि यीशु कौन हैं और यीशु को उसे यह बताने का अधिकार है कि उसे क्या करना चाहिए और वह उसे नष्ट कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"राक्षस यीशु को उनके नाम से बुलाता है\\—नाज़ारेथ का यीशु। लोगों का मानना था कि जब आप किसी को उनके नाम से बुलाते हैं तो उन पर आपका नियंत्रण होता है। फिर वह यीशु को \"परमेश्वर का पवित्र पुत्र\" कहता है। यह राक्षस इस नाम का इस्तेमाल करता है, जिसका अर्थ है कि वह जानता है कि यीशु में परमेश्वर की पवित्र आत्मा है। यह नाम बताता है कि राक्षस यीशु को जानता है\\—वह परमेश्वर हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहां रुकें और अपनी अनुवाद टीम से चर्चा करें कि आप अपनी संस्कृति में नामों का इस्तेमाल कैसे करते हैं। इन नामों का क्या अर्थ है? आप लोगों को उनके नाम से कब बुलाते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु यहाँ अपने कार्यों से अपनी शक्ति दिखाते हैं। आमतौर पर कानून के शिक्षक राक्षसों को बाहर निकालने के लिए संस्कार या समारोह का प्रदर्शन करते हैं। लेकिन यीशु को बस एक शब्द बोलना पड़ता है और राक्षस उस व्यक्ति से निकल जाते हैं। वह व्यक्ति इस तरह हिलता है जैसे कि उसे कोई दौरा पड़ रहा हो और उससे राक्षस बाहर आ जाता है। मार्क इस थीम को अपनी किताब में दिखाना पसंद करते हैं\\—और वह अक्सर हमें यीशु (अच्छाई) और शैतान (बुराई) के बीच के मुकाबले को दिखाते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अनुवाद टीम के साथ चर्चा करें: आपकी संस्कृति में बुरी आत्माओं से ग्रस्त लोगों के साथ कैसे निपटा जाता है? शैतान के वश में मौजूद लोग किस तरह की चीज़ें करते हैं? वे इस मुसीबत से कैसे ठीक हो जाते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क हमेशा हमें चमत्कार के परिणाम दिखाते हैं। इस मामले में, हमेशा की तरह यीशु और उनके चमत्कार के बारे में खबर पूरे इलाके में फैल जाती है।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का मतलब बताना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:21-28"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, आप इस कहानी की कल्पना करने में ग्रुप की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग और पात्रों को परिभाषित करने में ग्रुप की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में तीन दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" यीशु और उनके चार साथी अब केपरनॉम शहर में हैं। ये चार साथी वे मछुआरे हैं जिन्हें यीशु ने उनका अनुसरण करने के लिए बुलाया है। यह सबा का दिन है, इसलिए यीशु यहूदी प्रार्थनाघर में जाते हैं और उपदेश देते हैं। लोग उनकी शिक्षा से चकित हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" एक व्यक्ति जो एक बुरी आत्मा से ग्रस्त है, अचानक प्रकट हो जाता है। यीशु बुरी आत्मा को फटकार देते हैं और आज्ञा देते हैं कि वह व्यक्ति को छोड़ दे। इस पर, बुरी आत्मा चिल्लाती है, व्यक्ति को मरोड़ती है और बाहर निकल आती है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"तीसरा दृश्य:"},{"type":"text","text":" दर्शक चकित हैं। लोग इस कारनामे और यीशु के अधिकार पर चर्चा करना शुरू कर देते हैं। यीशु के बारे में खबर गैलिली के पूरे इलाके में जल्दी से फैल जाती है।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्र हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के चार साथी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आराधनालय के दर्शकों सहित:"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"स्थानीय लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आराधनालय के बाहर के लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक बुरी आत्मा से ग्रस्त व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"बुरी आत्मा"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"ग्रुप को इन दृश्यों के संदर्भ के बारे में सोचने के लिए कहें। हम अब एक अलग संदर्भ में हैं - केपरनॉम शहर में सबा के दिन एक आराधनालय के अंदर। यीशु बैठकर उपदेश दे रहे होंगे। फिर बुरी आत्मा से ग्रस्त व्यक्ति अचानक आता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"अगले दृश्य में, दर्शक मौजूद हैं - वे सभी लोग जो उस समय आराधनालय में थे। वे आराधनालय के बाहर के लोगों के साथ, क्या हुआ, यह चर्चा करने लगे और यह खबर गैलिलि के पूरे इलाके (जो गैलिली शहर के आसपास का क्षेत्र है) में फैल गई।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"टेक्स्ट को एम्बेड करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:21-28"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में तीन दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्र हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के चार साथी (जिन्हें उन्होंने अभी बुलाया है)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आराधनालय में मौजूद लोग, जिनमें शामिल हैं:"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"स्थानीय लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक बुरी आत्मा से ग्रस्त व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आराधनालय के बाहर मौजूद लोग (आराधनालय के भीतर मौजूद लोगों ने पूरे इलाके के लोगों के बीच खबर फैलाई)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"बुरी आत्मा"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने को कहें। जिस भाषा में वे अनुवाद कर रहे हैं, उसी भाषा में उन्हें अभिनय करना चाहिए। सबसे पहले, टीम को बिना रूके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी के अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब टीम कहानी पर पहली बार अभिनय करती है, तब, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ जगहों पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु भीड़ को शिक्षा दे रहे होते हैं, तो वे उस अधिकार से बहुत प्रभावित होते हैं, जिसके साथ यीशु उपदेश देते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें"},{"type":"text","text":"। यीशु के चार साथी की भूमिका निभाने वाले अभिनेताओं से पूछें कि वे क्या सोच रहे हैं। ये वही मछुआरे हैं जिन्हें यीशु ने अभी बुलाया है। आप ऐसी बातें सुन सकते हैं, \"उलझन,\" \"उनका विश्वास बढ़ रहा है,\" या \"वे सीख रहे हैं और समझ रहे हैं कि यीशु कौन हैं।\" अभिनय फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक बुरी आत्मा से ग्रस्त व्यक्ति आता है। कहानी का अभिनय इस तरह करें कि उसका आगमन अचानक और आश्चर्यजनक हो। यीशु के बुरी आत्मा को व्यक्ति को छोड़ने के लिए कहने के बाद, "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें"},{"type":"text","text":"। यीशु की भूमिका निभा रहे अभिनेता से पूछें कि उन्हें इस समय क्या महसूस हो रहा है। आप ऐसी बातें सुन सकते हैं, \"मैं शैतान की ओर से अपनी मिनिस्ट्री का विरोध महसूस कर रहा हूँ,\" या \"मुझे विश्वास और अधिकार महसूस हो रहा है कि मैं इस राक्षस को बाहर निकाल सकता हूँ।\""}]},{"type":"paragraph","content":[{"type":"text","text":"बुरी आत्मा से ग्रस्त व्यक्ति की भूमिका निभाने वाले अभिनेता से पूछें कि वह कैसे महसूस करते हैं। आप ऐसी बातें सुन सकते हैं, जैसे \"स्वतंत्रता,\" \"सुकून,\" या \"खुशी।\""}]},{"type":"paragraph","content":[{"type":"text","text":"कहानी के बाकी हिस्सों पर अभिनय करें। लोगों की भीड़ की भूमिका निभाने वालों से पूछें, \"आप अभी क्या सोच रहे हैं? शिक्षा में आपने कभी ऐसा अधिकार नहीं सुना है और अब आपने राक्षसों को बाहर निकालने में यीशु के अधिकार को देखा है। आप कैसा महसूस कर रहे हैं? क्या आप इसे समझ पा रहे हैं?\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"गैप को भरते हुए"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:21-28"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"केपरनॉम"}]},{"type":"paragraph","content":[{"type":"text","text":"केपरनॉम मिस्र से दमास्कस के रास्ते पर मौजूद एक प्रमुख शहर था, दमास्कस भी वहाँ का एक प्रमुख शहर था। यह मछली पकड़ने और व्यापार करने का केंद्र था। यह एक कर संग्रह स्टेशन के रूप में रोमन सरकार पर विजय प्राप्त करने वाली विदेशी रोमन सरकार के लिए भी महत्वपूर्ण था। यीशु ने इस शहर में अपनी मिनिस्ट्री का ज़्यादातर हिस्सा रखा। उनके कई शिष्य इस शहर के मछुआरे थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम को गैलिलि सागर के साथ साथ केपरनॉम, मिस्र और दमास्कस का भी नक्शा दिखाएं।"}]},{"type":"paragraph","content":[{"type":"text","text":"सबा"}]},{"type":"paragraph","content":[{"type":"text","text":"यहूदी लोग सबा के दिन को विश्राम और पूजा के दिन के रूप में तब से देखते हैं जब से परमेश्वर ने उन्हें ऐसा करने का आदेश दिया था। इसे सप्ताह का 7वां दिन माना जाता था। यीशु के समय तक, इस दिन किस तरह का काम किया जा सकता था और क्या नहीं, इस बारे में कड़े, विशिष्ट नियम बना दिए गए थे। यह वह दिन भी था जब लोग पूजा करने के लिए आराधनालय में जाते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय यहूदियों की पूजा की जगह थी, जहाँ जेरुसलम के मुख्य मंदिर के बजाय लोग गाँवों में मिल सकते थे। लोग आराधनालयों में बलिदान नहीं करते थे। ये यहूदी की स्थानीय सभाएँ थीं जो सबा के दिन (विश्राम का धार्मिक दिन) और विशेष पर्व के दिन प्रार्थना करने, धर्मशास्त्र को पढ़ने के लिए, शिक्षा लेने के लिए इकट्ठा होती थीं और कभी-कभी सामुदायिक समस्याओं या विवादों पर न्याय करती थीं।"}]},{"type":"paragraph","content":[{"type":"text","text":"शिक्षण टूल: इसे दिखलाने का एक तरीका है कि अलग-अलग आकार के कई पत्थर इकट्ठा किए जाएं, लेकिन एक पत्थर औरों से काफ़ी बड़ा होना चाहिए। छोटे पत्थरों की तुलना में बड़े पत्थर को थोड़ा ऊँचे स्थान पर रखें और लोग मंदिर में क्या करते थे बनाम आराधनालय में जो करते थे, उस पर चर्चा करने के दौरान इस शब्द चित्र का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"लोग "},{"type":"text","marks":[{"type":"bold"}],"text":"उनकी शिक्षा पर चकित थे\\—"},{"type":"text","text":" दूसरे शब्दों में, वे "},{"type":"text","marks":[{"type":"italic"}],"text":"उस तरीके"},{"type":"text","text":" पर चकित थे जिस तरीके से वह शिक्षा देते थे क्योंकि वह एक अधिकार से सिखाते थे।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"अधिकार"},{"type":"text","text":" का अर्थ है शक्ति होना, या लोगों को कुछ करने के लिए कहने का अधिकार रखना।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"कानून के शिक्षकों को"},{"type":"text","text":" कभी-कभी इस तरह से अनुवाद किया जाता है, "},{"type":"text","marks":[{"type":"bold"}],"text":"लेखक या कानून को लिखने वाले"},{"type":"text","text":": वे पवित्र शास्त्रों से ली गई कानून की व्याख्याओं के विद्वान और विशेषज्ञ थे। इन व्याख्याओं को लिखा नहीं गया था, लेकिन याद किया गया था और शिक्षकों द्वारा मौखिक रूप से सिखाया गया था।"}]},{"type":"paragraph","content":[{"type":"text","text":"बुरी और अशुद्ध आत्मा"}]},{"type":"paragraph","content":[{"type":"text","text":"ग्रीक में इस टेक्स्ट में इसे सही मायने में "},{"type":"text","marks":[{"type":"italic"}],"text":"एक अशुद्ध आत्मा कहा जाता है। अशुद्ध"},{"type":"text","text":" का तात्पर्य किसी ऐसी चीज़ से है जो परमेश्वर की सेवा के योग्य नहीं है। सभी बुरी आत्माएँ "},{"type":"text","marks":[{"type":"italic"}],"text":"अशुद्ध"},{"type":"text","text":" हैं क्योंकि वे ईश्वर की सेवा के योग्य नहीं हैं*।* बुरी "},{"type":"text","marks":[{"type":"italic"}],"text":"आत्माएँ"},{"type":"text","text":" उन आध्यात्मिक प्राणियों को संदर्भित करती है जो शैतान के अधिकार में रहते हुए उसके एजेंटों के रूप में उसकी सेवा करती हैं। इन अलौकिक प्राणियों में एक इंसान पर अत्याचार करने की ताकत है और उस पर काबू भी पा सकते हैं। यही वह बात है, जो हम इस कहानी में इस व्यक्ति के साथ घटते देख रहें हैं। हम बाइबल से जानते हैं कि बुरी आत्माओं को परमेश्वर ने बनाया था और उन्होंने उनकी आज्ञा न मानने का चुनाव किया। कुछ भाषाओं में ऐसा शब्द नहीं है, जो उसके बारे में ठीक से बतला सके। (उनकी "},{"type":"text","marks":[{"type":"italic"}],"text":"बुरी आत्मा"},{"type":"text","text":", एक मृत व्यक्ति की आत्मा को संदर्भित कर सकती है, जो लोगों को बार बार ''डराती'' हों।) एक ऐसा शब्द चुनने की कोशिश करें जो किसी ऐसी स्वतंत्र आत्मा को संदर्भित करता है, जो पापी है और ईश्वर विरोधी है। अगर ऐसा कोई शब्द हो, तो आप स्वतंत्र आत्मा के लिए तटस्थ शब्द का भी इस्तेमाल कर सकते हैं और "},{"type":"text","marks":[{"type":"italic"}],"text":"शैतान से आत्मा"},{"type":"text","text":" या "},{"type":"text","marks":[{"type":"italic"}],"text":"बदमाश/बुरी आत्मा"},{"type":"text","text":" जैसे विशेषण जोड़ सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपने अनुवादकों के साथ "},{"type":"text","marks":[{"type":"italic"}],"text":"अशुद्ध"},{"type":"text","text":" या "},{"type":"text","marks":[{"type":"italic"}],"text":"बुरी आत्मा"},{"type":"text","text":" के लिए सबसे अच्छे शब्द के बारे में चर्चा करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह वह बुरी आत्मा है जो तब बात कर रही है, जब वह पहचान जाती है कि यीशु कौन हैं। सुनिश्चित करें कि आपका अनुवाद दर्शाता है कि यह बुरी आत्मा बोल रही है।"}]},{"type":"paragraph","content":[{"type":"text","text":"नाज़ारेथ के यीशु"}]},{"type":"paragraph","content":[{"type":"text","text":"यह वह शहर है जहाँ यीशु पले-बढ़े\\—यह गैलिली के इलाके का एक अलग हिस्सा है। बुरी आत्मा ने यीशु के लिए एक नाम का इस्तेमाल किया, जिससे पता चला कि उसे पता था कि वह कौन हैं और वह कहाँ से आए हैं। बुरी आत्मा ने यीशु के नाम का इस्तेमाल करके कुछ नियंत्रण या शक्ति हासिल करने की उम्मीद की।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम को गैलिलि का एक नक्शा दिखाएँ, जो नाज़ारेथ को दिखाता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर के पवित्र पुत्र"}]},{"type":"paragraph","content":[{"type":"text","text":"यह शीर्षक आमतौर पर परमेश्वर को ही संदर्भित करता है, और इसका अर्थ है कि यीशु का परमेश्वर के साथ एक विशेष संबंध है और उनमें परमेश्वर की आत्मा है।"}]},{"type":"paragraph","content":[{"type":"text","text":"गैलिलि"}]},{"type":"paragraph","content":[{"type":"text","text":"केपरनॉम गैलिलि सागर के किनारे और गैलिली के क्षेत्र में था। यह झील लगभग 21 किलोमीटर लंबी और 10 किलोमीटर चौड़ी है। इसकी सर्वाधिक गहराई 67 मीटर है। कई लोग इसे मछली पकड़ने और पैसा कमाने के लिए इस्तेमाल करते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"अगर आपने पहले अनुवादकों को झील का नक्शा और तस्वीरें नहीं दिखाई हैं, तो अब दिखाएँ। आप इस जलाशय के लिए क्या शब्द इस्तेमाल करेंगे, इस बात पर चर्चा करें*।*"}]}]}}]', 37616, 1);
SELECT @TextResourceContentId = @@IDENTITY;
END
