SELECT @TextResourceContentId = NULL;
SELECT @TextResourceContentId = Id FROM ResourceContents WHERE [ResourceId] = @ResourceId AND [LanguageId] = @LanguageId AND [MediaType] = 1;
IF @TextResourceContentId IS NULL
BEGIN
INSERT INTO ResourceContents([ResourceId], [LanguageId], [DisplayName], [Version], [Completed], [Trusted], [Content], [ContentSize], [MediaType]) VALUES (@ResourceId, @LanguageId, N'मरकुस 1:29-34', 1, 1, 1, N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:29-34 को सुनें और इसे अपने दिल में बसा लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप किसी ऐसे को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:29-34"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय से निकलने के तुरंत बाद वे साइमन के घर गए थे\\—वह शायद आराधनालय से पास था। यीशु का नाम इस कहानी में कभी नहीं लिया जाता है, लेकिन हमें पता है कि वह चारों शिष्यों के साथ साइमन और एंड्र्यू के घर चले गए थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"हम नहीं जानते कि साइमन की सास को किस तरह का बुखार था या उन्हें बुखार कबसे था। हमें बस इतना पता था कि वह साइमन की पत्नी की माँ थीं, जो उनके साथ उस घर में रहती थीं और बिस्तर में बीमार पड़ी थीं। लेकिन यीशु ने उन्हें ठीक कर दिया\\—वह तुरंत उठ गईं, उन्हें कोई थकान नहीं महसूस हो रही थी और उन्होंने सबका सत्कार किया। उन्होंने शायद उनके लिए शाम का भोजन भी पकाया।"}]},{"type":"paragraph","content":[{"type":"text","text":"लोग सूर्यास्त के बाद बीमार लोगों को लाने लगे। विश्राम के दिन, सबा के दौरान लोगों को चटाई पर लोगों को ले जाने जैसे काम करने की अनुमति नहीं थी। हालांकि, सबा सूर्यास्त पर खत्म हो गया, इसलिए लोग बीमार लोगों को यीशु के पास ला सकते थे। उन्होंने पहले आराधनालय में यीशु की शिक्षा और चिकित्सा के बारे में सुना था और उन्हें उम्मीद थी कि वह उन्हें भी ठीक कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"\"पूरा शहर\" दरवाज़े पर जमा था, यह एक अतिश्योक्ति हो सकती है\\—या दूसरे शब्दों में कहें, तो ऐसा "},{"type":"text","marks":[{"type":"italic"}],"text":"लग रहा था"},{"type":"text","text":" कि पूरे शहर के सभी लोग वहाँ आ गए थे। अलग-अलग लोगों को अलग-अलग बीमारियाँ थीं, लेकिन बीमार लोगों को उन लोगों से अलग बताया जाता है जिन पर शैतान का साया था।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम के साथ चर्चा करें: आपकी संस्कृति में, जो लोग शारीरिक रूप से बीमार हैं, वे बुरी आत्माओं से ग्रस्त लोगों से कैसे अलग हैं? उनके व्यवहार में क्या अंतर हैं और लोग उनके साथ कैसा व्यवहार करते हैं? इन टेक्स्ट में, शारीरिक रूप से बीमार लोगों और बुरी आत्माओं से ग्रस्त लोगों के बीच स्पष्ट अंतर है।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु ने राक्षसों को बाहर निकाला, तो उन्होंने उन्हें यह कहने से मना किया कि वह कौन हैं। यीशु लोगों को यह नहीं बताना चाहते थे कि वह परमेश्वर के बेटे हैं, लेकिन उन्होंने जिस राक्षस को पहले निकाला था, उसने यीशु को \"परमेश्वर का पवित्र पुत्र\" कहा था। यीशु राक्षसों को चुप रखना चाहते थे, ताकि लोग यह महसूस नहीं कर पाएँ कि वह कौन हैं और उन्हें वे चीज़ें करने को न कहने लगें, जो वह करने नहीं आए थे। लोग केवल उन चमत्कारों के बारे में सोच रहे थे जो वह उनके लिए कर सकते हैं, न कि वह कौन थे। उनके आने का मुख्य कारण यह नहीं था।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का मतलब बताना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:29-34"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में आप इस कहानी का दृश्य बनाने में समूह की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग्स, व कहानी के चरित्रों को उतारने में समूह की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" यीशु साइमन और एंड्रयू के साथ उनके घर जाते हैं और साइमन की सास को ठीक कर देते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" लोगों की भीड़ यीशु से इलाज कराने के लिए अपने बीमार लोगों को लाना शुरू कर देती है।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"साइमन और एंड्रयू"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"साइमन की सास"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य वाले सभी लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"कई बीमार और आत्माओं से ग्रसित लोग (जो सूर्यास्त के बाद यीशु के पास आते हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पूरा शहर (जो यीशु को देखने के लिए इकट्ठा हुआ)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"दानव (जिसे यीशु ने बोलने नहीं दिया)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दिखाएँ कि जब यीशु लोगों को घर के अंदर ठीक कर रहे थे और साइमन की सास उठ खड़ी हुईं और खाना बनाने लगीं, तो क्या हुआ।"}]},{"type":"paragraph","content":[{"type":"text","text":"इसके बाद, दूसरे दृश्य में पहुँचने वाले लोगों की भीड़ को दिखाएँ।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"टेक्स्ट को एम्बेड करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:29-34"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"साइमन और एंड्रयू"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"साइमन की सास"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य में सभी लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"कई बीमार और राक्षस ग्रसित लोग (जो सूर्यास्त के बाद यीशु के पास आते हैं)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पूरा शहर (जो यीशु को देखने के लिए इकट्ठा हुआ)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"राक्षस (जिसे यीशु ने बोलने नहीं दिया)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने को कहें। जिस भाषा में वे अनुवाद कर रहे हैं, उसी भाषा में उन्हें अभिनय करना चाहिए। सबसे पहले, टीम को बिना रूके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी के अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब टीम कहानी पर पहली बार अभिनय करती है, तब, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ जगहों पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु साइमन और एंड्रयू के साथ उनके घर जाते हैं। यीशु साइमन की बीमार सास का हाथ पकड़कर उन्हें बैठाते हैं और वह ठीक हो जाती हैं। तब साइमन की सास उनके लिए भोजन तैयार करती हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें"},{"type":"text","text":"। साइमन की सास का किरदार निभाने वाली अभिनेत्री से पूछें कि वह इस समय कैसा महसूस कर रही हैं। आप ऐसी बातें सुन सकते हैं कि \"कृतज्ञ,\" \"धन्य,\" या \"स्वस्थ और सेवा करने के लिए तैयार।\" अभिनय फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य में, बीमार और राक्षस से पीड़ित कई लोग आ जाते हैं। यीशु उनमें से कई लोगों को ठीक कर देते हैं और कई राक्षसों को निकाल देते हैं। राक्षस जानते हैं कि यीशु कौन हैं, लेकिन यीशु उन्हें बोलने नहीं देते हैं। समूह के कहानी का अभिनय करने के बाद, उनसे पूछें कि वे इस बारे में क्या सोचते हैं। राक्षस का अभिनय करने वाले अभिनेताओं से पूछें, \"क्या आप यीशु को जानते हैं?\" आप ऐसी बातें सुन सकते हैं कि \"परमेश्वर का बेटा,\" \"मसीह,\" \"सभी अधिकार रखने वाला,\" या \"परमेश्वर।\""}]},{"type":"paragraph","content":[{"type":"text","text":"अब यीशु का अभिनय करने वाले अभिनेता से पूछें, \"आप उन्हें बोलने क्यों नहीं दे रहे थे?\" आप ऐसी बातें सुन सकते हैं कि \"वह मेरी पहचान उजागर होने का सही समय नहीं था,\" \"मैं नहीं चाहता था कि राक्षस यह दावा कर सकें कि मैं कौन हूँ,\" या \"मैं खुद अपनी पहचान बताना चाहता था।\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"खाली जगह को भरना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:29-34"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"सीधा"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु आराधनालय से निकलने के ठीक बाद साइमन और एंड्र्यू के घर गए। साइमन और एंड्रयू का घर शायद आराधनालय के पास था।"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय पूजा के लिए यहूदियों के स्थान थे, जहां यरूशलेम के मुख्य मंदिर के बजाय गांवों में लोग मिल सकते थे। लोग आराधनालयों में बलिदान नहीं करते थे। ये यहूदी लोगों की स्थानीय सभाएँ थीं जो सबा के दिन (विश्राम का धार्मिक दिन) और विशेष पर्व के दिन प्रार्थना करने, धर्मशास्त्र को पढ़ने के लिए, शिक्षा लेने के लिए इकट्ठा होती थीं और कभी-कभी सामुदायिक समस्याओं या विवादों पर न्याय करती थीं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पत्नी की माँ"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु से ठीक होने वाली महिला साइमन की पत्नी की माँ थीं। इस रिश्ते के लिए अपनी भाषा में सबसे स्वाभाविक वाक्यांश का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"बुखार से बीमार"}]},{"type":"paragraph","content":[{"type":"text","text":"उन दिनों में लोगों को पता नहीं होता था कि कौनसी बीमारी है। बुखार से पीड़ित होने का मतलब किसी भी तरह की बीमारी हो सकती थी। यह कहानी में स्पष्ट नहीं है कि यह किस तरह का बुखार था या उसे कितने समय से बुखार था।"}]},{"type":"paragraph","content":[{"type":"text","text":"उन्होंने यीशु को \"उनके बारे में बताया\" इसका मतलब है कि उन्होंने उन्हें बताया कि वह बीमार हैं"}]},{"type":"paragraph","content":[{"type":"text","text":"\"उनके बारे में बताया\" स्पष्ट नहीं है, लेकिन उन्होंने उन्हें बताया होगा कि वह बीमार हैं, क्योंकि उसके बाद ही यीशु उन्हें ठीक करने के लिए गए थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु "},{"type":"text","marks":[{"type":"bold"}],"text":"ने उनकी मदद की और उन्हें उठाया,"},{"type":"text","text":" तो उन्होंने उनका हाथ पकड़ लिया, ताकि उन्हें बिस्तर से उठाकर बिठाया जा सके।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"उन्होंने उनकी सेवा की"},{"type":"text","text":" या "},{"type":"text","marks":[{"type":"bold"}],"text":"उन्हें खाना खिलाया"},{"type":"text","text":" वाक्यांश से इसका विवरण नहीं मिलता है कि उन्होंने क्या किया; हालाँकि, उन्होंने शायद उनके लिए शाम का भोजन बना दिया क्योंकि यह उनके लिए सामान्य काम होता था।"}]},{"type":"paragraph","content":[{"type":"text","text":"ग्रस्त"}]},{"type":"paragraph","content":[{"type":"text","text":"दानव लोगों को वश में करना चुनते हैं क्योंकि वे लोगों और समुदायों में उनके संबंधों को नष्ट करना चाहते हैं। जब राक्षस लोगों को वश में करते हैं, तो वे लोगों के कार्यों पर नियंत्रण करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम के साथ चर्चा करें: आपकी संस्कृति में राक्षस से ग्रस्त लोग क्या करते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"राक्षस"}]},{"type":"paragraph","content":[{"type":"text","text":"इस शब्द का इस्तेमाल "},{"type":"text","marks":[{"type":"bold"}],"text":"बुरी या अशुद्ध आत्माओं"},{"type":"text","text":" की जगह किया जाता है।"}]}]}}]', 25561, 1);
SELECT @TextResourceContentId = @@IDENTITY;
END
