UPDATE ResourceContents SET [DisplayName] = N'मरकुस 1:35-39', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:35-39 को सुनें और इसे अपने दिल में बसा लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप किसी ऐसे को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:35-39"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह कहानी यीशु के द्वारा उस रात कॅपरनॉम में साइमन की सास और कई अन्य लोगों के ठीक हो जाने के ठीक बाद घटती है। अगली सुबह, बहुत ही जल्दी, यीशु प्रार्थना करने के लिए उठ जाते हैं। मार्क में दूसरे दो जगहों पर यीशु चमत्कार करते हैं और उपदेश देते हैं, और फिर रात में एकांत मेंं प्रार्थना करने और अकेले रहने के लिए लोगों से दूर एक जगह पर चले जाते हैं। हालांकि, लोग यीशु को अकेले नहीं छोड़ना चाहते हैं\\—वे अधिक उपचार के अनुभव के लिए उनके पीछे चलते जाते हैं। इस कारण के लिए यीशु पृथ्वी पर नहीं आए थे\\—इस कहानी में वे अपने शिष्यों से वे कहते हैं कि वे उपदेश देने के लिए आए हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह हमें उस समय की याद दिलाता है, जब यीशु शैतान के द्वारा प्रलोभन दिए जाने पर निर्जन में चिल्ला उठे थे। एक तरीके से शैतान ने यीशु को प्रलोभन दिया कि वह यीशु को दुनिया भर का राजा बनाने का जतन करेगा। शायद यीशु को प्रार्थना करने के लिए निर्जन में जाने की जरूरत है और अपने पिता से ये याद दिलाए जाने के लिए कि वे बस एक प्रसिद्ध राजा बनने के लिए नहीं आए हैं, बल्कि उनके लिए परमेश्वर की योजना को पूरा करने के लिए आए हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहां रुकें और अपनी अनुवाद टीम के साथ चर्चा करें: आपकी संस्कृति में लोग कैसे प्रार्थना करते हैं? लोग कब प्रार्थना करते हैं? लोग आमतौर पर क्या प्रार्थना करते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु \"निर्जन\" में जाते हैं\\— और जहाँ जॉन रहते थे और जहां यीशु को प्रलोभन दिया गया था, समान शब्द का उपयोग किया गया है। हालांकि, कॅपरनॉम के आसपास का क्षेत्र अत्यधिक आबादी वाला था\\—कोई निर्जन स्थान नहीं था जैसा कि वे शैतान के द्वारा प्रलोभन दिए जाने के लिए गए थे। मार्क हमें याद दिलाते हैं कि कई बार परमेश्वर के लोग परमेश्वर को पा लेते हैं और बड़े शहरों से कहीं दूर उनसे संवाद करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"साइमन और अन्य शिष्य (शायद एंड्रयू, जेम्स और जॉन) यीशु की तलाश के लिए जाते हैं \\—''की तलाश के लिए'' के लिए इस्तेमाल किया जाने वाला कार्रवाई का शब्द हमें दिखलाता है कि वे यीशु के कार्यों से खुश नहीं हैं। वे गलत समझे हैं कि यीशु क्यों आए थे\\—वे मानते हैं कि यीशु एक प्रसिद्ध उपचारकर्ता और नेता बनना चाहते हैं। यीशु उपदेश देने के लिए आसपास के गांवों में जाना चाहते हैं। यीशु कहते हैं कि वे इसलिए पृथ्वी पर आए थे\\—हालांकि इस कहानी में यह स्पष्ट नहीं है, इसी कहानी का ल्यूक का वर्णन हमें बताता है कि यीशु"}]},{"type":"paragraph","content":[{"type":"text","text":"लोगों को यह जानकारी देने के लिए आये थे कि उन्हें परमेश्वर के राज्य का हिस्सा बनने के बारे में फैसला करना चाहिए\\—न कि प्रसिद्ध बनने के लिए।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने गैलिली के इलाके तक भ्रमण किया, जो घनी आबादी वाले मध्यम आकार के शहरों के साथ गैलिली सागर के पश्चिमी किनारे पर स्थित था, जहां लोग मछली पालन और इस क्षेत्र की उपजाऊ मिट्टी में खेती करते थे। जब वे यात्रा करते हैं, तो वह गांव के लोगों से मिलने के लिए लोगों के आराधनालय का इस्तेमाल करते हैं। उन्होंने उपदेश दिया और दुष्टात्माओं को लोगों को छोड़ने का आदेश दिया। हालांकि उन्होंने शायद शारीरिक रूप से भी लोगों को ठीक कर दिया, दुष्टात्माओं को बाहर करना बेहतर तरीके से दिखाता है कि उन्होंने परमेश्वर के साथ परिपूर्णता के लिए लोगों को हर तरह से वापस ला दिया।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी टीम को गैलिली क्षेत्र का एक नक्शा दिखाएं, जिसमें कॅपरनॉम शामिल है।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में, यीशु ने फिर से यह स्थापित कर दिया है कि वही हैं जो अपने कार्यों को नियंत्रित करने वाले हैं\\—न कि उनके आसपास की भीड़ या उनके शिष्य..."}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों को निर्धारित करते हुए"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:35-39"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में आप इस कहानी का दृश्य बनाने में समूह की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग्स, व कहानी के चरित्रों को उतारने में समूह की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में एक दृश्य है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दृश्य:"},{"type":"text","text":" यह दृश्य एक अलग-थलग जगह पर घटित होता है। जब यीशु प्रार्थना कर रहे हैं, उनके नए साथी आते हैं और उन्हें खोज लेते हैं। यीशु उनसे कहते हैं, \"हमें अन्य शहरों में जाना चाहिए और उन्हें भी उपदेश देना चाहिए।\" तब वे गैलीली के पूरे इलाके में भ्रमण करते हैं, आराधनालयों में उपदेश देते हैं और दुष्टात्माओं को भगा देते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में पात्रों में ये शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के साथी (साइमन और अन्य भी जिन्हें यीशु ने बुलाया है)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह दृश्य एक अलग-थलग जगह से शुरू होता है और फिर पूरे इलाके में हर जगह घटता है। आप टीम को पूरे इलाके में यीशु और उनके साथियों के प्रत्याशित गमन के मार्ग पर खास ध्यान देते हुए गैलिली का नक्शा दिखा सकते हैं।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"पाठ का बोध"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:35-39"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में एक दृश्य है।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्रों में ये शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के साथी (साइमन और अन्य भी जिन्हें यीशु ने बुलाया है)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"क्या टीम ने कहानी पर दो बार अभिनय में उतार दिया है। उन्हें उस भाषा में इसे उतारना चाहिए, जिसमें वे अनुवाद कर रहे हैं। पहला, टीम बिना रुके कहानी का अभिनय कर रही है। टीम कहानी को दूसरी बार कर दिखलाती है, कहानी को अलग अलग बिंदुओं पर थाम लेती है।"}]},{"type":"paragraph","content":[{"type":"text","text":"जैसे जैसे कि टीम कहानी को पहली बार पेश करती है, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या गद्यांश के अहम या मुश्किल हिस्सों पर नज़र रखें जिन्हें करना है और छूट न जाएं। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करती है, तो उन्हें अभिनय के अंत में रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु के साथी की भूमिका निभा रहे अभिनेताओं से पूछें, उन्हें क्या लगता है कि जब यीशु ने कहा था, \"हमें दूसरे शहरों में जाना चाहिए और उन्हें भी उपदेश देना चाहिए\\—इसीलिए मैं आया हूँ\", तो उनका क्या मतलब था। उनसे पूछें, कि उन्हें क्यों लगता है कि यीशु इस बिंदु पर आए थे।"}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"गैप को भरना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:35-39"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"एकांत जगह"},{"type":"text","text":" वही शब्द है जिसका इस्तेमाल उस जगह के लिए किया जाता है जहां जॉन रहा करते थे, और जहाँ यीशु जाँचे जाने के लिए गए थे। कॅपरनॉम के आस-पास कोई असली निर्जन स्थान नहीं है\\—यह ऐसा कहने का एक तरीका है कि वह एक ऐसी जगह खोजने के लिए गए थे जहां वह परमेश्वर से बात करने के लिए अकेले हो सकते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"प्रार्थना"}]},{"type":"paragraph","content":[{"type":"text","text":"यह शब्द एक लंबे समयकाल के लिए की गई प्रार्थना का अर्थ लाता है। अपने व्यापक अर्थ में, प्रार्थना परमेश्वर के साथ संवाद होती है। उस शब्द का इस्तेमाल न करने की कोशिश करें, जिसका मतलब केवल शब्दों के एक सूत्र को जपना भर हो।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"साथी -"},{"type":"text","text":" संभवतः एंड्रयू, जेम्स और जॉन"}]},{"type":"paragraph","content":[{"type":"text","text":"साइमन और उसके साथियों का लहजा आरोप लगाने वाला था जब उन्होंने कहा, \"हर कोई आपकी तलाश कर रहा है!\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"शहर/गांव:"},{"type":"text","text":" उन शहरों को संदर्भित करते हैं जो बड़े कृषि क्षेेत्र के गाँवों जैसे थे\\—छोटे आकार के शहर (कम से कम 15,000 लोग) लेकिन एक गांव की तरह लगते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"गैलिली"}]},{"type":"paragraph","content":[{"type":"text","text":"गैलिली सागर के पश्चिमी किनारे के आसपास क्षेत्र, घनी आबादी वाली मध्यम आकार के शहरों में थी, जहां लोग मछली पालन और क्षेत्र की उपजाऊ मिट्टी में खेती करते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय"}]},{"type":"paragraph","content":[{"type":"text","text":"आराधनालय के लिए अन्य पाठ्यांशों में जिस शब्द का इस्तेमाल किया है, उसी का इस्तेमाल करें। आराधनालय पूजा के लिए यहूदियों के स्थान थे, जहां यरूशलेम के मुख्य मंदिर के बजाय गांवों के लोग आपस में मिल सकते थे। आराधनालय में लोग बलिदान नहीं दिया करते थे। ये यहूदी के स्थानीय सभाएं थीं जो सैबेथ के दिन (विश्राम का धार्मिक दिन) और विशेष पर्व के दिन प्रार्थना करने, धर्मशास्त्र को पढ़ने के लिए, शिक्षा लेने के लिए इकट्ठा होते थे और कभी-कभी सामुदायिक समस्याओं या विवादों पर न्याय करते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"शिक्षण टूल: इसे दिखलाने का एक तरीका है कि अलग-अलग आकार के कई पत्थर इकट्ठा किए जाएं, लेकिन एक पत्थर औरों से काफ़ी बड़ा होना चाहिए। छोटे पत्थरों की तुलना में बड़े पत्थर को थोड़ा ऊंचे स्थान पर रखें और जिस तरह मंदिर में लोग जैसा किया करते थे बनाम आराधनालय में जो करते थे उस पर चर्चा करने के दौरान इस शब्द -चित्र का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"उनका आराधनालय"},{"type":"text","text":" गांवों में लोगों के आराधनालय के बारे में बतलाता है..."}]},{"type":"paragraph","content":[{"type":"text","text":"दुष्टात्माएँ"}]},{"type":"paragraph","content":[{"type":"text","text":"\"दुष्टात्माएँ\" शब्द का प्रयोग अशुद्ध या दुष्ट आत्मा के लिए अदल-बदल कर किया जाता है। हम बाइबिल से जानते हैं कि बुरी आत्माओं को परमेश्वर ने बनाया था और जो उनकी अवज्ञा करने लगे थे, लेकिन कुछ भाषाओं में ऐसा शब्द नहीं है जो इसे पर्याप्त रूप से व्यक्त कर सके। (उनकी "},{"type":"text","marks":[{"type":"italic"}],"text":"बुरी आत्मा"},{"type":"text","text":", एक मृत व्यक्ति की आत्मा को संदर्भित कर सकती है, जो लोगों को बार-बार डराती हों। एक ऐसा शब्द चुनने की कोशिश करें जो किसी ऐसी स्वतंत्र आत्मा को संदर्भित करता है, जो दुष्ट है और ईश्वर विरोधी है। अगर ऐसा हो, तो आप स्वतंत्र आत्मा के लिए किसी तटस्थ शब्द का इस्तेमाल कर सकते हैं और "},{"type":"text","marks":[{"type":"italic"}],"text":"शैतान से आई आत्मा"},{"type":"text","text":" या "},{"type":"text","marks":[{"type":"italic"}],"text":"बुरी/दुष्ट आत्मा"},{"type":"text","text":" जैसे विवेचन जोड़ सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपने अनुवादकों के साथ "},{"type":"text","marks":[{"type":"italic"}],"text":"दुष्टात्माएँ, अशुद्ध"},{"type":"text","text":" या "},{"type":"text","marks":[{"type":"italic"}],"text":"बुरी आत्मा"},{"type":"text","text":" के लिए सर्वश्रेष्ठ शब्द पर चर्चा करें।"}]}]}}]', [ContentSize] = 25847, Updated = GETUTCDATE() WHERE Id = @TextResourceContentId;

