UPDATE ResourceContents SET [DisplayName] = N'मरकुस 1:40-45', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:40-45 को सुनें और इसे अपने दिल में बसा लें। कथा को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम की तरह नीचे दिए गए सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कथा में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कथा हमें यीशु के बारे में क्या बताती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कथा हमें लोगों के बारे में क्या बताती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कथा हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप किसी ऐसे को जानते हैं, जिसे यह कथा सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"माहौल बनाते हैं"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:40-45"}]},{"type":"paragraph","content":[{"type":"text","text":"कथा को एक बार फिर सबसे आसानी से समझ आने वाले शब्दों में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कुष्ठ रोग से ग्रस्त लोगों से समाज बहुत डरता था और वे समाज उन्हें अछूत मानता था। कुष्ठ रोग का मतलब संभवतः त्वचा के कई तरह के रोगों से है। कुष्ठ रोग वाले लोगों को समुदाय में रहने और काम करने के लिए अपवित्र या अयोग्य माना जाता था। लैव्यव्यवस्था (लेविटिकस) मूसा ने कानून में कहा है कि कुष्ठ रोग से ग्रसित लोगों को फटे कपड़े पहनने चाहिए, बाल कंघी नहीं करने चाहिए और दिन भर \"अपवित्र, अपवित्र\" चिल्लाना चाहिए। कुष्ठरोगी समुदाय से परे अकेले रहते थे। कोई भी व्यक्ति जो किसी कुष्ठरोगी को छू लेता था, उसे भी अपवित्र माना जाता था, इसलिए लोग उनसे यथासंभव दूरी बनाए रखते थे। कुष्ठरोगी पूजा स्थल में तभी जा सकते थे, जब अगर उनके और बाकी मंडली के बीच में कोई पर्दा हो। किसी कुष्ठरोगी की बीमारी हो सकता है कि ठीक हो गई हो, लेकिन तब उसे फिर से \"पवित्र\" घोषित किए जाने और समुदाय में सामान्य रूप से रहने में सक्षम होने के लिए किसी पुजारी के पास जाने की जरूरत पड़ती थी।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम के साथ चर्चा करें: क्या आपकी संस्कृति में कोई ऐसी बीमारियाँ हैं, जो लोगों को समुदाय से अलग कर देती हैं, या वे ईश्वर की पूजा करने में असमर्थ हो जाते हैं? उन लोगों को क्या करना पड़ता है ताकि दिख सके कि वे अलग कर दिए गए हैं। दूसरे लोग उनसे कैसा बर्ताव करते हैं? वे समुदाय में फिर से कैसे वापिस आ पाते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कथा में जिस्मानी तौर पर ठीक करने का अनुरोध करने के लिए कुष्ठरोगी यीशु के पास जाता है। यीशु ने करुणा दिखाई या गहरे ज़ज्बात दिखाए - उन्होंने असल में उस व्यक्ति को छू लिया। व्यक्ति अपनी अछूत होने की स्थिति के कारण बाहर के लोगों के स्पर्श का आदी नहीं था। यीशु ने धार्मिक नियमों के टूटने की परवाह नहीं की, यीशु के मन में प्रेम का नियम धार्मिक नियमों से अधिक महत्व रखता था।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु ने करुणा दिखाई और जब यीशु ने व्यक्ति को एक कठोर चेतावनी दी, तब उनकी गहरी भावनाएँ थीं - उनमें उस बुराई पर क्रोध और कुंठा जैसा कुछ था, जिसने व्यक्ति को बीमार कर दिया था। यीशु शायद निराश भी हों, क्योंकि उन्हें पता था कि वह व्यक्ति चुप रहने के उनके आदेश की अवज्ञा करने वाला था।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने व्यक्ति को बहुत कड़ाई से निर्देश दिया कि वह समारोह में शामिल होने के लिए पुजारी के पास जाए, और उन बलिदानों को करे, जो उसे ''पवित्र'' बनाएँगे या फिर से समुदाय में रहने के योग्य बना देंगे। यीशु उस व्यक्ति से कहते हैं कि वह किसी पुजारी के पास जाएँ और नियमों को निभाएँ, ताकि यह लोगों के लिए एक प्रमाण या साक्ष्य रहे। शायद यह उस व्यक्ति के ठीक होने का सबूत था, या एक प्रमाण कि यीशु ने यहूदी धार्मिक कानूनों का पालन किया, या एक साक्ष्य कि यीशु कुष्ठरोगियों तक को भी ठीक करने आए थे। यह प्रकृति पर यीशु के अधिकार को धार्मिक नेताओं को दिखाने के लिए और भी प्रमाण होगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"दिक्कत यह है कि वह व्यक्ति सभी को यह बताने लगा कि यीशु ने क्या किया है। इससे और भी ज्यादा समस्याएँ पैदा हो गईं \\— हर बार यीशु जब किसी शहर जाते थे, तो लोगों की भीड़ यीशु द्वारा ठीक होने के लिए आ जाती थी। हमारी पिछली कथा में यीशु हमें बताते हैं कि वह लोगों को उपदेश देने के लिए और परमेश्वर के राज्य में आमंत्रित करने के लिए आए हैं, न कि जब लोगों को कोई दिक्कत हो, तो उन्हें बस ठीक करने के लिए।"}]},{"type":"paragraph","content":[{"type":"text","text":"भीड़ से यीशु का दूर होना यीशु की सेवकाई के इस हिस्से का अंत है। हम अब यीशु की सेवकाई से जुड़े विवादों के बारे में कहानियों की एक शृंखला की तरफ़ आते हैं।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का निर्धारण करें"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:40-45"}]},{"type":"paragraph","content":[{"type":"text","text":"कथा को एक बार फिर सबसे आसानी से समझ आने वाले शब्दों में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में आप इस कथा के दृश्य बनाने में समूह की मदद करेंगे। आप कथा के दृश्यों, माहौल और कथा का चरित्र गढ़ने में समूह की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कथा में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" एक कुष्ठरोगी आता है और यीशु के सामने शीष नवाता है, ठीक होने की याचना करता है। वह कहता है, \"यदि आप चाहें तो, आप मुझे ठीक कर सकते हैं और मुझे पवित्र कर सकते हैं।\""}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु करुणा से भर जाते हैं, और कुष्ठरोगी को छूते हैं। यीशु कहते हैं, \"मैं चाहता हूँ, ठीक हो जाओ।\" कुष्ठरोग तुरंत गायब हो जाता है, और वह व्यक्ति ठीक हो जाता है। यीशु उसे यह कहते हुए विदा करते हैं कि \"इस बारे में किसी को न बताना। पुजारी के पास जाओ, उन्हें अपनी जाँच करने दो, और मूसा के नियमों के अनुसार आवश्यक भेंट अपने साथ ले लेना। यह इस बात का सबूत होगा कि आप पवित्र कर दिए गए हैं।\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" वह व्यक्ति विदाई लेता है, लेकिन यीशु ने जो कहा उसे करने के बजाय वह सभी को बता देता है। नतीज़न, जल्द ही बड़ी भीड़ यीशु को घेर लेती है इस हद तक कि वे सार्वजनिक तौर पर शहरों में प्रवेश नहीं कर पाते और उन्हें एकांत स्थानों में ही रहना पड़ता है। लेकिन तब भी हर जगह से लोग उसके पास आते रहते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कथा के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"कुष्ठरोगी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के साथी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लोगों की भीड़"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनाएँ या इसमें कथा का दृश्य बनाने के लिए किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य में, यीशु यात्रा कर रहे होंगे। यह दृश्य शहर के बाहर घटा होगा, क्योंकि कुष्ठरोगी शहर के अंदर सड़क पर तो नहीं हो सकता था।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु के साथ शायद उनके साथी होते होंगे, जो शायद हैरान हो गए होंगे कि यीशु ने ऐसे संक्रामक रोग से ग्रसित किसी व्यक्ति को छुआ।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य में, वह व्यक्ति विदा लेगा और क्या हुआ, उसे इस बारे में सभी को बताते हुए दिखाएँ। साथ ही यीशु के पास आने वाले लोगों की भीड़ को भी दिखाएँ ।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"कथा को मूर्त रूप दें"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:40-45"}]},{"type":"paragraph","content":[{"type":"text","text":"कथा को एक बार फिर सबसे आसानी से समझ आने वाले शब्दों में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कथा को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कथा में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कथा के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के साथी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"कुष्ठ (एक अत्यधिक संक्रामक त्वचा रोग) से ग्रसित व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लोगों की भीड़"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"क्या टीम ने कथा पर दो बार अभिनय करके देख लिया है। उन्हें उस भाषा में अभिनय करना चाहिए, जिसमें वे अनुवाद कर रहे हैं। पहला, क्या टीम बिना रुके कथा का अभिनय कर रही है। टीम दूसरी बार कथा पर अभिनय करके दिखाती है, उन्हें कथा में अलग अलग जगह पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब टीम कथा को पहली बार पेश करती है, कथा के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कथा या गद्यांश के उन अहम या मुश्किल हिस्सों पर नज़र रखें जिन पर अभिनय करना है और वह छूट न जाएँ। कथा के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब टीम इस कथा को दूसरी बार पेश करती है, तो उन्हें कुछ जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब कुष्ठ रोगी आता है और यीशु के सामने उनसे ठीक करने की भीख माँगते हुए शीष नवा देता है, "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" कुष्ठरोगी की भूमिका निभा रहे अभिनेता से पूछें कि उन्हें कैसा महसूस हो रहा है। आप ऐसी बातें सुन सकते हैं कि \"मैं हताश हूँ,\" \"मैं अछूत हूँ\"\" या \"मैं यीशु को मुझे ठीक करने और समाज में वापसी करने के अपने अवसर के रूप में देख रहा हूँ।\" अब यीशु के साथियों से पूछें कि वे कैसा महसूस कर रहे हैं। आप ऐसी बातें सुन सकते हैं कि \"हम इस संक्रामक रोग वाले किसी व्यक्ति के हमारे पास होने में थोड़ा संकोच कर रहे हैं।\" अभिनय को फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु करुणा से भर जाते हैं, उसके पास जाते हैं और उस व्यक्ति को छू लेते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय को बंद करें।"},{"type":"text","text":" यीशु की भूमिका निभाने वाले अभिनेता से पूछें कि उन्हें कैसा महसूस हो रहा है। आप ऐसी बातें सुन सकते हैं कि \"करुणा,\"प्रेम,\" या \"इस व्यक्ति को ठीक करने की इच्छा\" यीशु के साथियों की भूमिका निभाने वाले अभिनेताओं से पूछें, कि वे कैसा महसूस कर रहे हैं। आप ऐसी बातें सुन सकते हैं कि \"हैरान थे कि इस रोग से ग्रस्त इस व्यक्ति को यीशु ने छुआ ।\" व्यक्ति की भूमिका निभाने वाले अभिनेता से पूछें, कि वह कैसा महसूस कर रहे हैं। आप ऐसी बातें सुन सकते हैं कि \"यह पहली बार हुआ है जब मुझे बहुत लंबे समय तक छुआ गया, या\"मुझे यीशु के प्रेम और करुणा का अनुभव हो रहा है।\" अभिनय को फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कुष्ठ रोग तुरंत गायब हो जाता है और यीशु उस व्यक्ति से कहते हैं, \"इस बारे में किसी को नहीं बताना। पुजारी के पास जाएँ, उन्हें अपनी जाँच करने दें, और आवश्यक भेंट ले लें। यह सार्वजनिक सबूत होगा कि आप ठीक हो चुके हैं।\" लेकिन वह व्यक्ति जाकर सभी को यह बताते हुए कि क्या हुआ बात को फैला देता है। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय को बंद करें।"},{"type":"text","text":" ठीक हो चुके व्यक्ति की भूमिका निभाने वाले अभिनेता से पूछें, \"आपने जाकर सभी को क्यों बताया, कि क्या हुआ?\" आप ऐसी बातें सुन सकते हैं कि \"मैं उत्तेजित था और चाहता था कि सभी को यीशु के बारे में पता चले\" या \"मैं चाहता था कि लोग जानें कि मैं ठीक हो गया हूँ और मुझे पवित्र कर दिया गया है।\" अभिनय को फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"लोगों की भीड़ से घिरे बिना यीशु कहीं भी नहीं जा सकते हैं। उन्हें एकांत स्थानों में रहना पड़ता है, लेकिन तब भी हर जगह से लोग उनके पास आते रहते हैं। अभिनय के अंत में, समूह से पूछें, \"यीशु ने उस व्यक्ति से किसी को नहीं बताने के लिए क्यों कहा था?\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"कुछ आवश्यक जानकारी"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 1:40-45"}]},{"type":"paragraph","content":[{"type":"text","text":"कथा को एक बार फिर सबसे आसानी से समझ आने वाले शब्दों में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कुष्ठरोग"}]},{"type":"paragraph","content":[{"type":"text","text":"बाइबिल में इस शब्द का मतलब कई तरह के त्वचा रोगों में से किसी से भी हो सकता है। इन बीमारियों ने किसी व्यक्ति को ''अपवित्र'' बना दिया, या समुदाय में रहने लायक नहीं रहने दिया। पूजा स्थल में कुष्ठरोगी केवल किसी पर्दे के पीछे से ही पूजा कर सकते थे, और जो भी किसी कुष्ठरोगी को छू लेता था, वह भी अपवित्र हो जाता था। ओल्ड टेस्टामेंट का कानून कहता था कि कुष्ठरोगियों को फटे कपड़े पहनना है, अपने बालों में कंघी नहीं करनी है, और जहाँ जाएँ, उन स्थानों पर उन्हें जनता में ''अपवित्र'' चिल्लाना है।"}]},{"type":"paragraph","content":[{"type":"text","text":"पवित्र/अपवित्र"}]},{"type":"paragraph","content":[{"type":"text","text":"ऐसी स्थिति को संदर्भित करते हैं जिसमें कोई परमेश्वर की सेवा करने के योग्य है, या उनके कोई ऐसा लक्षण हैं जो उन्हें इस समय सेवा के लिए अयोग्य बनाता है। लोग अपनी कर्मों व स्वास्थ्य की स्थिति के आधार पर फिर से अपवित्र या पवित्र हो सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"करुणा"}]},{"type":"paragraph","content":[{"type":"text","text":"इस विषय में कई पांडुलिपियाँ दयालुता, दया या किसी को फ़ायदा करने को दिखाने के लिए शब्द का उपयोग करती हैं। हालाँकि कुछ पांडुलिपियों में ''क्रोध'' शब्द का प्रयोग किया जाता है, यह संभावना है कि यहाँ शब्द का अर्थ किसी तरह के गहरे भाव से है। यदि आपको किसी भावना को चुनना हो, तो यहाँ करुणा या दया के लिए आपका शब्द सबसे उचित होगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"पवित्र/ठीक किया गया/उपचार किया गया"}]},{"type":"paragraph","content":[{"type":"text","text":"जब व्यक्ति पवित्र होने के लिए कहता है और जब यीशु उसे ठीक कर देते हैं, तो यहाँ पर इसी शब्द का इस्तेमाल किया जाता है। धार्मिक कानून के अनुसार, किसी व्यक्ति के शारीरिक रोग उसे समुदाय में रहने लायक नहीं छोड़ते। इसका मतलब है कि जब यीशु शारीरिक रूप से व्यक्ति को ठीक कर देते हैं, तो वे उसे पुजारी के पास जाकर धार्मिक समारोहों को करने के बाद फिर से पवित्र होने की अनुमति दे रहे हैं। कुछ अनुवादों ने दोनों अवधारणाओं को एक साथ रख दिया गया और कहा गया \"यदि आप चाहें तो आप मुझे ठीक कर सकते हैं या मेरा उपचार कर सकते हैं और मुझे पवित्र कर सकते हैं।\""}]},{"type":"paragraph","content":[{"type":"text","text":"पुजारी"}]},{"type":"paragraph","content":[{"type":"text","text":"यहूदी धर्म में पुजारी वे लोग थे जो लोगों की ओर से परमेश्वर को बलिदान अर्पण कर उनको परमेश्वर से जोड़ते थे, लोगों को शिक्षित करते थे, भगवान के मंदिर की देखभाल करते थे। पुजारी परमेश्वर के लोगों केे लिए परमेश्वर के प्रतिनिधि थे और परमेश्वर के समक्ष उनके लोगों के प्रतिनिधि थे। पुजारी वह भी थे जो लोगों की जाँच कर निर्णय लेते थे कि क्या उन्हें फिर से पवित्र किया जा सकता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"बलिदान"}]},{"type":"paragraph","content":[{"type":"text","text":"कोई "},{"type":"text","marks":[{"type":"italic"}],"text":"बलिदान"},{"type":"text","text":" कुछ ऐसी चीज़ है, जो परमेश्वर को अर्पण की जाती है। यह कोई खून का बलिदान हो सकता है, जिसका अर्थ है किसी पशु का वध कर जला देना, या कोई बिना खून का बलिदान (जैसे कि अगरबत्ती या एक भेंट के रूप में दिया गया अनाज)। ओल्ड टेस्टामेंट पशु बलि में एक सामान्य रीति थी। यह दिखाने के लिए कि वह परमेश्वर के प्रति पूरी तरह से समर्पित था उपासक किसी पशु का वध कर उसे अग्नि को समर्पित कर देता था, और उसके हाथों पशु की हत्या से उसके पाप निपट जाएँगे या माफ़ हो जाएँगे या कोई फिर से पवित्र हो जाएगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"मूसा का कानून"}]},{"type":"paragraph","content":[{"type":"text","text":"ओल्ड टेस्टामेंट के ग्रंथ जिन्होंने परमेश्वर के द्वारा इज़राइल में अपने लोगों के लिए मूसा को दिए गए कानूनों का ज़िक्र किया था, वे मूसा के कानून हैं। पवित्रता के बारे में ये विशेष कानून बुक ऑफ़ लेविटिकस से आते हैं।"}]}]}}]', [ContentSize] = 32955, Updated = GETUTCDATE() WHERE Id = @TextResourceContentId;

SELECT @PassageId = NULL;
SELECT @PassageId = Id FROM Passages WHERE [StartVerseId] = 1042002001 AND [EndVerseId] = 1042002012;
IF @PassageId IS NULL
BEGIN
INSERT INTO Passages([StartVerseId], [EndVerseId]) VALUES (1042002001, 1042002012);
SELECT @PassageId = @@IDENTITY;
END

SELECT @ResourceId = NULL;
SELECT @ResourceId = Id FROM Resources WHERE [ParentResourceId] = 1 AND [EnglishLabel] = N'CBBT-ER Mark 2:1-12';
IF @ResourceId IS NULL
BEGIN
INSERT INTO Resources([ParentResourceId], [EnglishLabel]) VALUES (1, N'CBBT-ER Mark 2:1-12');
SELECT @ResourceId = @@IDENTITY;
END

IF NOT EXISTS (SELECT 1 FROM PassageResources WHERE [PassageId] = @PassageId AND [ResourceId] = @ResourceId)
BEGIN
INSERT INTO PassageResources([PassageId], [ResourceId]) VALUES (@PassageId, @ResourceId);
END

SELECT @AudioResourceContentId = NULL;
SELECT @AudioResourceContentId = Id FROM ResourceContents WHERE [ResourceId] = @ResourceId AND [LanguageId] = @LanguageId AND [MediaType] = 2;
IF @AudioResourceContentId IS NULL
BEGIN
