UPDATE ResourceContents SET [DisplayName] = N'मरकुस 2:1-12', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'{"webm":{"url":"https://cdn.aquifer.bible/aquifer-content/resources/CBBTER/HIN/audio/webm/HIN_CBBTER_042_MRK_002_001_WEBM.zip","size":3294845,"steps":[{"stepNumber":1,"file":"1.webm"},{"stepNumber":2,"file":"2.webm"},{"stepNumber":3,"file":"3.webm"},{"stepNumber":4,"file":"4.webm"},{"stepNumber":5,"file":"5.webm"}]},"mp3":{"url":"https://cdn.aquifer.bible/aquifer-content/resources/CBBTER/HIN/audio/mp3/HIN_CBBTER_042_MRK_002_001_MP3.zip","size":5539698,"steps":[{"stepNumber":1,"file":"1.mp3"},{"stepNumber":2,"file":"2.mp3"},{"stepNumber":3,"file":"3.mp3"},{"stepNumber":4,"file":"4.mp3"},{"stepNumber":5,"file":"5.mp3"}]}}', [ContentSize] = 3294845, Updated = GETUTCDATE() WHERE Id = @AudioResourceContentId;

