IF @TextResourceContentId IS NULL
BEGIN
INSERT INTO ResourceContents([ResourceId], [LanguageId], [DisplayName], [Version], [Completed], [Trusted], [Content], [ContentSize], [MediaType]) VALUES (@ResourceId, @LanguageId, N'मरकुस 2:1-12', 1, 1, 1, N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:1-12 को सुनें और इसे अपने दिल में बसा लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आप किसी ऐसे को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:1-12"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह कहानी यीशु और धार्मिक नेताओं के बीच गैलिलि में हुए एक विवाद के बारे में एक सेक्शन (मार्क 2-3:6) से शुरू होती है। ये कहानियां उन समस्याओं के बारे में बताती हैं जो आखिर में यीशु की गिरफ़्तारी और मौत का कारण बनती हैं। इस कहानी का मुख्य विषय यह है कि यीशु के पास पृथ्वी पर किए गए पापों को क्षमा करने का अधिकार है क्योंकि वह आदम के बेटे हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"कुष्ट रोगी ने सभी को अपने इलाज के बारे में बताया, जिसके बाद यीशु को फिर से जंगल में जाना पड़ा। कुछ समय के बाद, यीशु फिर से केपरनॉम शहर आ जाते हैं और लोगों को पता चल जाता है कि वे वापस आ गए हैं। वह शायद पीटर और एंड्र्यू के घर में रहे लेकिन हमें यह पक्का नहीं पता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहां रुकें और टीम को गैलिलि सागर का नक्शा दिखाएं, जिसमें केपरनॉम शामिल है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु वहाँ मौजूद लोगों को \"उपदेश\" दे रहे थे। उपदेश परमेश्वर की ओर से एक संदेश है जिसका अर्थ है कि उसका राज्य निकट है, इसलिए लोगों को पश्चाताप करना चाहिए (अपने पापों का पश्चाताप) और परमेश्वर पर विश्वास करना चाहिए। यीशु लोगों से भरे एक घर में थे। घर इतना भरा था कि लकवाग्रस्त व्यक्ति के दोस्तों को उसे चटाई से छत के ऊपर ले जाना पड़ा। वहाँ के घरों में सपाट छत होती थीं और घर के बाहर बनी सीढ़ियाँ छत तक पहुँचाती थीं। लोग अक्सर गर्म रातों में छत पर सोते थे। छत आमतौर पर शाखाओं से बनी होती थी और उसे मिट्टी से प्लास्टर किया जाता था और रस्सियों का इस्तेमाल करके व्यक्ति की चटाई को नीचे लाने के लिए, इस छत को आसानी से हटाया भी जा सकता था। मार्क का कहना है कि उन्होंने छत में खुदाई की, जिससे हमें पता चलता है कि छत शायद सूखी मिट्टी और शाखाओं की बनी थी और लोगों ने सामान को एक तरफ़ रखकर खुदाई की, ताकि वह किसी पर गिरे नहीं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी टीम को उस समय के गैलिलि क्षेत्र के एक विशिष्ट घर की तस्वीर दिखाएं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने देखा कि उस व्यक्ति और उसके दोस्तों का मानना था कि यीशु उस व्यक्ति को ठीक कर सकते हैं। यीशु ने व्यक्ति को प्यार से \"मेरे बच्चे/मेरे बेटे\" जैसे शब्दों से पुकारा। इसका उस व्यक्ति की उम्र से कोई लेना-देना नहीं है। तुरंत यीशु ने कहा कि उस व्यक्ति के पापों को क्षमा कर दिया गया है। हमें आश्चर्य हो सकता है कि यीशु ने उस व्यक्ति के शारीरिक रूप से ठीक होने के बाद, उसके पापों के बारे में बात क्यों की। हालाँकि, नए टेस्टामेंट के दौरान लोगों का मानना था कि बीमारी अक्सर लोगों के जीवन में पाप या विश्वास की कमी का परिणाम होती है। लोगों का यह मानना था की जब परमेश्वर आपको आपके पापों के लिए क्षमा कर देते थे, तो वह आपको ठीक कर देते थे। हालाँकि, यीशु यह नहीं कह रहे थे कि वह उस व्यक्ति के एक विशिष्ट पाप को क्षमा कर रहे थे, लेकिन यह सच है कि सामान्य रूप से बीमारी और मृत्यु लोगों के पाप के कारण ही होती है। इस वजह से, यीशु जो सबसे अहम चीज़ कर सकते थे, वह थी किसी के पापों को क्षमा करना। यीशु ने यह नहीं कहा कि वह स्वयं पापों को क्षमा कर देते थे\\— वह यह नहीं बताते थे कि कौन क्षमा करता है। यह कोई अलग बात नहीं है\\—उस दिन बहुत से यहूदियों ने इस तरह बात की, कि उन्हें परमेश्वर का नाम न लेना पड़े।"}]},{"type":"paragraph","content":[{"type":"text","text":"अपनी अनुवाद टीम के साथ वाक्यांश \"आपके पापों को क्षमा कर दिया गया है\" पर चर्चा करें। क्या आपकी भाषा में, आपको यह कहने की ज़रूरत है कि पापों को किसने क्षमा कर दिया? अगर ऐसा हो, तो आपको यह वाक्यांश इस्तेमाल करना चाहिए \"यीशु ने कहा, \"बेटा, परमेश्वर ने आपके पापों को क्षमा कर दिया।\""}]},{"type":"paragraph","content":[{"type":"text","text":"हालाँकि, एक समस्या थी...कानून के शिक्षकों को पता था कि पुराने टेस्टामेंट शास्त्रों के अनुसार, सिर्फ़ परमेश्वर पापों को क्षमा कर सकते हैं। पैगंबर दावा कर सकते थे कि परमेश्वर ने पापों को क्षमा कर दिया, जैसे कि जब पैगंबर नेथन ने डेविड को बताया कि उनके पापों को क्षमा कर दिया गया है। शिक्षक इस बारे में सोच रहे थे-\\—वे ''उनके दिलों के तर्क'' थे, क्योंकि लोगों का मानना था कि यह सोच दिल में पैदा हुई थी। धार्मिक नेता बहुत नाराज़ थे क्योंकि ऐसा लगता था कि यीशु ने खुद मनुष्य के पापों को क्षमा कर दिया। उनका मानना था कि यीशु परमेश्वर की निन्दा या अपमान कर रहे थे और यह यीशु पर आरोप लगाने और बाद में मार्क में उन्हें मारने के लिए एक आधार की शुरुआत थी।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु उनकी आलोचना करने वाले लोगों से सामना करने के लिए एक सवाल पूछते थे। यीशु यह बताना चाहते थे कि माफ़ी, उपचार से ज़्यादा ज़रूरी और ज़्यादा मुश्किल है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु खुद को \"मनुष्य का बेटा\" कहते हैं। \"मनुष्य का बेटा\" वह व्यक्ति है जिसे डैनियल 7 में, परमेश्वर ने सभी अधिकार और शक्ति दी हैं**।** इसका मतलब यह था कि यीशु कह रहे थे कि उन्हें पापों को क्षमा करने की शक्ति परमेश्वर से मिली थी। कहानी के इस हिस्से का व्याकरण मुश्किल है। यह संभव है कि वह वाक्य जहां यीशु ने खुद को मनुष्य का बेटा कहा है, वह वास्तव में लेखक मार्क ने अपनी किताब में ईसाई पाठकों से कहा हो कि उन्हें यह महसूस करना चाहिए कि यीशु के पास पापों को क्षमा करने का अधिकार था। मार्क कहना चाहते हैं, \"इस बात को समझें कि, मनुष्य के बेटे के पास पापों को क्षमा करने का अधिकार है।\" यह आपके लिए एक तरीका हो सकता है, जिससे आप इस मुश्किल वाक्य को समझ सकें... उस दिन घर में मौजूद धार्मिक नेताओं ने यीशु को जोर से यह कहते सुना कि उनके पास पापों को क्षमा करने का अधिकार है\\—उन्होंने यह तब देखा जब वह आदमी ठीक हो गया था।"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क फिर तुरंत ठीक होने और भीड़ की प्रतिक्रिया के बारे में बात करते हैं, क्योंकि वह आमतौर पर इन चमत्कारिक कहानियों में इसका ज़िक्र करते हैं।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का मतलब बताना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:1-12"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, आप इस कहानी की कल्पना करने में ग्रुप की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग और पात्रों को परिभाषित करने में ग्रुप की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में तीन दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" यीशु केपरनॉम में शिक्षा दे रहे हैं। वह एक घर में शिक्षा दे रहे हैं और वहाँ उनसे मिलने लोगों की भीड़ भी लगी है। चार व्यक्ति अपने लकवाग्रस्त दोस्त को यीशु के पास लेकर आते हैं, लेकिन वे भीड़ की वजह से घर में नहीं आ पाते। इसलिए वे घर की छत पर जाते हैं और छत में एक छेद खोदकर रास्ता बनाते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" दोस्तों ने लकवाग्रस्त व्यक्ति को यीशु के सामने भीड़ में नीचे लाकर रख दिया है। यीशु ने उनकी आस्था को देखते हुए कहा कि लकवाग्रस्त व्यक्ति के पापों को क्षमा कर दिया गया है। इस पर, धार्मिक नेता आपस में कहते हैं, \"यीशु यह क्या कह रहे हैं? यह परमेश्वर की निन्दा है! केवल परमेश्वर पापों को क्षमा कर सकते हैं।\""}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु जो यह जानते थे कि वे क्या सोच रहे हैं, कहते हैं, \"आप इस पर सवाल क्यों उठा रहे हैं? क्या यह कहना आसान है कि \"आपके पापों को क्षमा कर दिया गया है\" या \"खड़े हो जाओ, अपनी चटाई उठाओ और चल दो\"? मैं आप को साबित कर दूँगा कि मनुष्य के बेटे को पृथ्वी पर पापों को क्षमा करने का अधिकार है।\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"तीसरा दृश्य:"},{"type":"text","text":" यीशु लकवाग्रस्त व्यक्ति की ओर मुड़ते हैं और कहते हैं, \"खड़े हो जाओ, अपनी चटाई उठाओ और घर जाओ।\" व्यक्ति उछलता है, अपनी चटाई उठाता है और देखने वालों की भीड़ में से निकल जाता है। भीड़ चकित हो जाती है और परमेश्वर की प्रशंसा करते हुए कहती है, \"हमने पहले कभी ऐसा कुछ नहीं देखा है।\""}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्र हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"चार व्यक्ति (लकवाग्रस्त व्यक्ति के दोस्त)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक लकवाग्रस्त व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"घर को पूरी तरह से भरने वालों की भारी भीड़"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह कहानी केपरनॉम के एक घर में हुई थी। यह घर काफ़ी छोटा था, लेकिन यीशु की शिक्षा सुन रहे लोगों से भरा हुआ था।"}]},{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता शायद घर में सम्मान की जगह पर मौजूद हो सकते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस घर पर सीढ़ियाँ मिट्टी और भूसे से बनी एक सपाट छत तक ले जातीं। सुनिश्चित करें कि टीम ड्रॉ करती है या किसी तरह प्रदर्शित करती है कि इस घर में एक सपाट, मिट्टी और भूसे की छत रही होगी, और इसलिए दोस्तों के लिए छत में छेद करना आसान रहा होगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"लकवाग्रस्त व्यक्ति को उनके बिस्तर से ही ले जाया गया होगा जो कि एक चटाई की तरह होगा। चार दोस्तों ने उन्हें इस चटाई के ज़रिए ही नीचे पहुँचाया होगा।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"टेक्स्ट को एम्बेड करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:1-12"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में तीन दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"चार व्यक्ति (लकवाग्रस्त व्यक्ति के दोस्त)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक लकवाग्रस्त व्यक्ति"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"घर को पूरी तरह से भरने वालों की भारी भीड़"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने के लिए कहें। जिस भाषा में वे अनुवाद कर रहे हैं, उसी भाषा में उन्हें अभिनय करना चाहिए। सबसे पहले, टीम को बिना रूके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी के अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब टीम कहानी पर पहली बार अभिनय करती है, तब, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ जगहों पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"चार व्यक्ति अपने लकवाग्रस्त दोस्त को यीशु के पास ले आते हैं। वे लोगों की एक बड़ी भीड़ देखते हैं और घर में जाने की कोशिश करते हैं। जब टीम कहानी पर अभिनय करती है, तो सुनिश्चित करें कि चारों व्यक्ति इस बात को ठीक से दिखा पाएँ कि उनके पास अपने लकवाग्रस्त दोस्त को यीशु के पास ले जाने का और कोई रास्ता नहीं था, सिवाय घर की सपाट छत पर जाकर, उसमें एक छेद को खोदकर लकवाग्रस्त व्यक्ति को नीचे पहुँचाना। "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें"},{"type":"text","text":"। चार दोस्तों की भूमिका निभाने वाले अभिनेताओं से पूछें, \"आप कैसा महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"हताश,\" \"निराश,\" या \"हमारे दोस्त को ठीक करने के लिए हम कुछ भी कर सकते हैं।\" अभिनय फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दोस्त छत पर जाते हैं, एक छेद खोदते हैं और उस लकवाग्रस्त व्यक्ति को छेद से नीचे भेजते हैं। यीशु, दोस्तों की आस्था को देखते हैं और लकवाग्रस्त व्यक्ति से कहते हैं, \"आपके पापों को क्षमा कर दिया गया है।\" यीशु की भूमिका निभाने वाले अभिनेता से पूछें, \"आपने उस व्यक्ति को क्यों क्षमा कर दिया?\" आप ऐसी बातें सुन सकते हैं कि \"उनके दोस्तों की आस्था,\" \"मैं चाहता था कि धार्मिक नेता सुनें,\" या \"व्यक्ति की भी मुझमें आस्था होगी, तभी वह इस तरह से नीचे आया।\" अभिनय फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता खुद से पूछते हैं, \"यीशु क्या कह रहे हैं? यह परमेश्वर की निन्दा है! केवल परमेश्वर पापों को क्षमा कर सकते हैं।\" यीशु तुरंत यह समझ जाते हैं कि वे क्या सोच रहे हैं, इसलिए वह उन्हें देखते हैं और कहते हैं, \"आप इस पर अपने दिलों में क्यों सवाल उठाते हैं? क्या लकवाग्रस्त व्यक्ति से यह कहना आसान है, ''आपके पापों को क्षमा कर दिया गया है'' या ''खड़े हो जाओ, अपनी चटाई उठाओ, और चले जाओ''?\" "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें"},{"type":"text","text":"। यीशु की भूमिका निभाने वाले अभिनेता से पूछें, \"क्या आप धार्मिक नेताओं से जवाब की उम्मीद कर रहे थे?\" आप ऐसी बातें सुन सकते हैं \"नहीं, मैं कुछ साबित करने जा रहा हूँ। मैं यह दिखाने वाला हूँ कि मैं सिर्फ शब्द नहीं कह रहा हूँ, लेकिन मैं खुद परमेश्वर हूँ, जो न केवल यह कह सकते हैं कि ''आपके पापों को क्षमा कर दिया गया है,\" बल्कि किसी को ठीक भी कर सकते हैं।\" अभिनय फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु कहते हैं, \"मैं आप को साबित कर दूँगा कि मनुष्य के बेटे को पृथ्वी पर पापों को क्षमा करने का अधिकार है।\" फिर यीशु लकवाग्रस्त व्यक्ति की ओर मुड़ते हैं और कहते हैं, \"खड़े हो जाओ, अपनी चटाई उठाओ और घर जाओ।\" "},{"type":"text","marks":[{"type":"bold"}],"text":"अभिनय बंद कर दें।"},{"type":"text","text":" टीम से पूछें, \"यीशु ने इस व्यक्ति को क्यों ठीक कर दिया?\" आप ऐसी बातें सुन सकते हैं कि \"यह साबित करने के लिए कि उन्हें परमेश्वर से अधिकार मिला है\" या \"धार्मिक नेताओं को यह साबित करने के लिए कि वह मनुष्य के बेटे हैं (मसीहा को दिया गया एक टाइटल, जिसे वह ढूंढ रहे थे)।\" अभिनय फिर से शुरू करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"अभिनय के अंत में, भीड़ की भूमिका निभाने वालों से पूछें, जो चकित थे और परमेश्वर की प्रशंसा कर रहे थे, \"आप इसे देखने के बाद कैसा महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"उत्तेजित,\" \"उलझा हुआ,\" या \"इस बात पर आश्चर्य कि क्या यीशु वही हैं जिस व्यक्ति के लिए हम सब इंतज़ार कर रहे थे।\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"गैप को भरते हुए"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:1-12"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए सबसे सरल भाषा में टेक्स्ट को सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"केपरनॉम"}]},{"type":"paragraph","content":[{"type":"text","text":"केपरनॉम के साथ गैलिलि का एक नक्शा दिखाएँ।"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर का उपदेश"}]},{"type":"paragraph","content":[{"type":"text","text":"अच्छी खबर का संदेश कि परमेश्वर का राज्य करीब था।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक चटाई पर लकवाग्रस्त व्यक्ति"}]},{"type":"paragraph","content":[{"type":"text","text":"एक व्यक्ति चटाई पर पड़ा था क्योंकि उसे एक बीमारी थी जिसके कारण वह उठ नहीं सकता था। शास्त्र आमतौर पर हमें बताते हैं कि किसी को जन्म से ही यह बीमारी होती है, लेकिन यहाँ ऐसा नहीं बताया गया है, तो शायद इस व्यक्ति को जन्म के बाद ही यह बीमारी हुई होगी। हालाँकि, उन्हें शायद कुछ समय से ही यह रोग था। यह चटाई शायद एक स्ट्रेचर थी जिसे लोग लपेट सकते थे और इस पर उन्हें ले जा सकते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"मेरा बच्चा/मेरा बेटा"}]},{"type":"paragraph","content":[{"type":"text","text":"यह शब्द व्यक्ति की उम्र को युवा के रूप में नहीं दिखाता, बल्कि यह यीशु का स्नेह और प्यार दिखाता है। व्यक्ति यीशु का असल बेटा नहीं है, इसलिए आपको किसी ऐसे शब्द का इस्तेमाल नहीं करना चाहिए, जिसका अर्थ सिर्फ़ असली बेटा हो। क्योंकि यह स्नेह का एक शब्द है, इसलिए आपको \"मेरा दोस्त\" जैसे व्यापक स्नेह वाले शब्द का इस्तेमाल करना होगा।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"यीशु में उनकी आस्था"},{"type":"text","text":" या विश्वास कि वे उस व्यक्ति को ठीक कर सकते हैं। \"उनकी\" में शायद खुद बीमार व्यक्ति और उसके दोस्त भी शामिल हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"रुकें और \"विश्वास\" के लिए आप जिस शब्द का इस्तेमाल कर रहे हैं उसकी समीक्षा करें। यह कौनसा शब्द है? उस शब्द का इस्तेमाल यहाँ करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"पाप क्षमा कर दिए गए हैं"}]},{"type":"paragraph","content":[{"type":"text","text":"आमतौर पर यीशु नहीं कहते हैं \"मैं आपके पापों को क्षमा करता हूँ।\" यदि आपकी भाषा में निष्क्रिय वाक्यांश नहीं बनाए जा सकते, तो आपको यह कहने की आवश्यकता हो सकती है कि किसने पापों को क्षमा किया-\\—कुछ इस तरह \"भगवान आपके पापों को क्षमा करें।\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"italic"}],"text":"पाप"},{"type":"text","text":" परमेश्वर के प्रति अवज्ञा को संदर्भित करता है। सुनिश्चित करें कि आप यहाँ जिस शब्द का इस्तेमाल करते हैं, इसका मतलब यह नहीं है कि कोई व्यक्ति धार्मिक कार्य करने में विफल रहा है जैसे कि धार्मिक प्रार्थना या कुछ धार्मिक नियमों का पालन न करना।"}]},{"type":"paragraph","content":[{"type":"text","text":"सुनिश्चित करें कि आप पापों और क्षमा के लिए उन्हीं शब्दों का इस्तेमाल कर रहे हैं, जिनका इस्तेमाल आपने पिछली कहानियों में किया है। यहाँ पर कुछ समय लें और समीक्षा करें कि आपने किस शब्द का इस्तेमाल किया है और क्यों। क्या यह अभी भी सबसे अच्छा शब्द है?"}]},{"type":"paragraph","content":[{"type":"text","text":"धार्मिक कानून के शिक्षक"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"कानून के शिक्षकों को"},{"type":"text","text":" कभी-कभी इस तरह से अनुवाद किया जाता है, "},{"type":"text","marks":[{"type":"bold"}],"text":"लेखक या कानून को लिखने वाले"},{"type":"text","text":": वे पवित्र शास्त्रों से ली गई कानून की व्याख्याओं के विद्वान और विशेषज्ञ थे। इन व्याख्याओं को लिखा नहीं गया था, लेकिन याद किया गया था और शिक्षकों द्वारा मौखिक रूप से सिखाया गया था।"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर की निन्दा"}]},{"type":"paragraph","content":[{"type":"text","text":"अपनी भाषा से परमेश्वर का अपमान या तिरस्कार करना। पुराने टेस्टामेंट के कानून के हिसाब से, इसकी सज़ा मौत थी।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु को पता था कि वे क्या सोच रहे हैं (उनके मन में)=उनके दिमाग में"}]},{"type":"paragraph","content":[{"type":"text","text":"ऐसे शब्द का इस्तेमाल न करें, जिसका अर्थ हो कि यीशु कुछ जादू की शक्ति का इस्तेमाल कर रहे हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"मनुष्य का बेटा"}]},{"type":"paragraph","content":[{"type":"text","text":"पुराने टेस्टामेंट में \"मनुष्य का बेटा\" शब्द का अर्थ बस एक व्यक्ति है। यीशु इस शब्द का इस्तेमाल अपने बारे में बात करने के लिए अपने पसंदीदा तरीके के रूप में करते हैं। \"मनुष्य का बेटा\" वह व्यक्ति है जिसे डैनियल 7 में, परमेश्वर ने सभी अधिकार और शक्ति दी हैं**।** यीशु को शायद यह शब्द इसलिए पसंद है क्योंकि शीर्षक एक सामान्य इंसान या एक अलौकिक प्राणी को संदर्भित कर सकता है जैसे कि यह डैनियल 7 में है। मनुष्य का बेटा शब्द लोगों को यह फैसला करने के लिए मजबूर करता है कि यीशु, इंसान हैं या परमेश्वर। यह मसीहा/क्राइस्ट के राजनीतिक शब्द को भी टालता है\\—वह व्यक्ति जिसके बारे में लोगों ने सोचा था कि वह राजा बन जाएगा जो उन सभी को अत्याचारों से बचाएगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क हर बार उस पर बात करना पसंद करते हैं जब यीशु ने खुद को मनुष्य का बेटा कहा, ताकि वह एक व्यक्ति की तरह दुखी होने और मरने की उनकी किस्मत के बारे में बात कर सकें। लेकिन, मनुष्य के बेटे एक आम इंसान से बढ़कर हैं और वे मरकर भी ज़िंदा हो जाएँगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु तीसरे व्यक्ति की तरह खुद की बात करते हैं, जब वह खुद को यह खिताब देते हैं। सुनिश्चित करें कि आपके अनुवाद से यह स्पष्ट हो कि यीशु खुद के बारे में बोल रहे हैं। कभी-कभी ''मनुष्य का बेटा'' शब्द का मतलब है कि यीशु एक कुंवारी महिला से नहीं पैदा हो सकते हैं। अगर यह मामला है, तो आप कुछ ऐसा कह सकते हैं कि \"वह व्यक्ति जो वास्तव में मनुष्य था।\""}]},{"type":"paragraph","content":[{"type":"text","text":"चकित"}]},{"type":"paragraph","content":[{"type":"text","text":"इस शब्द में एक सकारात्मक अर्थ है। लोग खुश थे और हैरान थे कि वह व्यक्ति ठीक हो गया।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"परमेश्वर की महिमा"},{"type":"text","text":" का अर्थ है परमेश्वर की स्तुति करना, यह कहना कि वह कितने अच्छे हैं।"}]}]}}]', 45299, 1);
SELECT @TextResourceContentId = @@IDENTITY;
END
