UPDATE ResourceContents SET [DisplayName] = N'मरकुस 2:13-17', [Version] = 1, [Completed] = 1, [Trusted] = 1, [Content] = N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:13-17 को सुनें और इसे दिल में बसा लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"क्या आप किसी ऐसे व्यक्ति को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:13-17"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए सबसे आसान लहजे में टेक्स्ट को एक बार सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"पिछली कहानी में यीशु ने एक चमत्कार किया था। इसलिए वह यहाँ से फिर जंगल (गैलिली सागर के किनारे) चले जाते हैं\\—लेकिन लोग उसके पास आते रहते हैं। उसके बाद वह वापस कपरनियम लौट जाते हैं, जहाँ उन्हें लेवी मिलता है और वे उसे उनका अनुसरण करने के लिए बुलाते हैं। यह मार्क का एक पैटर्न है-बार-बार उन जंगल और उन शहरों के बीच यीशु का आना-जाना दिखाना जहाँ वह चमत्कार करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"उसके बाद यीशु वापस कपरनियम आ जाते हैं। कपरनियम एक महत्वपूर्ण शहर है क्योंकि यह सड़क पर स्थित था और अन्य क्षेत्रों से यहाँ यात्री आते थे। लेवी एक यहूदी टैक्स अधिकारी था\\—जिसका मतलब था कि वह शायद बाज़ार में अपने उत्पाद बेचने वाले व्यापारियों और मछुआरों से पैसे लेता था। टैक्स अधिकारियों को कोई पसंद नहीं करता था क्योंकि वे आमतौर पर बेईमान होते थे और लोगों से अपने हिस्से से अधिक पैसे लेते थे। परिणामस्वरूप, उन्हें समाज से बाहर निकाले गए लोग माना जाता था\\—वे अदालत में जज या गवाह नहीं बन सकते थे, वे अन्य लोगों के साथ आराधनालय में पूजा नहीं कर सकते थे और उन्हें उनके परिवार के लिए अपमानजनक या शर्मनाक माना जाता था। जब लोग, टैक्स अधिकारियों के बारे में बात करते थे तब वे उन्हें हत्यारों और लुटेरों की तरह की श्रेणी में डाल देते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी अनुवाद टीम के साथ चर्चा करें\\—आपके समाज में किन लोगों को ऐसे बाहर निकाल दिया जाता है? बाहर निकाले गए लोग किस तरह का काम करते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने अल्फ़ेयस के बेटे लेवी को अपने बूथ में बैठकर टैक्स इकट्ठा करते देखा। मैथ्यू के उपदेश का कहना है कि यह मैथ्यूज़ ही है, लेकिन ल्यूक और मार्क दोनों ने इस व्यक्ति के लिए लेवी नाम का उपयोग किया है। लेवी तुरंत उठकर यीशु का अनुसरण करने लगा\\—कपरनियम उतना बड़ा नहीं है और लेवी को लोग अच्छी तरह से जानते थे, इसलिए यह संभावना है कि यीशु का अनुसरण करने वाले मछुआरे और खुद यीशु, एक-दूसरे और लेवी के बारे में जानते थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"लेवी ने तुरंत यीशु का अनुसरण किया और अन्य टैक्स कलेक्टर और पापियों को भोजन के लिए आमंत्रित किया। टेक्स्ट में स्पष्ट नहीं है कि उन्होंने किसके घर खाया\\—यीशु के घर या लेवी के घर\\—लेकिन यह साफ़ है कि यीशु भोजन का नेतृत्व कर रहे हैं। आप भी अपने अनुवाद में इसे अस्पष्ट छोड़ सकते हैं कि वह कौन सा घर था। आश्चर्य की बात है कि यीशु इस तरह के लोगों के साथ खा रहे हैं, क्योंकि अब तक उन्हें धार्मिक कानून का एक सम्मानित शिक्षक माना जाता है। इस भोजन में ऐसे कई लोग शामिल थे जिनके साथ शिक्षक नहीं खाएँगे। उस समय यहूदी रिवाज में, एक साथ भोजन करना, दोस्ती के सबसे महत्वपूर्ण संकेतों में से एक था। खाने पर \"पापियों\" को बुलाया गया था क्योंकि वे ऐसे लोग थे जिनसे लोग नफ़रत करते थे और जिन्हें बाहर निकाल देते थे, क्योंकि वे खाने से पहले खुद को धार्मिक और औपचारिक रूप से साफ़ नहीं करते थे। यीशु दो तरह के लोगों के समूहों\\—टैक्स अधिकारियों और पापियों\\—के साथ खा रहे थे जिन्हें समाज से बाहर निकाल दिया गया था।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और एक अनुवाद टीम की तरह चर्चा करें: आपकी संस्कृति में एक साथ खाना खाने की क्या भूमिका है? क्या यह एक साथ खाना ज़रूरी है? क्या ऐसे नियम हैं कि एक साथ कौन खा सकता है? जब आप एक साथ खाते हैं, तो क्या आप महत्वपूर्ण भोजन के लिए कुछ खास बातें करते हैं\\—जैसे कि आप क्या खाते हैं या एक साथ कैसे बैठते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ जो कार्य किया गया है वह यह है कि मेहमानों ने मेज़ के चारों तरफ़ ''टेक लगाया'' या वे लेट गए। यहूदी लोग, औपचारिक भोजन में इसी तरह एक साथ खाते थे। महत्वपूर्ण बात यह है कि वे एक साथ खाने के लिए मेज़ पर आए थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"फरीसी, धार्मिक नेता थे जो शास्त्र के लिखित और मौखिक कानून का पालन करना पसंद करते थे। उन्हें ऐसे लोगों के रूप में जाना जाता था जो पापियों से ''अलग'' रहना चाहते थे, इसलिए यीशु के कार्यों से वे बहुत नाराज़ हुए।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु ने उन्हें एक पारंपरिक कहावत के साथ जवाब दिया कि सबका सम्मान किया जाएगा। समाज से बाहर निकाले गए लोगों ने समझा कि उन्हें धर्मी बनाने की ज़रूरत है, जबकि जो लोग सोचते थे कि वे लोग सही हैं उन्हें समझ नहीं आ रहा था कि यीशु क्यों आए थे। यीशु, पापियों के साथ इसलिए खाना चाहते थे क्योंकि वे उन्हें बदलना "},{"type":"text","marks":[{"type":"italic"}],"text":"चाहते"},{"type":"text","text":" थे।"}]},{"type":"paragraph","content":[{"type":"text","text":"अध्याय 2 के शेष अंश में औपचारिक या धार्मिक तरीके से साफ़ हुए बिना यीशु और उनके शिष्यों की खाने की आदत के बारे में बताया गया है। मार्क इस कहानी को अपनी किताब में इसलिए जगह देते हैं क्योंकि यह इस अगले विषय का परिचय देता है।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का मतलब बताना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:13-17"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए पाठ को एक बार फिर से सबसे आसान भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, आप इस कहानी की कल्पना करने में ग्रुप की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग्स, व कहानी के चरित्रों को उतारने में grup की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"पहला दृश्य:"},{"type":"text","text":" यीशु ने उस घर में शिक्षा देना पूरा कर दिया है, जहां उन्होंने लकवाग्रस्त व्यक्ति को ठीक कर दिया था। वह कपरनियम में हैं। कपरनियम एक विशाल झील गैलिली सागर के किनारे है, इसलिए यीशु उस घर को छोड़कर तट पर जाने का फ़ैसला करते हैं। जब वह कपरनियम से निकल रहे हैं, तो वह टैक्स कलेक्टर लेवी को अपने बूथ पर टैक्स कलेक्ट करते हुए देखते हैं। यीशु कहते हैं, \"मेरा अनुसरण करें और मेरा शिष्य बनें।\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दूसरा दृश्य:"},{"type":"text","text":" उस दिन बाद में, लेवी ने यीशु और उनके शिष्यों को रात के खाने के लिए अपने घर में आमंत्रित किया है। लेवी ने अन्य टैक्स कलेक्टरों और अन्य लोगों को आमंत्रित किया है, जिन्हें पापी माना जाता है। धार्मिक नेता इसे देखते हैं और यीशु के शिष्यों से पूछते हैं, \"वह इन तरह के लोगों के साथ क्यों खाते हैं?\""}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु यह सुनते हैं और कहते हैं, \"स्वस्थ लोगों को डॉक्टर की ज़रूरत नहीं होती है। बीमार लोगों को ज़रूरत होती है। मैं उन लोगों को नहीं बुलाने आया हूँ जो सोचते हैं कि वे सच्चे हैं, बल्कि उनको मिलने आया हूँ जो जानते हैं कि वे पापी हैं।\""}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्र हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लेवी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लोगों की भीड़"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लेवी के घर पर अन्य टैक्स कलेक्टर"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लेवी के घर पर अन्य पापी"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य में, यीशु को कपरनियम से होकर जाते हुए और तट पर मौजूद लेवी को बुलाते हुए दिखाया गया है। यीशु के पीछे लोगों की भीड़ होगी क्योंकि वह उन्हें रास्ते में शिक्षा देते हुए चलते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य में, लेवी के घर में यीशु की कल्पना करें। यह घर शायद एक अच्छा घर होगा।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"टेक्स्ट को हकीकत में उतारना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:13-17"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए टेक्स्ट को एक बार फिर से सबसे आसान भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम, कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में दो दृश्य हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पहले दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लोगों की भीड़"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लेवी (एक टैक्स कलेक्टिंग बूथ में एक टैक्स कलेक्टर)"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे दृश्य के पात्रों में निम्नलिखित शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लेवी"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"अन्य टैक्स कलेक्टर"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पापी माने जाने वाले अन्य लोग"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"धार्मिक नेता"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने को कहें। उन्हें उस भाषा में इसे अभिनय करना चाहिए, जिसमें वे अनुवाद कर रहे हैं। सबसे पहले, टीम को बिना रुके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी की अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जैसे जैसे टीम कहानी पर पहली बार अभिनय करती है, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ जगहों पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु कपरनियम से होते हुए जाते हैं, तो वह लेवी को अपने टैक्स बूथ में देखते हैं और कहते हैं, \"मेरे पीछे हो लीजिए और मेरे शिष्य बन जाइए।\" लेवी तुरंत उठ जाते हैं और यीशु का अनुसरण करते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"इस अभिनय को बंद कर दें।"},{"type":"text","text":" लेवी की भूमिका निभाने वाले अभिनेता से पूछें, \"आप इस बात पर क्या महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"यीशु का अनुसरण करने की विवशता,\" \"आशा,\" या \"एक ऐसी तात्कालिकता कि मुझे यीशु का अनुसरण करना चाहिए।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"अब यह अभिनय, लेवी के घर पर हो रहा है। धार्मिक नेता, यीशु के चेलों से पूछने लगते हैं कि \"यीशु, पापियों के साथ क्यों खाते हैं?\" "},{"type":"text","marks":[{"type":"bold"}],"text":"इस अभिनय को बंद कर दें।"},{"type":"text","text":" धार्मिक नेताओं की भूमिका निभाने वाले अभिनेताओं से पूछें, \"आप इस तरह से क्यों महसूस कर रहे हैं?\" आप ऐसी बातें सुन सकते हैं कि \"सम्मानित धार्मिक नेताओं को पापी लोगों के साथ जुड़ना नहीं चाहिए,\" \"हमने यीशु को पहले से ही पंगु व्यक्ति की चिकित्सा करते देखा था और अब वह केवल यह नहीं कहते हैं कि वह पापियों को माफ़ कर देते हैं बल्कि वह पापियों के साथ खाना भी खाते हैं, जो ईश-निन्दा है,\" या \"हम बाहर निकलकर देख रहे हैं और इस बात से जल-भुन रहे हैं कि यीशु हमारे साथ नहीं हैं।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जब यीशु ने धार्मिक नेताओं की बात सुनी तो उन्होंने उनसे कहा, \"स्वस्थ लोगों को डॉक्टर की ज़रूरत नहीं होती है। बीमार लोगों को ज़रूरत होती है। मैं उन लोगों को बुलाने नहीं आया हूं जो सोचते हैं कि वे सच्चे हैं बल्कि उन लोगों को बुलाने आया हूं जो जानते हैं कि वे पापी हैं।\" अभिनय के अंत में यीशु की भूमिका निभाने वाले अभिनेता से पूछें, \"आपका क्या मतलब हैं जब आप कहते हैं कि कुछ लोग सोचते हैं कि वे सच्चे हैं और कुछ लोग जानते हैं कि वे पापी हैं? फर्क क्या है?\" यीशु की भूमिका निभाने वाले अभिनेता से पूछें, \"आप पापियों के साथ समय क्यों बिता रहे हैं?\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"अंतराल भरना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:13-17"}]},{"type":"paragraph","content":[{"type":"text","text":"सबसे आसानी से समझने वाले संस्करण में एक बार टेक्स्ट सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"टैक्स कलेक्टर का बूथ"}]},{"type":"paragraph","content":[{"type":"text","text":"वह स्थान जहाँ लेवी व्यापारियों और मछुआरों से टैक्स वसूल करने के लिए बैठता था।"}]},{"type":"paragraph","content":[{"type":"text","text":"शिष्य"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु के "},{"type":"text","marks":[{"type":"italic"}],"text":"सबसे करीबी अनुयायी"},{"type":"text","text":" उनकी ज़्यादातर सार्वजनिक मिनिस्ट्री में उनके साथ थे, हर जगह उनके साथ यात्रा करते थे, उनसे सीखते थे और कभी-कभी यीशु उन्हें राज्य के अच्छे समाचार का प्रचार करने और बीमारों को ठीक करने के लिए भेजते थे। अगर भाषा में एक शिक्षक के समर्पित अनुयायियों के लिए एक आसान शब्द है, तो आप उस शब्द का इस्तेमाल कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"पापी"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ इस शब्द का प्रयोग असामान्य ढंग से किया गया है। यहां, इसका मतलब उन लोगों से है जो खाने से पहले खुद को साफ़ करने के बारे में लगातार धार्मिक नियमों का उल्लंघन करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह स्पष्ट नहीं है कि इस कहानी में फरीसी यीशु का अनुसरण कर रहे थे या नहीं।"}]},{"type":"paragraph","content":[{"type":"text","text":"धार्मिक कानून के शिक्षक/फरीसी"}]},{"type":"paragraph","content":[{"type":"text","text":"\"फरीसी\" नाम का अर्थ \"अलग होना\" है। फरीसी बहुत प्रभावशाली और सही माने जाते थे। वे लोगों के लिए अच्छे उदाहरण थे क्योंकि वे परमेश्वर के नियमों के प्रति समर्पित थे। उन्होंने लिखित और मौखिक दोनों तरह की धार्मिक परंपराओं और कानूनों को मान्यता दी। कुछ फरीसी परमेश्वर के कानून के शिक्षक भी थे, और इस कहानी में उन्हीं के बारे में बताया गया है। फरीसी शब्द इस प्रकार के धार्मिक नेता का नाम है।"}]},{"type":"paragraph","content":[{"type":"text","text":"चिकित्सक"}]},{"type":"paragraph","content":[{"type":"text","text":"चिकित्सा पेशे में किसी के लिए सम्मानजनक शब्द।"}]},{"type":"paragraph","content":[{"type":"text","text":"धर्मी"}]},{"type":"paragraph","content":[{"type":"text","text":"पुराने टेस्टामेंट के अनुसार, "},{"type":"text","marks":[{"type":"italic"}],"text":"धार्मिकता"},{"type":"text","text":" का विचार परमेश्वर और मनुष्य के बीच, या लोगों के बीच के संबंध पर आधारित है। धार्मिकता का अर्थ है पवित्रता, सही स्थिति, अपराधबोध का अभाव। सीईवी (समकालीन अंग्रेजी संस्करण) इस अवधारणा का अनुवाद इसायाह 53:11 में निम्नलिखित तरीके से करता है: "},{"type":"text","marks":[{"type":"italic"}],"text":"...वह दूसरों के पापों का दण्ड भोगेगा, ताकि उनमें से बहुत से लोग दोषी न रहें।"}]},{"type":"paragraph","content":[{"type":"text","text":"आपको अपने पूरे अनुवाद के दौरान "},{"type":"text","marks":[{"type":"italic"}],"text":"धार्मिकता"},{"type":"text","text":" और औचित्य की अवधारणाओं का वर्णन इसी तरह करना चाहिए। उदाहरण के लिए, आप धार्मिकता का वर्णन "},{"type":"text","marks":[{"type":"italic"}],"text":"परमेश्वर के साथ सही संबंध"},{"type":"text","text":" के रूप में कर सकते हैं। आप इसे कानूनी भाषा में "},{"type":"text","marks":[{"type":"italic"}],"text":"दोषरहित/निर्दोष"},{"type":"text","text":" भी कह सकते हैं। इन अवधारणाओं का वर्णन करने के अन्य तरीके हैं:"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर की दृष्टि में सही होना"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर के साथ सही स्थिति में होना"}]},{"type":"paragraph","content":[{"type":"text","text":"परमेश्वर द्वारा स्वीकार किया जाना"}]},{"type":"paragraph","content":[{"type":"text","text":"आपके श्रोताओं के पास भी धार्मिकता की अवधारणा का वर्णन करने वाले मुहावरे हो सकते हैं, जैसे कि "},{"type":"text","marks":[{"type":"italic"}],"text":"साफ़ हृदय होना, ईमानदार होना, दोषरहित/निर्दोष होना, परमेश्वर के मार्ग पर चलना,"},{"type":"text","text":" आदि। अपने अनुवाद के लिए उपयुक्त शब्द चुनते समय इन्हें बातों का ध्यान रखें।"}]},{"type":"paragraph","content":[{"type":"text","text":"याद रखें कि बाइबल में लोग कभी भी अपने दम पर या बहुत सारी अच्छी चीज़ें करके धर्मी नहीं बन सकते। "},{"type":"text","marks":[{"type":"italic"}],"text":"धार्मिकता"},{"type":"text","text":" के लिए आपका शब्द या वाक्यांश अच्छे कार्यों पर आधारित नहीं होना चाहिए।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"शिक्षण उपकरण:"},{"type":"text","text":" क्योंकि यह इतना महत्वपूर्ण शब्द है, इस पर कुछ समय व्यतीत करें। आपको संस्कृति में किसी प्रकार की उपयुक्त उपमा मिल सकती है। उदाहरण के लिए, आप किसी चीज़ के टूटने और उसके अनुपयोगी हो जाने की उपमा का उपयोग कर सकते हैं। एक बार जब कोई चीज़ टूट जाती है, तो वास्तव में इसे ठीक करने का कोई तरीका नहीं होता है जिससे की वह पूरी तरह से उपयोग करने योग्य बन जाए। (शायद यह एक मिट्टी का बर्तन, एक लौकी, एक धनुष और तीर, आदि हो सकता है। शायद यह एक मुड़ा हुआ तीर हो सकता है जिससे अब निशाना नहीं लगाया जा सकता।) "},{"type":"text","marks":[{"type":"bold"}],"text":"धार्मिकता"},{"type":"text","text":" यह धारणा है कि परमेश्वर उन चीज़ों को फिर से ठीक कर देते हैं जो कभी ठीक नहीं हो सकतीं। वे न केवल वस्तु की मरम्मत करते हैं, बल्कि उसे फिर से इस तरह से उपयोगी बना देते हैं, जैसे कि वह कभी टूटी ही नहीं थी। आप प्रतिभागियों से यह भी कह सकते हैं कि वे आपको जोड़ियों में नकल करके "},{"type":"text","marks":[{"type":"italic"}],"text":"धार्मिकता"},{"type":"text","text":" ''दिखाएँ''। समूह के लोग चर्चा कर सकते हैं कि उन्होंने क्या देखा और यह निर्धारित कर सकते हैं कि क्या ये क्रियाएँ अवधारणा को सटीक रूप से दर्शाती हैं। अगर ऐसा है, तो आप उन क्रियाओं का उपयोग "},{"type":"text","marks":[{"type":"italic"}],"text":"धार्मिकता"},{"type":"text","text":" का वर्णन करने के लिए कर सकते हैं।"}]}]}}]', [ContentSize] = 38490, Updated = GETUTCDATE() WHERE Id = @TextResourceContentId;

