SELECT @TextResourceContentId = NULL;
SELECT @TextResourceContentId = Id FROM ResourceContents WHERE [ResourceId] = @ResourceId AND [LanguageId] = @LanguageId AND [MediaType] = 1;
IF @TextResourceContentId IS NULL
BEGIN
INSERT INTO ResourceContents([ResourceId], [LanguageId], [DisplayName], [Version], [Completed], [Trusted], [Content], [ContentSize], [MediaType]) VALUES (@ResourceId, @LanguageId, N'मरकुस 2:18-22', 1, 1, 1, N'[{"stepNumber":1,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:18-22 को सुनें और इसे दिल से याद कर लें। पाठ को तीन बार सुनें (अगर हो सके, तो तीन अलग-अलग अनुवादों में)। फिर एक टीम के तौर पर नीचे दिये गये सवालों पर चर्चा करें:"}]},{"type":"orderedList","attrs":{"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको इस कहानी में क्या पसंद है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"आपको क्या पसंद नहीं या समझ में नहीं आया?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें यीशु के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमें लोगों के बारे में क्या बतलाती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यह कहानी हमारे दैनिक जीवन पर कैसे असर डालती है?"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"क्या आप किसी ऐसे व्यक्ति को जानते हैं, जिसे यह कहानी सुननी चाहिए?"}]}]}]}]}},{"stepNumber":2,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"स्टेज को सेट करना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:18-22"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए सबसे आसान लहजे में टेक्स्ट को एक बार सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"यह ज़रूरी नहीं है कि मार्क 2 में मौजूद सभी लघु कहानियाँ उसी समय, उसी क्रम में हुई होंगी। हालाँकि, मार्क समान कहानियों और उपदेशों को एक साथ रखते हैं। पिछली कहानी में, यीशु ने पापियों के साथ खाया था, जो कि धार्मिक कानून के खिलाफ प्रतीत होता है। इस कहानी में यीशु और उनके शिष्य फिर से धार्मिक नेताओं को आश्चर्य करते हैं क्योंकि वे उपवास नहीं करते हैं। यीशु और उनके शिष्य यह दिखा रहे हैं कि वे अलग हैं और एक ही धार्मिक रीति रिवाजों का पालन नहीं करते हैं जिनका पालन बाकी सब लोग करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"हालाँकि पुराने टेस्टामेंट में, उपवास (प्रायश्चित का दिन) के लिए केवल एक दिन निर्धारित किया गया था, जब तक यीशु का उपवास अधिक पारंपरिक नहीं हुआ था और यहूदी संस्कृति में अक्सर किया जाने लगा था। फरीसी और उनका अनुसरण करने वाले लोग सप्ताह में दो दिन उपवास करते थे, और जॉन के शिष्य शायद इसलिए उपवास कर रहे थे क्योंकि उनके नेता, जॉन की मृत्यु हो गई थी। उपवास का मूल विचार पश्चाताप है\\—पाप की सफ़ाई के लिए और परमेश्वर से सब कुछ फिर से सही करने का कहने के लिए उपवास रखा जाता था। यीशु के दिन पर लोग अधिक धर्मी बनना चाहते थे और परमेश्वर की कृपा या योग्यता प्राप्त करना चाहते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अनुवाद टीम के साथ उन कार्यों पर चर्चा करें जिन्हें लोग आपकी संस्कृति में अधिक धर्मी बनने या परमेश्वर की कृपा पाने की कोशिश करने के लिए करते हैं।"}]},{"type":"paragraph","content":[{"type":"text","text":"तो यह सवाल उचित लगता है\\—अगर यीशु इतने महान शिक्षक हैं, तो वह अपने अनुयायियों को उपवास करना क्यों नहीं सिखाते हैं? उन दिनों, रैबी या शिक्षक अक्सर इस तरह से सवालों का जवाब देते थे, कि विषय पर बहस हो सके। यीशु एक सवाल पूछते हैं और उसके बाद खुद ही उसका जवाब देते हैं: \"क्या शादी में आने वाले मेहमान, दूल्हे के साथ जश्न मनाते समय उपवास करते हैं? बिल्कुल नहीं। वे उपवास नहीं कर सकते हैं जब तक दूल्हा उनके साथ है।\" यीशु अपनी तुलना एक दूल्हे से कर रहे हैं और लोगों को याद दिलाते हैं कि अगर उनके मेहमान उनके साथ शादी के दिन खुश नहीं होंगे, तो दूल्हा नाराज़ हो जाएगा। हालाँकि, लोगों ने उनकी बात को नहीं माना होगा कि वह वचनबद्ध मसीहा या वचनबद्ध उद्धारकर्ता थे, लेकिन वे मसीहा से मिलने वाली ख़ुशी को जाहिर करने के लिए एक शादी की दावत के विचार का इस्तेमाल किया करते थे। यहाँ, यीशु इस बात पर ज़ोर देते हैं कि उनके शिष्यों को अपने गुरु, दूल्हे में खुशी दिखाई देती हैं, तो वे उपवास क्यों करेंगे? हालाँकि, यीशु उस समय का भी पूर्वानुमान करते हैं जब वह उन्हें छोड़ देंगे...और तब उपवास उचित हो सकता है। वाक्य \"...और तब वे उपवास करेंगे\" एक आदेश नहीं, बल्कि उसका एक वर्णन है कि भविष्य में क्या होगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"यहाँ रुकें और अपनी संस्कृति में पार्टियों या विशेष समारोहों में क्या होता है, उसका वर्णन करें। क्या लोग खुशी से लोटपोट हैं? वे एक विशेष समारोह में अपनी खुशी को दिखाने के लिए किस तरह का काम करते हैं?"}]},{"type":"paragraph","content":[{"type":"text","text":"पुराने और नए कपड़े, और पुरानी और नई वाइनस्किन के बारे में छंद, शायद एक अलग बातचीत हैं जो यीशु ने कभी की थी। लेकिन वे यहाँ फ़िट बैठते हैं, क्योंकि वे यीशु के आने के बाद से, काम करने के पुराने और नए तरीके की तुलना करते हैं\\—अब यह बातचीत सिर्फ उपवास के बारे में नहीं है। यीशु की उपस्थिति इस बात का संकेत है कि पुरानी स्थिति हर तरह से मौलिक रूप से बदल गई है क्योंकि यीशु अब यहाँ हैं। अगर यीशु के शिष्यों को उपवास करना होता और अपने अन्य कार्यों में पुराने तरीके का अनुसरण करना होता, तो वे ऐसे लोगों की तरह होते जो पुराने कपड़ों पर नए कपड़े डाल लेते हैं। ईसाईत\\—यीशु का नया तरीका\\—एक नया कपड़ा है और यहूदी धर्म एक पुराना कपड़ा है।"}]},{"type":"paragraph","content":[{"type":"text","text":"नया कपड़ा एक बिना सिकुड़े या बिना धुले कपड़े जैसा है। अगर आप इसे एक पुराने कपड़े पर डालते हैं और उसके बाद उसे धोते हैं, तो वह कपड़ा असमान रूप से सिकुड़ जाएगा और नया कपड़ा, पुराने कपड़े से अलग हो जाएगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक दर्ज़िन को देखने और इस कहानी के लिए सही शब्दावली की खोज करने में समय बिताएँ।"}]}]}},{"stepNumber":3,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"दृश्यों का मतलब बताना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:18-22"}]},{"type":"paragraph","content":[{"type":"text","text":"वर्ज़न को समझने के लिए कथा को सबसे सरल भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, आप इस कहानी की कल्पना करने में ग्रुप की मदद करेंगे। आप कहानी के दृश्यों, सेटिंग और पात्रों को परिभाषित करने में ग्रुप की मदद करेंगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सेक्शन में एक दृश्य है।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"दृश्य:"},{"type":"text","text":" जॉन के शिष्य और फरीसी (धार्मिक नेता) उपवास कर रहे थे। तो कुछ लोग यीशु के पास आकर पूछते हैं, \"आपके शिष्य जॉन के शिष्यों और फरीसियों की तरह उपवास नहीं करते हैं?\""}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु उन्हें तीन उदाहरणों से जवाब देते हैं और कहते हैं, \"क्या शादी के मेहमान दूल्हे के साथ जश्न मनाते समय उपवास रखते हैं? बिल्कुल नहीं। वे उपवास नहीं कर सकते हैं क्योंकि दूल्हा उनके साथ है। जब दूल्हे को ले जाया जाएगा, तब वे उपवास कर पाएँगे।"}]},{"type":"paragraph","content":[{"type":"text","text":"\"इसके अलावा, पुराने कपड़े पर नए कपड़े का पैच कौन लगाता है? नया कपड़ा सिकुड़ जाएगा, जिससे पहले से भी बड़ा छेद हो जाएगा।"}]},{"type":"paragraph","content":[{"type":"text","text":"\"कोई भी नई शराब पुरानी वाइन की बोतल में नहीं डालता है, क्योंकि शराब से वाइन की बोतल फट जाएगी और शराब और बोतल दोनों नष्ट हो जाएँगी। नई शराब के लिए नई शराब की बोतल चाहिए होती है।\""}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्र हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जॉन के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"फरीसी (धार्मिक नेता)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लोग, यीशु से एक सवाल पूछ रहे हैं"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"पहले उदाहरण के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक दूल्हे के साथ शादी में आने वाले मेहमान"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे उदाहरण के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पुराने कपड़े पर पैबंद लगाने वाला कोई व्यक्ति"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"तीसरे उदाहरण के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वाइनस्किन में नया वाइनस्किन डालने वाला कोई व्यक्ति"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में, समूह स्टोरीबोर्ड लें, रेखाचित्र बनायें या इसमें कथा का दृश्य बनाने के लिये किसी तरह के फेरबदल का इस्तेमाल करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जो तीन उदाहरण यीशु ने दिए, अपनी टीम से उन्हें विज़ुअलाइज़ करने के लिए कहें: शादी में दूल्हे के साथ मेहमान, पुराने कपड़े जिन पर पुराने कपड़े का पैच लगाना है और नई और पुरानी वाइन की बोतल। समूह को एक तस्वीर दिखाएँ, जिससे पता चले कि शराब की बोतल कैसी दिखती होगी।"}]}]}},{"stepNumber":4,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"टेक्स्ट को हकीकत में उतारना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:18-22"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए पाठ को एक बार फिर से सबसे आसान भाषा में सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस सत्र में टीम कहानी को नाटक के रूप में पेश करेगी।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी में एक दृश्य है।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस कहानी के पात्रों में ये शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"जॉन के शिष्य"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"फरीसी (धार्मिक नेता)"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"लोग, यीशु से एक सवाल पूछ रहे हैं"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"यीशु"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"पहले उदाहरण के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"एक दूल्हे के साथ शादी में आने वाले मेहमान"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरे उदाहरण के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"पुराने कपड़े पर पैबंद लगाने वाला कोई व्यक्ति"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"तीसरे उदाहरण के पात्रों में शामिल हैं:"}]},{"type":"bulletList","content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"वाइनस्किन में नई वाइन डालने वाला कोई व्यक्ति"}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"टीम को कहानी पर दो बार अभिनय करने को कहें। जिस भाषा में वे अनुवाद कर रहे हैं, उसी भाषा में उन्हें उस पर अभिनय करना चाहिए। सबसे पहले, टीम को बिना रुके कहानी का अभिनय करने दें। दूसरी बार जब टीम कहानी पर अभिनय करे, तो उन्हें कहानी की अलग-अलग जगहों पर रोकें।"}]},{"type":"paragraph","content":[{"type":"text","text":"जैसे जैसे टीम कहानी पर पहली बार अभिनय करती है, कहानी के संवाद, प्रवाह, कथानक और कालक्रम पर ध्यान दें। कहानी या पैसेज के उन महत्वपूर्ण या कठिन हिस्सों पर ध्यान दें, जिन पर अभिनय किया जाना है और जिन्हें छोड़ा नहीं जा सकता। कहानी के अंशों को याद करने में टीम की मदद करें।"}]},{"type":"paragraph","content":[{"type":"text","text":"दूसरी बार जब टीम इस कहानी पर अभिनय करे, तो उन्हें कुछ जगहों पर रोक दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"कहानी में जब लोग यीशु से एक सवाल पूछते हैं, तो यीशु जवाब देते हैं, \"क्या शादी में आने वाले मेहमान, दूल्हे के साथ जश्न मनाते समय उपवास करते हैं?\" "},{"type":"text","marks":[{"type":"bold"}],"text":"इस अभिनय को बंद कर दें"},{"type":"text","text":"। यीशु के उदाहरण में शादी में आने वाले मेहमानों की भूमिका निभाने वालों से पूछें, \"आप दूल्हे के साथ होने के बारे में कैसा महसूस करते हैं?\" आप ऐसी बातें सुन सकते हैं जैसे \"ख़ुशी,\" \"उत्साह,\" या \"जश्न।\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"तब यीशु पुराने कपड़े पर नए कपड़े का पैबंद लगाने वाले किसी अन्य व्यक्ति एक दूसरा उदाहरण देते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"इस अभिनय को बंद कर दें"},{"type":"text","text":"। कपड़े पर पैबंद लगाने वाले अभिनेता से पूछें, \"आप पुराने कपड़े पर पैबंद लगाने के लिए पुराने कपड़े का इस्तेमाल क्यों करते हैं?\" आप ऐसी बातें सुन सकते हैं कि \"नया कपड़ा सिकुड़ जाएगा जिससे यह फिर से फट जाएगा।\" उसके बाद समूह से पूछें, \"आपको क्या लगता है, यीशु ने इस उदाहरण का इस्तेमाल क्यों किया?\" अभिनय को फिर से शुरू कर दें।"}]},{"type":"paragraph","content":[{"type":"text","text":"इस अभिनय के अंत में जब यीशु नई वाइन को पुराने वाइनस्किन में डालने वाले व्यक्ति एक उदाहरण देते हैं, तब समूह से पूछें, \"यीशु ने इस उदाहरण का इस्तेमाल क्यों किया?\""}]}]}},{"stepNumber":5,"tiptap":{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"खाली जगह को भरना"}]},{"type":"paragraph","content":[{"type":"text","text":"मार्क 2:18-22"}]},{"type":"paragraph","content":[{"type":"text","text":"संस्करण को समझने के लिए सबसे आसान लहजे में टेक्स्ट को एक बार सुनें।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक बार जब जॉन के "},{"type":"text","marks":[{"type":"bold"}],"text":"शिष्य"},{"type":"text","text":" और फरीसी उपवास कर रहे थे, तो कुछ लोग यीशु के पास आकर पूछने लगे, \"आपके शिष्य, जॉन के शिष्यों और फरीसियों की तरह उपवास क्यों नहीं करते हैं?\""}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"शिष्यों"},{"type":"text","text":" के लिए उसी शब्द का इस्तेमाल करें जिसे आप सभी कहानियों में इस्तेमाल कर रहे हैं। यीशु और जॉन के शिष्य सभी "},{"type":"text","marks":[{"type":"italic"}],"text":"करीबी अनुयायी"},{"type":"text","text":" थे जो उनकी सार्वजनिक मिनिस्ट्री के अधिकांश हिस्सों के दौरान इन लोगों के साथ थे, उनके साथ हर जगह यात्रा करते थे, उनसे सीखते थे, और यीशु के मामले में, उन्हें कभी-कभी उनके द्वारा राज्य की अच्छी खबर का उपदेश देने और बीमार को ठीक करने के लिए भेजा जाता था। अगर भाषा में एक शिक्षक के समर्पित अनुयायियों के लिए एक आसान शब्द है, तो आप उस शब्द का इस्तेमाल कर सकते हैं।"}]},{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"फरीसी"},{"type":"text","text":" शब्द एक तरह के धार्मिक नेता के लिए इस्तेमाल किया जाने वाला नाम है। यहाँ उसी शब्द का इस्तेमाल करें जिसे आपने पिछली कहानियों में इस्तेमाल किया है। \"फरीसी\" नाम का अर्थ \"अलग होना\" है। फरीसी बहुत प्रभावशाली और सही माने जाते थे। वे लोगों के लिए अच्छे उदाहरण थे क्योंकि वे परमेश्वर के नियमों के प्रति समर्पित थे। उन्होंने लिखित और मौखिक दोनों तरह की धार्मिक परंपराओं और कानूनों को मान्यता दी।"}]},{"type":"paragraph","content":[{"type":"text","text":"उन्होंने यीशु से पूछा, \"आपके शिष्य "},{"type":"text","marks":[{"type":"bold"}],"text":"उपवास"},{"type":"text","text":" क्यों नहीं करते हैं?\" पुराने टेस्टामेंट में उपवास केवल प्रायश्चित के दिन, पाप और पीड़ा की माफ़ी के लिए किया जाता था। यहाँ, यह शायद उस समय को कम करने के लिए पश्चाताप करने का एक तरीका है जब परमेश्वर सबको माफ़ कर देंगे और बचा लेंगे। उपवास के दौरान लोग कुछ नहीं खाते हैं। शायद इसीलिए यीशु ने यहाँ एक पार्टी\\—काल्पनिक शादी\\—का इस्तेमाल किया। यीशु के समय तक उपवास पारंपरिक हो गए थे...फरीसी इसे सप्ताह में दो बार किया करते थे। जॉन के शिष्यों ने उपवास किया होगा क्योंकि उनके नेता की मृत्यु हो गई थी।"}]},{"type":"paragraph","content":[{"type":"text","text":"पवित्रा शास्त्रों के अनुसार उपवास में, भोजन से एक तरह का धार्मिक परहेज़ किया जाता था, ताकि ज़्यादा धार्मिकता पैदा की जा सके या परमेश्वर की अधिक कृपा या उनसे माफ़ी पाई जा सके। इसीलिए कुछ भाषाओं में उपवास से संबंधित अभिव्यक्तियों में इस बात का एक वर्णन शामिल होना चाहिए कि उपवास में क्या शामिल होता है।"}]},{"type":"paragraph","content":[{"type":"text","text":"यीशु पुराने और नए कपड़े, और उसके बाद पुरानी और नई "},{"type":"text","marks":[{"type":"bold"}],"text":"वाइनस्किन"},{"type":"text","text":" के बारे में बात करते चले जाते हैं। "},{"type":"text","marks":[{"type":"bold"}],"text":"वाइनस्किन"},{"type":"text","text":" एक चमड़े का बैग होता है जिसका इस्तेमाल शराब को ले जाने के लिए किया जाता है। एक पुराना बैग, भंगुर और कठोर होता है और फ़र्मेंटिग वाइन उसमें नहीं रखी जा सकती। यह समझना ज़रूरी है कि इन वाइनस्किन में इस्तेमाल किया जाने वाली ड्रिंक एक फ़र्मेंटिंग ड्रिंक है जो समय के साथ फ़ैलती है।"}]},{"type":"paragraph","content":[{"type":"text","text":"एक वाइनस्किन की तस्वीर दिखाएँ।"}]}]}}]', 32081, 1);
SELECT @TextResourceContentId = @@IDENTITY;
END
